/**
 * Decrypts the given data using the given key using the given algorithm. If you are decrypting
 * data that is supposed to be a string, consider that it might be Base64-encoded.
 *
 * @throws DecryptionException if the string cannot be decrypted with the given key
 * @throws IllegalArgumentException if the algorithm is not supported.
 */
public static byte[] decrypt(byte[] text, Key key, String algorithm) throws DecryptionException {
  Cipher cipher;
  try {
    LOGGER.trace("start decrypting text using {} cipher", algorithm);
    cipher = Cipher.getInstance(algorithm);
    cipher.init(Cipher.DECRYPT_MODE, key);
    LOGGER.trace("initialized decryption with key of type {}", key.getClass());
  } catch (GeneralSecurityException e) {
    throw new IllegalArgumentException(
        "unable to initialize cipher for algorithm " + algorithm, e);
  }
  try {
    return cipher.doFinal(text);
  } catch (GeneralSecurityException e) {
    throw new DecryptionException("unable to decrypt data using algorithm " + algorithm, e);
  }
}

public static String encrypt(String text, Key key) throws CipherException {
  try {
    Cipher cipher = Cipher.getInstance(CIPHER_TYPE);
    cipher.init(Cipher.ENCRYPT_MODE, key);

    byte[] outputBytes = cipher.doFinal(text.getBytes());

    // BASE64Encoder encoder = new BASE64Encoder();
    // String base64 = encoder.encode(outputBytes);
    String base64 = Base64.encodeBytes(outputBytes);
    return base64;
  } catch (NoSuchAlgorithmException e) {
    throw new CipherException("Failed to encrypt text: " + e.getMessage());
  } catch (NoSuchPaddingException e) {
    throw new CipherException("Failed to encrypt text: " + e.getMessage());
  } catch (InvalidKeyException e) {
    throw new CipherException("Failed to encrypt text: " + e.getMessage());
  } catch (IllegalBlockSizeException e) {
    throw new CipherException("Failed to encrypt text: " + e.getMessage());
  } catch (BadPaddingException e) {
    throw new CipherException("Failed to encrypt text: " + e.getMessage());
  }
}

