  }
}

private String encrypt(String plainText, Key key)
    throws NoSuchAlgorithmException, NoSuchPaddingException, InvalidKeyException,
        ShortBufferException, IllegalBlockSizeException, BadPaddingException {

  byte[] plainTextBytes = plainText.getBytes();
  byte[] encText = new byte[(((plainTextBytes.length - 1) / 100 + 1)) * 128];

  Cipher cipher = Cipher.getInstance("RSA");
  cipher.init(Cipher.ENCRYPT_MODE, key);
  for (int inOffset = 0, outOffset = 0, inLen = 0;
      (inLen = plainTextBytes.length - inOffset) > 0;
      inOffset += 100) {
    outOffset +=
        cipher.doFinal(plainTextBytes, inOffset, Math.min(100, inLen), encText, outOffset);
  }

  // also test serialization
  BigInteger bi = new BigInteger(encText);
  return bi.toString(16);
}

