/**
 * Encrypt a file.
 *
 * @param key A <code>Key</code>.
 * @param source A <code>File</code>.
 * @param target A <code>File</code>.
 * @param buffer A <code>byte[]</code>.
 * @throws InvalidKeyException
 * @throws IOException
 */
public void encrypt(final Key key, final File source, final File target, final byte[] buffer)
    throws InvalidKeyException, IOException {
  if (null == key || null == source || null == target || null == buffer) {
    throw new NullPointerException();
  }
  if (!source.exists()) {
    throw new IllegalArgumentException("Source " + source.getAbsolutePath() + " does not exist.");
  }
  if (!source.isFile()) {
    throw new IllegalArgumentException("Source " + source.getAbsolutePath() + " is not a file.");
  }
  if (!source.canRead()) {
    throw new IllegalArgumentException("Source " + source.getAbsolutePath() + " cannot be read.");
  }
  if (!target.exists()) {
    throw new IllegalArgumentException("Target " + target.getAbsolutePath() + " does not exist.");
  }
  if (!target.isFile()) {
    throw new IllegalArgumentException("Target " + target.getAbsolutePath() + " is not a file.");
  }
  if (!target.canRead()) {
    throw new IllegalArgumentException("Target " + target.getAbsolutePath() + " cannot be read.");
  }
  if (!target.canWrite()) {
    throw new IllegalArgumentException(
        "Target " + target.getAbsolutePath() + " cannot be written to.");
  }
  cipher.init(Cipher.ENCRYPT_MODE, key);
  final InputStream inStream = new FileInputStream(source);
  try {
    final CipherOutputStream cipherStream =
        new CipherOutputStream(new FileOutputStream(target), cipher);
    try {
      int bytesRead;
      while (true) {
        bytesRead = inStream.read(buffer);
        if (-1 == bytesRead) {
          break;
        }
        cipherStream.write(buffer, 0, bytesRead);
      }
    } finally {
      try {
        cipherStream.flush();
      } finally {
        cipherStream.close();
      }
    }
  } finally {
    inStream.close();
  }
}

/** {@inheritDoc} */
protected void runImpl() {
  if (data == null) return;

  byte[] decrypted;
  try {
    Cipher rsaCipher = Cipher.getInstance("RSA/ECB/nopadding");
    rsaCipher.init(Cipher.DECRYPT_MODE, getConnection().getRSAPrivateKey());
    decrypted = rsaCipher.doFinal(data, 0, 128);
  } catch (GeneralSecurityException e) {
    log.warn("Error while decripting data on user auth." + e, e);
    sendPacket(new SM_LOGIN_FAIL(AionAuthResponse.SYSTEM_ERROR));
    return;
  }
  String user = new String(decrypted, 64, 32).trim().toLowerCase();
  String password = new String(decrypted, 96, 32).trim();

  int ncotp = decrypted[0x7c];
  ncotp |= decrypted[0x7d] << 8;
  ncotp |= decrypted[0x7e] << 16;
  ncotp |= decrypted[0x7f] << 24;

  log.debug("AuthLogin: " + user + " pass: " + password + " ncotp: " + ncotp);

  AionConnection client = getConnection();
  AionAuthResponse response = AccountController.login(user, password, client);
  switch (response) {
    case AUTHED:
      client.setState(State.AUTHED_LOGIN);
      client.setSessionKey(new SessionKey(client.getAccount()));
      client.sendPacket(new SM_LOGIN_OK(client.getSessionKey()));
      break;

    default:
      client.close(new SM_LOGIN_FAIL(response), true);
      break;
  }
}

