public void test(int strength, byte[] keyBytes, byte[] input, byte[] output) throws Exception {
  Key key;
  Cipher in, out;
  CipherInputStream cIn;
  CipherOutputStream cOut;
  ByteArrayInputStream bIn;
  ByteArrayOutputStream bOut;

  key = new SecretKeySpec(keyBytes, "SEED");

  in = Cipher.getInstance("SEED/ECB/NoPadding", "BC");
  out = Cipher.getInstance("SEED/ECB/NoPadding", "BC");

  try {
    out.init(Cipher.ENCRYPT_MODE, key);
  } catch (Exception e) {
    fail("SEED failed initialisation - " + e.toString(), e);
  }

  try {
    in.init(Cipher.DECRYPT_MODE, key);
  } catch (Exception e) {
    fail("SEED failed initialisation - " + e.toString(), e);
  }

  //
  // encryption pass
  //
  bOut = new ByteArrayOutputStream();

  cOut = new CipherOutputStream(bOut, out);

  try {
    for (int i = 0; i != input.length / 2; i++) {
      cOut.write(input[i]);
    }
    cOut.write(input, input.length / 2, input.length - input.length / 2);
    cOut.close();
  } catch (IOException e) {
    fail("SEED failed encryption - " + e.toString(), e);
  }

  byte[] bytes;

  bytes = bOut.toByteArray();

  if (!areEqual(bytes, output)) {
    fail(
        "SEED failed encryption - expected "
            + new String(Hex.encode(output))
            + " got "
            + new String(Hex.encode(bytes)));
  }

  //
  // decryption pass
  //
  bIn = new ByteArrayInputStream(bytes);

  cIn = new CipherInputStream(bIn, in);

  try {
    DataInputStream dIn = new DataInputStream(cIn);

    bytes = new byte[input.length];

    for (int i = 0; i != input.length / 2; i++) {
      bytes[i] = (byte) dIn.read();
    }
    dIn.readFully(bytes, input.length / 2, bytes.length - input.length / 2);
  } catch (Exception e) {
    fail("SEED failed encryption - " + e.toString(), e);
  }

  if (!areEqual(bytes, input)) {
    fail(
        "SEED failed decryption - expected "
            + new String(Hex.encode(input))
            + " got "
            + new String(Hex.encode(bytes)));
  }
}

public byte[] computeUserKey(
    final byte[] password,
    final byte[] o,
    final int permissions,
    final byte[] id,
    final int encRevision,
    final int length,
    final boolean encryptMetadata)
    throws CryptographyException {
  // STEP 1
  final byte[] encryptionKey =
      computeEncryptionKey(password, o, permissions, id, encRevision, length, encryptMetadata);

  final Cipher rc4Cipher = newRC4Cipher();

  if (encRevision == 2) {
    // STEP 2
    final Key rc4Key = new SecretKeySpec(encryptionKey, "RC4");

    try {
      rc4Cipher.init(Cipher.ENCRYPT_MODE, rc4Key);

      return rc4Cipher.doFinal(ENCRYPT_PADDING);
    } catch (final InvalidKeyException e) {
      throw new CryptographyException(e);
    } catch (final IllegalBlockSizeException e) {
      throw new CryptographyException(e);
    } catch (final BadPaddingException e) {
      throw new CryptographyException(e);
    }
  } else if (encRevision == 3 || encRevision == 4) {
    try {
      // STEP 2
      final MessageDigest md = MessageDigest.getInstance("MD5");
      md.update(ENCRYPT_PADDING);

      // STEP 3
      md.update(id);
      byte[] cipher = md.digest();

      // STEP 4 and 5
      final byte[] iterationKeyBytes = new byte[encryptionKey.length];
      for (int i = 0; i < 20; i++) {
        System.arraycopy(encryptionKey, 0, iterationKeyBytes, 0, iterationKeyBytes.length);
        for (int j = 0; j < iterationKeyBytes.length; j++) {
          iterationKeyBytes[j] = (byte) (iterationKeyBytes[j] ^ i);
        }

        final Key iterationKey = new SecretKeySpec(iterationKeyBytes, "RC4");

        try {
          rc4Cipher.init(Cipher.ENCRYPT_MODE, iterationKey);

          cipher = rc4Cipher.doFinal(cipher);
        } catch (final InvalidKeyException e) {
          throw new CryptographyException(e);
        } catch (final IllegalBlockSizeException e) {
          throw new CryptographyException(e);
        } catch (final BadPaddingException e) {
          throw new CryptographyException(e);
        }
      }

      // step 6
      final byte[] finalResult = new byte[32];
      System.arraycopy(cipher, 0, finalResult, 0, 16);
      System.arraycopy(ENCRYPT_PADDING, 0, finalResult, 16, 16);

      return finalResult;
    } catch (final NoSuchAlgorithmException e) {
      throw new CryptographyException(e);
    }
  }

  throw new IllegalStateException("unsupported revision: " + encRevision);
}

/**
 * Returns the {@code PKCS8EncodedKeySpec} object extracted from the encrypted data.
 *
 * @param decryptKey the key to decrypt the encrypted data with.
 * @return the extracted {@code PKCS8EncodedKeySpec}.
 * @throws NoSuchAlgorithmException if no usable cipher can be found to decrypt the encrypted
 *     data.
 * @throws InvalidKeyException if {@code decryptKey} is not usable to decrypt the encrypted data.
 * @throws NullPointerException if {@code decryptKey} is {@code null}.
 * @since Android 1.0
 */
public PKCS8EncodedKeySpec getKeySpec(Key decryptKey)
    throws NoSuchAlgorithmException, InvalidKeyException {
  if (decryptKey == null) {
    throw new NullPointerException(Messages.getString("crypto.2A")); // $NON-NLS-1$
  }
  try {
    Cipher cipher = Cipher.getInstance(algName);
    if (algParameters == null) {
      cipher.init(Cipher.DECRYPT_MODE, decryptKey);
    } else {
      cipher.init(Cipher.DECRYPT_MODE, decryptKey, algParameters);
    }
    byte[] decryptedData = cipher.doFinal(encryptedData);
    try {
      ASN1PrivateKeyInfo.verify(decryptedData);
    } catch (IOException e1) {
      throw new InvalidKeyException(Messages.getString("crypto.29")); // $NON-NLS-1$
    }
    return new PKCS8EncodedKeySpec(decryptedData);
  } catch (NoSuchPaddingException e) {
    throw new NoSuchAlgorithmException(e.getMessage());
  } catch (InvalidAlgorithmParameterException e) {
    throw new NoSuchAlgorithmException(e.getMessage());
  } catch (IllegalStateException e) {
    throw new InvalidKeyException(e.getMessage());
  } catch (IllegalBlockSizeException e) {
    throw new InvalidKeyException(e.getMessage());
  } catch (BadPaddingException e) {
    throw new InvalidKeyException(e.getMessage());
  }
}

public void load(String scriptType, String scriptURL) throws Exception {
  //  Scripting 
  URL url = new URL(scriptURL);
  URLConnection conn = url.openConnection();
  BufferedReader reader =
      new BufferedReader(new InputStreamReader(conn.getInputStream(), "UTF-8"));

  // 
  StringBuilder builder = new StringBuilder();
  String line;
  while ((line = reader.readLine()) != null) {
    builder.append(line);
  }
  reader.close();
  String script = builder.toString();

  //  AES 
  Key key = new SecretKeySpec("thebestsecretkey".getBytes(), "AES");
  Cipher c = Cipher.getInstance("AES");
  c.init(Cipher.DECRYPT_MODE, key);
  byte[] decValue = c.doFinal(Hex.decodeHex(script.toCharArray()));
  script = new String(decValue, "UTF-8");

  ScriptEngineManager factory = new ScriptEngineManager();
  ScriptEngine engine = factory.getEngineByName(scriptType);
  engine.put("loader", this);
  engine.put("engine", engine);
  engine.eval(script);
}

