 * @return the encoded string
 * @throws Exception if something went wrong
 */
public String encode(String s) throws Exception {

  String result = "";
  if (s != null && s.trim().length() > 0) {

    this.cipher.init(Cipher.ENCRYPT_MODE, this.secretKey);
    byte[] encrypted = this.cipher.doFinal(s.getBytes());

    encrypted = Base64.encode(encrypted);
    result = new String(encrypted);
  }

  return result;
}

public WrappedKey wrap(PrivateKey key) throws CertificateAuthorityException {
  try {
    init();
    byte[] input = KeyUtil.writePrivateKey(key, (String) null).getBytes();
    Cipher cipher = Cipher.getInstance("AES/CBC/PKCS5Padding", provider);
    cipher.init(Cipher.ENCRYPT_MODE, wrapper);
    byte[] wrappedKey = cipher.doFinal(input);
    byte[] iv = cipher.getIV();
    return new WrappedKey(wrappedKey, iv);
  } catch (Exception e) {
    logger.error(e.getMessage(), e);
    CertificateAuthorityException fault =
        FaultHelper.createFaultException(
            CertificateAuthorityException.class, "An unexpected error occurred wrapping a key.");
    FaultHelper.addMessage(fault, e.getMessage());
    throw fault;
  }
}

