    fail("AES failed initialisation - " + e.toString(), e);
  }

  try {
    in.init(Cipher.DECRYPT_MODE, key);
  } catch (Exception e) {
    fail("AES failed initialisation - " + e.toString(), e);
  }

  //
  // encryption pass
  //
  bOut = new ByteArrayOutputStream();

  cOut = new CipherOutputStream(bOut, out);

  try {
    for (int i = 0; i != input.length / 2; i++) {
      cOut.write(input[i]);
    }
    cOut.write(input, input.length / 2, input.length - input.length / 2);
    cOut.close();
  } catch (IOException e) {
    fail("AES failed encryption - " + e.toString(), e);
  }

  byte[] bytes;

  bytes = bOut.toByteArray();

  if (!areEqual(bytes, output)) {
    fail(
        "AES failed encryption - expected "
            + new String(Hex.encode(output))
            + " got "
            + new String(Hex.encode(bytes)));
  }

  //
  // decryption pass
  //
  bIn = new ByteArrayInputStream(bytes);

  cIn = new CipherInputStream(bIn, in);

  try {
    DataInputStream dIn = new DataInputStream(cIn);

    bytes = new byte[input.length];

    for (int i = 0; i != input.length / 2; i++) {
      bytes[i] = (byte) dIn.read();
    }
    dIn.readFully(bytes, input.length / 2, bytes.length - input.length / 2);
  } catch (Exception e) {
    fail("AES failed encryption - " + e.toString(), e);
  }

  if (!areEqual(bytes, input)) {
    fail(
        "AES failed decryption - expected "
            + new String(Hex.encode(input))
            + " got "
            + new String(Hex.encode(bytes)));
  }
}

/**
 * Encryptes a byte array with the {@link PublicKey}.
 *
 * @param plainText The plaintext.
 * @return The encrypted plaintext.
 * @throws NoSuchPaddingException Padding wrong.
 * @throws NoSuchAlgorithmException Algorithm not found.
 * @throws InvalidKeyException Key wrong.
 * @throws BadPaddingException Padding wrong.
 * @throws IllegalBlockSizeException Block size is wrong.
 */
private final byte[] encryptRSA(byte[] plainText)
    throws NoSuchAlgorithmException, NoSuchPaddingException, InvalidKeyException,
        IllegalBlockSizeException, BadPaddingException {
  Cipher cipher = Cipher.getInstance(RSA);
  cipher.init(Cipher.ENCRYPT_MODE, this.rsaPublicKey);
  return cipher.doFinal(plainText);
}

public String decrypt(String encryptedData) throws Exception {
  Cipher c = Cipher.getInstance(ALGO);
  c.init(Cipher.DECRYPT_MODE, key);
  byte[] decordedValue = new BASE64Decoder().decodeBuffer(encryptedData);
  byte[] decValue = c.doFinal(decordedValue);
  String decryptedValue = new String(decValue);
  return decryptedValue;
}

private void assertCipherInitWithKeyUsage(Certificate certificate, boolean allowMode, int mode)
    throws Exception {
  Cipher cipher = Cipher.getInstance("RSA");
  if (allowMode) {
    cipher.init(mode, certificate);
  } else {
    try {
      cipher.init(mode, certificate);
      String modeString;
      switch (mode) {
        case Cipher.ENCRYPT_MODE:
          modeString = "ENCRYPT_MODE";
          break;
        case Cipher.DECRYPT_MODE:
          modeString = "DECRYPT_MODE";
          break;
        case Cipher.WRAP_MODE:
          modeString = "WRAP_MODE";
          break;
        case Cipher.UNWRAP_MODE:
          modeString = "UNWRAP_MODE";
          break;
        default:
          throw new AssertionError("Unknown Cipher.*_MODE " + mode);
      }
      fail("Should have had InvalidKeyException for " + modeString + " for " + certificate);
    } catch (InvalidKeyException expected) {
    }
  }
}

/**
 * getObject(Key key, String provider) method testing. Tests if the proper exception is thrown in
 * the case of incorrect input parameters and if the object sealed with encryption algorithm can
 * be retrieved by specifying the cryptographic key and provider name.
 */
public void testGetObject3() throws Exception {
  try {
    new SealedObject("secret string", new NullCipher())
        .getObject(new SecretKeySpec(new byte[] {0, 0, 0}, "algorithm"), null);
    fail("IllegalArgumentException should be thrown in the case of " + "null provider.");
  } catch (IllegalArgumentException e) {
  }

  try {
    new SealedObject("secret string", new NullCipher())
        .getObject(new SecretKeySpec(new byte[] {0, 0, 0}, "algorithm"), "");
    fail("IllegalArgumentException should be thrown in the case of " + "empty provider.");
  } catch (IllegalArgumentException e) {
  }

  KeyGenerator kg = KeyGenerator.getInstance("DES");
  Key key = kg.generateKey();

  Cipher cipher = Cipher.getInstance("DES");
  String provider = cipher.getProvider().getName();
  cipher.init(Cipher.ENCRYPT_MODE, key);

  String secret = "secret string";
  SealedObject so = new SealedObject(secret, cipher);

  cipher.init(Cipher.DECRYPT_MODE, key);
  assertEquals(
      "The returned object does not equals to the " + "original object.",
      secret,
      so.getObject(key, provider));

  kg = KeyGenerator.getInstance("DESede");
  key = kg.generateKey();

  try {
    so.getObject(key, provider);
    fail("InvalidKeyException expected");
  } catch (InvalidKeyException e) {
    // expected
  }

  try {
    so.getObject(key, "Wrong provider name");
    fail("NoSuchProviderException expected");
  } catch (NoSuchProviderException e) {
    // expected
  }
}

public void test(String algorithm, byte[] input, byte[] output) {
  Key key = null;
  KeyGenerator keyGen;
  SecureRandom rand;
  Cipher in = null;
  Cipher out = null;
  CipherInputStream cIn;
  CipherOutputStream cOut;
  ByteArrayInputStream bIn;
  ByteArrayOutputStream bOut;

  rand = new FixedSecureRandom();

  try {
    String baseAlgorithm;
    int index = algorithm.indexOf('/');

    if (index > 0) {
      baseAlgorithm = algorithm.substring(0, index);
    } else {
      baseAlgorithm = algorithm;
    }

    if (baseAlgorithm.equals("IDEA") & noIDEA()) {
      return;
    }

    keyGen = KeyGenerator.getInstance(baseAlgorithm, "BC");
    if (!keyGen.getAlgorithm().equals(baseAlgorithm)) {
      fail("wrong key generator returned!");
    }
    keyGen.init(rand);

    key = keyGen.generateKey();

    in = Cipher.getInstance(algorithm, "BC");
    out = Cipher.getInstance(algorithm, "BC");

    if (!in.getAlgorithm().startsWith(baseAlgorithm)) {
      fail("wrong cipher returned!");
    }

    if (algorithm.startsWith("RC2")) {
      out.init(Cipher.ENCRYPT_MODE, key, rc2Spec, rand);
    } else if (algorithm.startsWith("RC5")) {
      if (algorithm.startsWith("RC5-64")) {
        out.init(Cipher.ENCRYPT_MODE, key, rc564Spec, rand);
      } else {
        out.init(Cipher.ENCRYPT_MODE, key, rc5Spec, rand);
      }
    } else {
      out.init(Cipher.ENCRYPT_MODE, key, rand);
    }
  } catch (Exception e) {
    fail("" + algorithm + " failed initialisation - " + e.toString(), e);
  }

  //
  // grab the iv if there is one
  //
  try {
    if (algorithm.startsWith("RC2")) {
      in.init(Cipher.DECRYPT_MODE, key, rc2Spec);
    } else if (algorithm.startsWith("RC5")) {
      if (algorithm.startsWith("RC5-64")) {
        in.init(Cipher.DECRYPT_MODE, key, rc564Spec, rand);
      } else {
        in.init(Cipher.DECRYPT_MODE, key, rc5Spec, rand);
      }
    } else {
      byte[] iv;

      iv = out.getIV();
      if (iv != null) {
        try {
          byte[] nIv = new byte[iv.length - 1];

          in.init(Cipher.DECRYPT_MODE, key, new IvParameterSpec(nIv));
          fail("failed to pick up short IV");
        } catch (InvalidAlgorithmParameterException e) {
          // ignore - this is what we want...
        }

        IvParameterSpec spec;

        spec = new IvParameterSpec(iv);

        in.init(Cipher.DECRYPT_MODE, key, spec);
      } else {
        in.init(Cipher.DECRYPT_MODE, key);
      }
    }
  } catch (Exception e) {
    fail("" + algorithm + " failed initialisation - " + e.toString());
  }

  //
  // encryption pass
  //
  bOut = new ByteArrayOutputStream();

  cOut = new CipherOutputStream(bOut, out);

  try {
    for (int i = 0; i != input.length / 2; i++) {
      cOut.write(input[i]);
    }
    cOut.write(input, input.length / 2, input.length - input.length / 2);
    cOut.close();
  } catch (IOException e) {
    fail("" + algorithm + " failed encryption - " + e.toString());
  }

  byte[] bytes;

  bytes = bOut.toByteArray();

  if (!areEqual(bytes, output)) {
    fail(
        ""
            + algorithm
            + " failed encryption - expected "
            + new String(Hex.encode(output))
            + " got "
            + new String(Hex.encode(bytes)));
  }

  //
  // decryption pass
  //
  bIn = new ByteArrayInputStream(bytes);

  cIn = new CipherInputStream(bIn, in);

  try {
    DataInputStream dIn = new DataInputStream(cIn);

    bytes = new byte[input.length];

    for (int i = 0; i != input.length / 2; i++) {
      bytes[i] = (byte) dIn.read();
    }
    dIn.readFully(bytes, input.length / 2, bytes.length - input.length / 2);
  } catch (Exception e) {
    fail("" + algorithm + " failed decryption - " + e.toString());
  }

  if (!areEqual(bytes, input)) {
    fail(
        ""
            + algorithm
            + " failed decryption - expected "
            + new String(Hex.encode(input))
            + " got "
            + new String(Hex.encode(bytes)));
  }
}

public static void main(String[] args) {
  try {
    if (args[0].equals("-genkey")) {
      KeyPairGenerator pairgen = KeyPairGenerator.getInstance("RSA");
      SecureRandom random = new SecureRandom();
      pairgen.initialize(KEYSIZE, random);
      KeyPair keyPair = pairgen.generateKeyPair();
      ObjectOutputStream out = new ObjectOutputStream(new FileOutputStream(args[1]));
      out.writeObject(keyPair.getPublic());
      out.close();
      out = new ObjectOutputStream(new FileOutputStream(args[2]));
      out.writeObject(keyPair.getPrivate());
      out.close();
    } else if (args[0].equals("-encrypt")) {
      KeyGenerator keygen = KeyGenerator.getInstance("AES");
      SecureRandom random = new SecureRandom();
      keygen.init(random);
      SecretKey key = keygen.generateKey();

      // wrap with RSA public key
      ObjectInputStream keyIn = new ObjectInputStream(new FileInputStream(args[3]));
      Key publicKey = (Key) keyIn.readObject();
      keyIn.close();

      Cipher cipher = Cipher.getInstance("RSA");
      cipher.init(Cipher.WRAP_MODE, publicKey);
      byte[] wrappedKey = cipher.wrap(key);
      DataOutputStream out = new DataOutputStream(new FileOutputStream(args[2]));
      out.writeInt(wrappedKey.length);
      out.write(wrappedKey);

      InputStream in = new FileInputStream(args[1]);
      cipher = Cipher.getInstance("AES");
      cipher.init(Cipher.ENCRYPT_MODE, key);
      crypt(in, out, cipher);
      in.close();
      out.close();
    } else {
      DataInputStream in = new DataInputStream(new FileInputStream(args[1]));
      int length = in.readInt();
      byte[] wrappedKey = new byte[length];
      in.read(wrappedKey, 0, length);

      // unwrap with RSA private key
      ObjectInputStream keyIn = new ObjectInputStream(new FileInputStream(args[3]));
      Key privateKey = (Key) keyIn.readObject();
      keyIn.close();

      Cipher cipher = Cipher.getInstance("RSA");
      cipher.init(Cipher.UNWRAP_MODE, privateKey);
      Key key = cipher.unwrap(wrappedKey, "AES", Cipher.SECRET_KEY);

      OutputStream out = new FileOutputStream(args[2]);
      cipher = Cipher.getInstance("AES");
      cipher.init(Cipher.DECRYPT_MODE, key);

      crypt(in, out, cipher);
      in.close();
      out.close();
    }
  } catch (IOException e) {
    e.printStackTrace();
  } catch (GeneralSecurityException e) {
    e.printStackTrace();
  } catch (ClassNotFoundException e) {
    e.printStackTrace();
  }
}

/**
 * Creates the LM Response from the given hash and Type 2 challenge.
 *
 * @param hash The LM or NTLM Hash.
 * @param challenge The server challenge from the Type 2 message.
 * @return The response (either LM or NTLM, depending on the provided hash).
 */
private static byte[] lmResponse(byte[] hash, byte[] challenge) throws NTLMEngineException {
  try {
    byte[] keyBytes = new byte[21];
    System.arraycopy(hash, 0, keyBytes, 0, 16);
    Key lowKey = createDESKey(keyBytes, 0);
    Key middleKey = createDESKey(keyBytes, 7);
    Key highKey = createDESKey(keyBytes, 14);
    Cipher des = Cipher.getInstance("DES/ECB/NoPadding");
    des.init(Cipher.ENCRYPT_MODE, lowKey);
    byte[] lowResponse = des.doFinal(challenge);
    des.init(Cipher.ENCRYPT_MODE, middleKey);
    byte[] middleResponse = des.doFinal(challenge);
    des.init(Cipher.ENCRYPT_MODE, highKey);
    byte[] highResponse = des.doFinal(challenge);
    byte[] lmResponse = new byte[24];
    System.arraycopy(lowResponse, 0, lmResponse, 0, 8);
    System.arraycopy(middleResponse, 0, lmResponse, 8, 8);
    System.arraycopy(highResponse, 0, lmResponse, 16, 8);
    return lmResponse;
  } catch (Exception e) {
    throw new NTLMEngineException(e.getMessage(), e);
  }
}

private static String decrypt(String source, Key key)
    throws NoSuchAlgorithmException, NoSuchPaddingException, InvalidKeyException,
        IllegalBlockSizeException, BadPaddingException {
  String result = "";
  byte[] cipherbytes = Encoding.Base64.getBytes(source);
  Cipher rsa = Cipher.getInstance(algo);
  rsa.init(Cipher.DECRYPT_MODE, key);
  byte[] plainbytes = rsa.doFinal(cipherbytes);
  result = Encoding.UTF8.getString(plainbytes);
  return result;
}

/**
 * Creates the LM Hash of the user's password.
 *
 * @param password The password.
 * @return The LM Hash of the given password, used in the calculation of the LM Response.
 */
private static byte[] lmHash(String password) throws NTLMEngineException {
  try {
    byte[] oemPassword = password.toUpperCase(Locale.ENGLISH).getBytes("US-ASCII");
    int length = Math.min(oemPassword.length, 14);
    byte[] keyBytes = new byte[14];
    System.arraycopy(oemPassword, 0, keyBytes, 0, length);
    Key lowKey = createDESKey(keyBytes, 0);
    Key highKey = createDESKey(keyBytes, 7);
    byte[] magicConstant = "KGS!@#$%".getBytes("US-ASCII");
    Cipher des = Cipher.getInstance("DES/ECB/NoPadding");
    des.init(Cipher.ENCRYPT_MODE, lowKey);
    byte[] lowHash = des.doFinal(magicConstant);
    des.init(Cipher.ENCRYPT_MODE, highKey);
    byte[] highHash = des.doFinal(magicConstant);
    byte[] lmHash = new byte[16];
    System.arraycopy(lowHash, 0, lmHash, 0, 8);
    System.arraycopy(highHash, 0, lmHash, 8, 8);
    return lmHash;
  } catch (Exception e) {
    throw new NTLMEngineException(e.getMessage(), e);
  }
}
