/**
 * Copies from one stream to another
 *
 * @param out
 * @param in
 * @throws IOException
 */
public static void Copy(InputStream in, OutputStream out) throws IOException {
  byte[] buffer = new byte[1024];
  int len = in.read(buffer);
  while (len != -1) {
    out.write(buffer, 0, len);
    len = in.read(buffer);
  }
}

@Override
public int read() throws IOException {
  // have set this to poll..
  while (true) {
    // there is data buffered, read and return it.
    if (in.available() > 0) return in.read();

    // no data left
    if (!opened.get()) {
      // we are closed, return EOF
      return ConsoleOperations.CTRL_D;
    }
    try {
      // still open, wait a little and try again.
      Clock.sleep(50);
    } catch (InterruptedException e) {
      // interrupted? return end of file.
      return ConsoleOperations.CTRL_D;
    }
  }
}

public int read(byte[] buffer, int offset, int len) throws IOException {

  if (offset < 0 || len < 0 || (offset + len) > buffer.length) {
    throw new IndexOutOfBoundsException(
        "Offset=" + offset + ",len=" + len + ",buflen=" + buffer.length);
  }
  if (len == 0) {
    return 0;
  }

  HttpURLConnection connection = null;
  InputStream is = null;
  String byteRange = "";
  int n = 0;
  try {
    connection =
        proxy == null
            ? (HttpURLConnection) url.openConnection()
            : (HttpURLConnection) url.openConnection(proxy);

    long endRange = position + len - 1;
    // IF we know the total content length, limit the end range to that.
    if (contentLength > 0) {
      endRange = Math.min(endRange, contentLength);
    }
    byteRange = "bytes=" + position + "-" + endRange;
    connection.setRequestProperty("Range", byteRange);

    is = connection.getInputStream();

    while (n < len) {
      int count = is.read(buffer, offset + n, len - n);
      if (count < 0) {
        if (n == 0) {
          return -1;
        } else {
          break;
        }
      }
      n += count;
    }

    position += n;

    return n;

  } catch (IOException e) {
    // THis is a bit of a hack, but its not clear how else to handle this. If a byte range is
    // specified
    // that goes past the end of the file the response code will be 416. The MAC os translates
    // this to
    // an IOException with the 416 code in the message. Windows translates the error to an
    // EOFException.
    //
    // The BAM file iterator uses the return value to detect end of file (specifically looks for n
    // == 0).
    if (e.getMessage().contains("416") || (e instanceof EOFException)) {
      if (n == 0) {
        return -1;
      } else {
        position += n;
        // As we are at EOF, the contentLength and position are by definition =
        contentLength = position;
        return n;
      }
    } else {
      throw e;
    }

  } finally {
    if (is != null) {
      is.close();
    }
    if (connection != null) {
      connection.disconnect();
    }
  }
}

private void fill(InputStream in, byte[] buf, int len) throws JSchException, IOException {
  int s = 0;
  while (s < len) {
    int i = in.read(buf, s, len - s);
    if (i <= 0) {
      throw new JSchException("ProxySOCKS5: stream is closed");
    }
    s += i;
  }
}

public ImageProcessor open16bitRawImage(InputStream is, int width, int height)
    throws IOException {
  int size = width * height * 2;
  byte[] bytes = new byte[size];
  int count = 0;
  while (count < size && count >= 0) count = is.read(bytes, count, size - count);
  short[] pixels = new short[size / 2];
  for (int i = 0, j = 0; i < size / 2; i++, j += 2)
    pixels[i] = (short) (((bytes[j] & 0xff) << 8) | (bytes[j + 1] & 0xff)); // big endian
  return new ShortProcessor(width, height, pixels, null);
}

