private int loadDataLength() throws IOException {
  int l = in.read();

  if (l < 0) {
    return -1;
  }

  partial = false;
  if (l < 192) {
    dataLength = l;
  } else if (l <= 223) {
    dataLength = ((l - 192) << 8) + (in.read()) + 192;
  } else if (l == 255) {
    dataLength = (in.read() << 24) | (in.read() << 16) | (in.read() << 8) | in.read();
  } else {
    partial = true;
    dataLength = 1 << (l & 0x1f);
  }

  return dataLength;
}

/** Reads the next encoded byte from the encoded input stream. */
protected int read() throws IOException {
  if (offset == buffer.length) {
    throw new ASN1Exception("Unexpected end of encoding");
  }

  if (in == null) {
    return buffer[offset++] & 0xFF;
  } else {
    int octet = in.read();
    if (octet == -1) {
      throw new ASN1Exception("Unexpected end of encoding");
    }

    buffer[offset++] = (byte) octet;

    return octet;
  }
}

