/**
 * Copy all bytes remaining on the input stream into this buffer.
 *
 * @param in the stream to read from, until EOF is reached.
 * @throws IOException an error occurred reading from the input stream, or while writing to a
 *     local temporary file.
 */
public void copy(final InputStream in) throws IOException {
  if (blocks != null) {
    for (; ; ) {
      Block s = last();
      if (s.isFull()) {
        if (reachedInCoreLimit()) break;
        s = new Block();
        blocks.add(s);
      }

      int n = in.read(s.buffer, s.count, s.buffer.length - s.count);
      if (n < 1) return;
      s.count += n;
    }
  }

  final byte[] tmp = new byte[Block.SZ];
  int n;
  while ((n = in.read(tmp)) > 0) overflow.write(tmp, 0, n);
}

