public void execute(WebAppController controller, HttpServletRequest req, HttpServletResponse res)
    throws Exception {
  res.setHeader("Cache-Control", "private max-age=600, s-maxage=120");
  ExoContainer container = ExoContainerContext.getCurrentContainer();
  DownloadService dservice =
      (DownloadService) container.getComponentInstanceOfType(DownloadService.class);
  DownloadResource dresource = dservice.getDownloadResource(resourceId);
  if (dresource == null) {
    res.setContentType("text/plain");
    res.getWriter()
        .write(
            "NO DOWNDLOAD RESOURCE CONTENT OR YOU DO NOT HAVE THE RIGHT TO ACCESS THE CONTENT");
    return;
  }
  String userAgent = req.getHeader("User-Agent");
  if (dresource.getDownloadName() != null) {
    if (userAgent != null && userAgent.contains("MSIE")) {
      res.setHeader(
          "Content-Disposition",
          "attachment;filename=\""
              + URLEncoder.encode(dresource.getDownloadName(), "UTF-8")
              + "\"");
    } else {
      res.setHeader(
          "Content-Disposition",
          "attachment; filename*=utf-8''"
              + URLEncoder.encode(dresource.getDownloadName(), "UTF-8")
              + "");
    }
  }
  res.setContentType(dresource.getResourceMimeType());
  InputStream is = dresource.getInputStream();
  byte[] buf = new byte[is.available()];
  is.read(buf);
  res.setContentType(dresource.getResourceMimeType());
  res.getOutputStream().write(buf);
  is.close();
}

/**
 * Reads into an array of bytes. This method will block until some input is available.
 *
 * @param b the buffer into which the data is read
 * @param off the start offset of the data
 * @param len the maximum number of bytes read
 * @return the actual number of bytes read, -1 is returned when the end of the stream is reached.
 * @throws IOException If an I/O error has occurred.
 */
public void fill() throws IOException {
  byte[] headerBuf = new byte[FCGIConstants.BUFFER_HEADER_LENGTH];
  int headerLen = 0;
  int status = 0;
  int count = 0;
  while (true) {
    /*
     * If buffer is empty, do a read
     */
    if (rdNext == buffStop) {
      try {
        count = in.read(buff, 0, buffLen);
      } catch (IOException e) {
        setException(e);
        return;
      }
      if (count <= 0) {
        setFCGIError(FCGIConstants.ERROR_PROTOCOL_ERROR);
        return;
      }
      rdNext = 0;
      buffStop = count; // 1 more than we read
    }
    /*
     * Now buf is not empty: If the current record contains more content
     * bytes, deliver all that are present in buff to callers buffer
     * unless he asked for less than we have, in which case give him
     * less
     */
    if (contentLen > 0) {
      count = Math.min(contentLen, buffStop - rdNext);
      contentLen -= count;
      if (!skip) {
        stop = rdNext + count;
        return;
      } else {
        rdNext += count;
        if (contentLen > 0) {
          continue;
        } else {
          skip = false;
        }
      }
    }
    /*
     * Content has been consumed by client. If record was padded, skip
     * over padding
     */
    if (paddingLen > 0) {
      count = Math.min(paddingLen, buffStop - rdNext);
      paddingLen -= count;
      rdNext += count;
      if (paddingLen > 0) {
        continue; // more padding to read
      }
    }
    /*
     * All done with current record, including the padding. If we are in
     * a recursive call from Process Header, deliver EOF
     */
    if (eorStop) {
      stop = rdNext;
      isClosed = true;
      return;
    }
    /*
     * Fill header with bytes from input buffer - get the whole header.
     */
    count = Math.min(headerBuf.length - headerLen, buffStop - rdNext);
    count = Math.max(count, 0); // jr: why could it be negative ??
    System.arraycopy(buff, rdNext, headerBuf, headerLen, count);
    headerLen += count;
    rdNext += count;
    if (headerLen < headerBuf.length) {
      continue;
    }
    headerLen = 0;
    /*
     * Interperet the header. eorStop prevents ProcessHeader from
     * reading past the end of record when using stream to read content
     */
    eorStop = true;
    stop = rdNext;
    status = 0;
    status = new FCGIMessage(this).processHeader(headerBuf);
    eorStop = false;
    isClosed = false;
    switch (status) {
      case FCGIConstants.HEADER_STREAM_RECORD:
        if (contentLen == 0) {
          stop = rdNext;
          isClosed = true;
          return;
        }
        break;
      case FCGIConstants.HEADER_SKIP:
        skip = true;
        break;
      case FCGIConstants.HEADER_BEGIN_RECORD:
        /*
         * If this header marked the beginning of a new request, return
         * role info to caller
         */
        return;
      case FCGIConstants.HEADER_MANAGEMENT_RECORD:
        break;
      default:
        /*
         * ASSERT
         */
        setFCGIError(status);
        return;
    }
  }
}

private int skipToNewline(InputStream is, int ch) throws IOException {
  for (; ch >= 0 && ch != '\n'; ch = is.read()) {}

  return is.read();
}

public static int readFully(InputStream is, byte[] b) throws IOException {
  int done = 0;
  int want = b.length;
  while (want > 0) {
    int bytesRead = is.read(b, done, want);
    if (bytesRead == -1) break;
    done += bytesRead;
    want -= bytesRead;
  }
  return done;
}

