    if (one && two) return true;
  } catch (Exception e) {
  }
  return false;
}

private static long readDoubleWord(InputStream is) throws IOException {
  return ((long) is.read() << 24)
      | ((long) is.read() << 16)
      | ((long) is.read() << 8)
      | (long) is.read();
}

/**
 * Reads data from a stream and writes them to a file.
 *
 * @param in The data to be read.
 * @param f The file to receive the data.
 * @return The number of bytes read.
 * @throws FileNotFoundException If the given file does not exist.
 * @throws IOException If the stream cannot be read.
 * @throws IOException If the the file cannot be written.
 * @throws DownloadException if the stream gave no bytes.
 */
private void readFromStream(InputStream in, File f)
    throws FileNotFoundException, IOException, DownloadException {
  BufferedOutputStream out = new BufferedOutputStream(new FileOutputStream(f));
  try {
    long n;
    for (n = 0; true; n++) {
      int c = in.read();
      if (c == -1) {
        break;
      } else {
        out.write(c);
      }
    }
    if (n == 0L) {
      throw new DownloadException("No data were read");
    }
  } finally {
    out.close();
  }
}

/**
 * Reads class byte array info from the given input stream.
 *
 * <p>The stream is closed within this method!
 *
 * @param inputStream The stream containing the class binary; null will lead to an {@link
 *     IOException}
 * @return The read bytes
 * @throws IOException Indicates a problem accessing the given stream.
 */
public static byte[] readByteCode(InputStream inputStream) throws IOException {
  if (inputStream == null) {
    throw new IOException("null input stream");
  }

  byte[] buffer = new byte[409600];
  byte[] classBytes = new byte[0];

  try {
    int r = inputStream.read(buffer);
    while (r >= buffer.length) {
      byte[] temp = new byte[classBytes.length + buffer.length];
      // copy any previously read bytes into the temp array
      System.arraycopy(classBytes, 0, temp, 0, classBytes.length);
      // copy the just read bytes into the temp array (after the previously read)
      System.arraycopy(buffer, 0, temp, classBytes.length, buffer.length);
      classBytes = temp;
      // read the next set of bytes into buffer
      r = inputStream.read(buffer);
    }
    if (r != -1) {
      byte[] temp = new byte[classBytes.length + r];
      // copy any previously read bytes into the temp array
      System.arraycopy(classBytes, 0, temp, 0, classBytes.length);
      // copy the just read bytes into the temp array (after the previously read)
      System.arraycopy(buffer, 0, temp, classBytes.length, r);
      classBytes = temp;
    }
  } finally {
    try {
      inputStream.close();
    } catch (IOException ignore) {
      // intentionally empty
    }
  }

  return classBytes;
}

private void skipElementValue(InputStream is) throws IOException {
  int code = is.read();

  switch (code) {
    case 'B':
    case 'C':
    case 'D':
    case 'F':
    case 'I':
    case 'J':
    case 'S':
    case 'Z':
    case 's':
      is.skip(2);
      return;
    case 'e':
      is.skip(4);
      return;
    case 'c':
      is.skip(2);
      return;
    case '@':
      scanAnnotation(is);
      return;
    case '[':
      int len = readShort(is);
      for (int i = 0; i < len; i++) {
        skipElementValue(is);
      }
      return;
    default:
      throw new IllegalStateException("unknown code: " + (char) code);
  }
}

public void doPost4Html5(HttpServletRequest request, HttpServletResponse response)
    throws ServletException, IOException {
  String source = ""; // 
  String subdir = request.getParameter("subdir"); // 
  if (subdir == null) subdir = "";
  JSONObject json = new JSONObject(); // 

  try {
    // 
    SystemContext context = (SystemContext) request.getSession().getAttribute(Context.KEY);
    if (context == null) {
      json.put("success", false);
      json.put("msg", "");
      writeReturnJson(response, json);
      return;
    }

    // 
    source = URLDecoder.decode(getHtml5FileName(request), "UTF-8");

    // 
    String extension = getExtension(source);

    // 
    if (!StringUtils.isEmpty(extensions)
        && ("," + extensions.toLowerCase() + ",").indexOf("," + extension.toLowerCase() + ",")
            == -1) {
      json.put("success", false);
      json.put("msg", "");
      writeReturnJson(response, json);
      return;
    }

    // 
    int i = request.getContentLength();
    logger.debug("contentLength={}", i);
    byte buffer[] = new byte[i];
    int j = 0;
    InputStream in = request.getInputStream();
    while (j < i) {
      int k = in.read(buffer, j, i - j);
      j += k;
    }

    // 
    int size = buffer.length;
    if (size == 0) {
      json.put("success", false);
      json.put("msg", "");
      writeReturnJson(response, json);
      return;
    }

    // 
    if (maxSize > 0 && size > maxSize) {
      json.put("success", false);
      json.put("msg", "");
      writeReturnJson(response, json);
      return;
    }

    // 
    Calendar now = Calendar.getInstance();
    String datedir = new SimpleDateFormat("yyyyMM").format(now.getTime());

    // 
    String realpath; // 
    String fileName =
        new SimpleDateFormat("yyyyMMddHHmmssSSSS").format(now.getTime())
            + "."
            + extension; // 
    realpath =
        Attach.DATA_REAL_PATH
            + (subdir.length() > 0 ? "/" + subdir : "")
            + "/"
            + datedir
            + "/"
            + fileName;

    // 
    File file = new File(realpath);
    if (!file.getParentFile().exists()) {
      if (logger.isWarnEnabled()) {
        logger.warn("mkdir={}", file.getParentFile().getAbsolutePath());
      }
      file.getParentFile().mkdirs();
    }

    // 
    String path = (subdir.length() > 0 ? subdir + "/" : "") + datedir + "/" + fileName;

    // 
    String ptype = request.getParameter("ptype");
    String puid = request.getParameter("puid");
    if (ptype != null && puid != null) {
      AttachHistory history = new AttachHistory();
      history.setPtype(ptype);
      history.setPuid(puid);
      history.setType(AttachHistory.TYPE_UPLOAD);
      history.setAuthor(context.getUserHistory());
      history.setFileDate(now);
      history.setPath(path);
      history.setAppPath(false);
      history.setFormat(extension);
      history.setSubject(source);
      String[] c = WebUtils.getClient(request);
      history.setClientIp(c[0]);
      history.setClientInfo(c[2]);
      this.getAttachService().saveHistory(history);
    } else {
      logger.warn("ptypepuid");
    }

    // 
    OutputStream out = new BufferedOutputStream(new FileOutputStream(realpath));
    out.write(buffer);
    out.close();

    // 
    json.put("success", true);
    json.put("msg", "");
    json.put("rootdir", Attach.DATA_REAL_PATH); // 
    json.put("subdir", subdir); // 
    json.put("to", datedir + "/" + fileName); // 
    json.put("file", path);
    json.put("source", source); // 
    json.put("size", size); // 
    writeReturnJson(response, json);
  } catch (Exception e) {
    logger.error(e.getMessage(), e);
    try {
      json.put("success", false);
      json.put("msg", e.getMessage());
    } catch (JSONException e1) {
      logger.error(e.getMessage(), e);
    }
    writeReturnJson(response, json);
  }
}

private void readCR(InputStream aStream) throws IOException {
  int cr = aStream.read();
  if (cr != CR) {
    throw new IOException("Expected CR char in InputStream");
  }
}

/**
 * Creates an image from the specified resource. To speed up loading the same images use a cache
 * (a simple hashtable). And flush the resources being used by an image when you won't use it
 * henceforward
 *
 * @param path is relative or the classpath, or an URL
 * @param clazz TODO
 * @return the loaded image or null
 */
public static Image getImage(String path, Class<?> clazz) {
  if ((path == null) || (path.length() == 0)) {
    return null;
  }
  Image image = null; // (Image) imagepool.get(path);
  try {
    URL url = clazz.getResource(path); // ClassLoader.getSystemResource(path)
    if (url != null) { // contributed by Stefan Matthias Aust
      image = Toolkit.getDefaultToolkit().getImage(url);
    }
  } catch (Throwable e) {
  }
  if (image == null) {
    try {
      InputStream is = clazz.getResourceAsStream(path);
      // InputStream is = ClassLoader.getSystemResourceAsStream(path);
      if (is != null) {
        byte[] data = new byte[is.available()];
        is.read(data, 0, data.length);
        image = Toolkit.getDefaultToolkit().createImage(data);
        is.close();
      } else { // contributed by Wolf Paulus
        image = Toolkit.getDefaultToolkit().getImage(new URL(path));
      }
    } catch (Throwable e) {
    }
  }
  return image;
}

