/**
 * Download a file if it does not yet exist. If no checksum is used (that is, if the parameter is
 * null), the checksum is printed. For security, checksums should always be used.
 *
 * @param target the target file name
 * @param fileURL the source url of the file
 * @param sha1Checksum the SHA-1 checksum or null
 */
protected void download(String target, String fileURL, String sha1Checksum) {
  File targetFile = new File(target);
  if (targetFile.exists()) {
    return;
  }
  mkdirs(targetFile.getAbsoluteFile().getParentFile());
  ByteArrayOutputStream buff = new ByteArrayOutputStream();
  try {
    println("Downloading " + fileURL);
    URL url = new URL(fileURL);
    InputStream in = new BufferedInputStream(url.openStream());
    long last = System.currentTimeMillis();
    int len = 0;
    while (true) {
      long now = System.currentTimeMillis();
      if (now > last + 1000) {
        println("Downloaded " + len + " bytes");
        last = now;
      }
      int x = in.read();
      len++;
      if (x < 0) {
        break;
      }
      buff.write(x);
    }
    in.close();
  } catch (IOException e) {
    throw new RuntimeException("Error downloading " + fileURL + " to " + target, e);
  }
  byte[] data = buff.toByteArray();
  String got = getSHA1(data);
  if (sha1Checksum == null) {
    println("SHA1 checksum: " + got);
  } else {
    if (!got.equals(sha1Checksum)) {
      throw new RuntimeException(
          "SHA1 checksum mismatch; got: "
              + got
              + " expected: "
              + sha1Checksum
              + " for file "
              + target);
    }
  }
  writeFile(targetFile, data);
}

/**
 * Reads bytes from an InputStream into the Blob.
 *
 * @param len The number of bytes to attempt to read.
 * @param in The InputStream to read from.
 * @return The number of bytes read and appended to the blob or -1 if the end of the stream was
 *     reached.
 * @exception java.io.IOException If there is a problem reading.
 */
public synchronized int read(int len, InputStream in) throws IOException {

  byte b[] = new byte[len];
  int bytesRead;
  try {
    bytesRead = in.read(b);
  } catch (EOFException e) {
    // We'll just return a -1 just as if
    // we had gotten a -1 in response from the read
    return -1;

  } catch (IOException e) {
    // Throw this one to the caller
    throw (IOException) e.fillInStackTrace();
  }
  append(b, 0, bytesRead);
  return bytesRead;
} // end read();

/**
 * Returns the given input stream's contents as a byte array. If a length is specified (ie. if
 * length != -1), only length bytes are returned. Otherwise all bytes in the stream are returned.
 * Note this doesn't close the stream.
 *
 * @throws IOException if a problem occured reading the stream.
 */
public static byte[] getInputStreamAsByteArray(InputStream stream, int length)
    throws IOException {
  byte[] contents;
  if (length == -1) {
    contents = new byte[0];
    int contentsLength = 0;
    int amountRead = -1;
    do {
      int amountRequested =
          Math.max(stream.available(), DEFAULT_READING_SIZE); // read at least 8K

      // resize contents if needed
      if (contentsLength + amountRequested > contents.length) {
        System.arraycopy(
            contents,
            0,
            contents = new byte[contentsLength + amountRequested],
            0,
            contentsLength);
      }

      // read as many bytes as possible
      amountRead = stream.read(contents, contentsLength, amountRequested);

      if (amountRead > 0) {
        // remember length of contents
        contentsLength += amountRead;
      }
    } while (amountRead != -1);

    // resize contents if necessary
    if (contentsLength < contents.length) {
      System.arraycopy(contents, 0, contents = new byte[contentsLength], 0, contentsLength);
    }
  } else {
    contents = new byte[length];
    int len = 0;
    int readSize = 0;
    while ((readSize != -1) && (len != length)) {
      // See PR 1FMS89U
      // We record first the read size. In this case len is the actual
      // read size.
      len += readSize;
      readSize = stream.read(contents, len, length - len);
    }
  }

  return contents;
}

/** @return true if the data length should be double-checked with a head request. */
private boolean downloadData(InputStream in) throws IOException {

  if (size == -1) {

    ByteArrayOutputStream out = new ByteArrayOutputStream();

    if (startPosition > 0) {
      if (data == null || startPosition > data.length) {
        // Permanent Error - don't automatically retry
        setState(ERROR);
        setErrorMessage("Bad Content-Range");
        return false;
      }
      out.write(data, 0, startPosition);
    }

    data = null;
    byte[] buffer = new byte[BUFFER_SIZE];

    try {
      while (state == DOWNLOADING) {
        int bytesRead = in.read(buffer);

        if (bytesRead == -1) {
          // Either the download is finished, or there was a connection error
          return true;
        } else if (bytesRead > 0) {
          increasePosition(bytesRead);
          markRestartPosition();
          out.write(buffer, 0, bytesRead);
        }
      }
    } finally {
      data = out.toByteArray();
    }
  } else {
    if (data == null) {
      data = new byte[size];
    } else if (data.length < size) {
      byte[] newData = new byte[size];
      System.arraycopy(data, 0, newData, 0, data.length);
      data = newData;
    }

    while (state == DOWNLOADING) {
      int pos = getCurrentPosition();

      int bytesRead = in.read(data, pos, Math.min(BUFFER_SIZE, size - pos));

      if (bytesRead > 0) {
        increasePosition(bytesRead);
        markRestartPosition();
      }

      if (getCurrentPosition() >= size) {
        setState(SUCCESS);
      } else if (bytesRead == -1) {
        throw new IOException("Closed Prematurely");
      }
    }
  }
  return false;
}

public static byte[] overrideBytes(String name, byte[] bytes, File location) {
  try {
    ZipFile zip = new ZipFile(location);
    ZipEntry entry = zip.getEntry(name.replace('.', '/') + ".class");
    if (entry == null) System.out.println(name + " not found in " + location.getName());
    else {
      InputStream zin = zip.getInputStream(entry);
      bytes = new byte[(int) entry.getSize()];
      zin.read(bytes);
      zin.close();
      // System.out.println(name + " was overriden from " + location.getName());
    }
    zip.close();
  } catch (Exception e) {
    throw new RuntimeException("Error overriding " + name + " from " + location.getName(), e);
  }
  return bytes;
}

/**
 * Copies from an input stream (until EOF) to output stream.
 *
 * @param is Input stream (will be closed at end)
 * @param os Output stream
 * @param bCloseOutput If true, will close output stream at end
 * @throws IOException If there's any I/O error in process
 */
public static void copy(InputStream is, OutputStream os, boolean bCloseOutput)
    throws IOException {
  byte[] abBuffer = new byte[COPYBUFFER_LENGTH];
  while (true) {
    int iRead = is.read(abBuffer);
    if (iRead <= 0) break;
    os.write(abBuffer, 0, iRead);
  }
  is.close(); // Was at EOF anyway
  if (bCloseOutput) os.close();
}

