private int receive_n(byte[] b, int length) throws IOException {
  // if (bench != null) bench.startBench("receive_n");
  int n = 0, i = 0, x = 0;
  while (n != length && i != -1) {
    i = is.read(b, n, length - n);
    n += i;
    x++;
  }
  // if (bench != null) bench.stopBench("receive_n");
  // if (bench != null) bench.indepBench("iterations_on read",x);
  if (i == -1) throw new IOException("Received EOF in the socket input stream.");

  return n;
}

public static boolean downLoad(String fname) {
  FileReader fileIn = null;
  try {
    fileIn = new FileReader(fname);
  } catch (FileNotFoundException e1) {
    sysoutStream.println("Error opening " + fname);
    System.exit(-1);
  }

  // read .jop file word for word and write bytes to JOP
  try {
    StreamTokenizer in = new StreamTokenizer(fileIn);
    in.slashSlashComments(true);
    in.whitespaceChars(',', ',');
    int cnt = 0;
    int lbuf[] = new int[CNTMOD];
    for (; in.nextToken() != StreamTokenizer.TT_EOF; ++cnt) {
      // in.nval contains the next 32 bit word to be sent
      int l = (int) in.nval;
      lbuf[cnt % CNTMOD] = l;

      // Java code length at index 1 position in .jop
      if (cnt == 1) {
        sysoutStream.println(l + " words of Java bytecode (" + (l / 256) + " KB)");
      }
      for (int i = 0; i < 4; i++) {
        byte b = (byte) (l >> ((3 - i) * 8));
        outputStream.write(b);
      }

      if (!usb) {
        if (cnt % CNTMOD == CNTMOD - 1) {
          for (int k = 0; k < CNTMOD; k++) {
            int r = 0;
            for (int i = 0; i < 4; i++) {
              r = (r << 8) | (iStream.read() & 0xff);
            }
            if (r != lbuf[k]) {
              sysoutStream.println("received word differs from sent word");
            }
          }
        }
      }

      if ((cnt & 0x3f) == 0) {
        sysoutStream.print(prog_char[(cnt >> 6) & 0x07] + "\r");
      }
    }

    if (!usb) {
      for (int k = 0; k < cnt % CNTMOD; k++) {
        int r = 0;
        for (int i = 0; i < 4; i++) {
          r = (r << 8) | (iStream.read() & 0xff);
        }
        if (r != lbuf[k]) {
          sysoutStream.println("received word differs from sent word");
        }
      }
    }

    sysoutStream.println(cnt + " words external RAM (" + (cnt / 256) + " KB)");
    sysoutStream.println("download complete");
    sysoutStream.println("");
    sysoutStream.println("");

  } catch (IOException e) {
    sysoutStream.println(e);
  }
  return true;
}

