@Override
public void close() throws IOException {
  super.close();
  if (closed) {
    return;
  }
  for (; ; ) {
    int b = delegate.read();
    if (b == 0x23 || b == -1) {
      break;
    }
  }
  closed = true;
}

public static void copy(InputStream src, OutputStream dst, ISVNCanceller canceller)
    throws SVNException {
  try {
    byte[] buffer = ourCopyBuffer.get();
    while (true) {
      if (canceller != null) {
        canceller.checkCancelled();
      }
      int length = src.read(buffer);
      if (length > 0) {
        dst.write(buffer, 0, length);
      }
      if (length < 0) {
        break;
      }
    }
  } catch (IOExceptionWrapper ioew) {
    throw ioew.getOriginalException();
  } catch (IOException ioe) {
    SVNErrorMessage err =
        SVNErrorMessage.create(SVNErrorCode.IO_ERROR, ioe.getLocalizedMessage());
    SVNErrorManager.error(err, ioe, SVNLogType.FSFS);
  }
}

public final long[] decodeFromInputStreamToIntArray(InputStream s) throws IOException {
  final List longList = new ArrayList();
  final byte[] b = new byte[LONG_SIZE];

  while (true) {
    int n = s.read(b);
    if (n != LONG_SIZE) {
      if (n == -1) {
        break;
      }

      while (n != LONG_SIZE) {
        final int m = s.read(b, n, LONG_SIZE - n);
        if (m == -1) {
          throw new EOFException();
        }
        n += m;
      }
    }

    final long l =
        (((long) b[0] << 56)
            + ((long) (b[1] & 0xFF) << 48)
            + ((long) (b[2] & 0xFF) << 40)
            + ((long) (b[3] & 0xFF) << 32)
            + ((long) (b[4] & 0xFF) << 24)
            + ((b[5] & 0xFF) << 16)
            + ((b[6] & 0xFF) << 8)
            + ((b[7] & 0xFF) << 0));

    longList.add(Long.valueOf(l));
  }

  return generateArrayFromList(longList);
}

public void writeTo(final OutputStream outstream) throws IOException {
  if (outstream == null) {
    throw new IllegalArgumentException("Output stream may not be null");
  }
  InputStream instream = this.content;
  byte[] buffer = new byte[BUFFER_SIZE];
  int l;
  if (this.length < 0) {
    // consume until EOF
    while ((l = instream.read(buffer)) != -1) {
      outstream.write(buffer, 0, l);
    }
  } else {
    // consume no more than length
    long remaining = this.length;
    while (remaining > 0) {
      l = instream.read(buffer, 0, (int) Math.min(BUFFER_SIZE, remaining));
      if (l == -1) {
        break;
      }
      outstream.write(buffer, 0, l);
      remaining -= l;
    }
  }
  this.consumed = true;
}

public void seekPastMagicBytes(InputStream in) throws IOException {
  int magicCursor = 3; // Which byte of the magic we're looking for currently.
  while (true) {
    int b = in.read(); // Read a byte.
    if (b == -1) {
      // There's no more data to read.
      throw new IOException("Socket is disconnected");
    }
    // We're looking for a run of bytes that is the same as the packet magic but we want to ignore
    // partial
    // magics that aren't complete. So we keep track of where we're up to with magicCursor.
    int expectedByte = 0xFF & (int) (params.packetMagic >>> (magicCursor * 8));
    if (b == expectedByte) {
      magicCursor--;
      if (magicCursor < 0) {
        // We found the magic sequence.
        return;
      } else {
        // We still have further to go to find the next message.
      }
    } else {
      magicCursor = 3;
    }
  }
}

private void streamOut(InputStream in, OutputStream out, int bufferSize) throws IOException {
  assert (bufferSize >= 0);

  byte[] buffer = new byte[bufferSize];
  int bytesRead = 0;
  while (true) {
    bytesRead = in.read(buffer);
    if (bytesRead >= 0) {
      // Copy the bytes out.
      out.write(buffer, 0, bytesRead);
    } else {
      // End of input stream.
      out.flush();
      return;
    }
  }
}

