public static byte[] readStream(InputStream is, int initialBufferCapacity) throws IOException {
  if (initialBufferCapacity <= 0) {
    throw new IllegalArgumentException("Bad initialBufferCapacity: " + initialBufferCapacity);
  }
  byte[] buffer = new byte[initialBufferCapacity];
  int cursor = 0;
  for (; ; ) {
    int n = is.read(buffer, cursor, buffer.length - cursor);
    if (n < 0) {
      break;
    }
    cursor += n;
    if (cursor == buffer.length) {
      byte[] tmp = new byte[buffer.length * 2];
      System.arraycopy(buffer, 0, tmp, 0, cursor);
      buffer = tmp;
    }
  }
  if (cursor != buffer.length) {
    byte[] tmp = new byte[cursor];
    System.arraycopy(buffer, 0, tmp, 0, cursor);
    buffer = tmp;
  }
  return buffer;
}

/**
 * This is called from URLConnection to guess the mime type of a stream. This method may return
 * null to indicate that it could not guess a type.
 */
static String guessContentTypeFromStream(InputStream is) throws IOException {
  if (!is.markSupported()) return null;
  is.mark(LENGTH);
  byte[] bytes = new byte[LENGTH];
  int r = is.read(bytes);
  if (r < 0) return null;
  is.reset();
  return guessContentTypeFromBuffer(bytes, r);
}

/** Reads the next encoded byte from the encoded input stream. */
protected int read() throws IOException {
  if (offset == buffer.length) {
    throw new ASN1Exception("Unexpected end of encoding");
  }

  if (in == null) {
    return buffer[offset++] & 0xFF;
  } else {
    int octet = in.read();
    if (octet == -1) {
      throw new ASN1Exception("Unexpected end of encoding");
    }

    buffer[offset++] = (byte) octet;

    return octet;
  }
}

public Object engineRead() throws StreamParsingException {
  try {
    if (sData != null) {
      if (sDataObjectCount != sData.size()) {
        return getCertificate();
      } else {
        sData = null;
        sDataObjectCount = 0;
        return null;
      }
    }

    currentStream.mark(10);
    int tag = currentStream.read();

    if (tag == -1) {
      return null;
    }

    if (tag != 0x30) // assume ascii PEM encoded.
    {
      currentStream.reset();
      return readPEMCertificate(currentStream);
    } else {
      currentStream.reset();
      return readDERCertificate(currentStream);
    }
  } catch (Exception e) {
    throw new StreamParsingException(e.toString(), e);
  }
}

/**
 * Returns the ASCII characters up to but not including the next "\r\n", or "\n".
 *
 * @throws java.io.EOFException if the stream is exhausted before the next newline character.
 */
public static String readAsciiLine(InputStream in) throws IOException {
  // TODO: support UTF-8 here instead
  StringBuilder result = new StringBuilder(80);
  while (true) {
    int c = in.read();
    if (c == -1) {
      throw new EOFException();
    } else if (c == '\n') {
      break;
    }

    result.append((char) c);
  }
  int length = result.length();
  if (length > 0 && result.charAt(length - 1) == '\r') {
    result.setLength(length - 1);
  }
  return result.toString();
}

private void sendRequestGetMedia(int iServer, Map mTokens, HttpScript.Item i, StepStats ss)
    throws IOException {
