  OutputStreamWriter osw = new OutputStreamWriter(s.getOutputStream());
  String sRequest = i.getRequest();
  sRequest = XML.replaceTokens(sRequest, "%%", mTokens).replaceAll("HTTP/1.1", "HTTP/1.0");
  osw.write(sRequest + "\r\n");
  if (i.getData() != null) osw.write(i.getData());
  osw.flush();

  // Get input
  InputStream is = s.getInputStream();
  String sLine = "";
  int iContentLength = -1;
  boolean bFirst = true;
  while (true) {
    int iChar = is.read();
    if (iChar == -1) throw new IOException("Unexpected EOF in socket input");
    if (iChar == 10) {
      if (sLine.equals("")) break; // End of headers
      Matcher m = HttpScript.CONTENTLENGTH.matcher(sLine);
      if (m.matches()) iContentLength = Integer.parseInt(m.group(1));
      if (bFirst) {
        bFirst = false;
        Matcher mStatus = HttpScript.STATUSCODE.matcher(sLine);
        if (!mStatus.matches()) throw new IOException("Unexpected HTTP status format: " + sLine);
        if (Integer.parseInt(mStatus.group(1)) != i.getExpectedResponse())
          throw new IOException(
              "Unexpected response code: "
                  + sLine
                  + " (expecting "
                  + i.getExpectedResponse()
                  + ")\n"
                  + IO.loadString(is));
      }
      // System.err.println(sLine);
      sLine = "";
    } else if (iChar != 13) {
      sLine += (char) iChar;
    }
  }

  byte[] abData;
  if (iContentLength != -1) {
    abData = new byte[iContentLength];
    for (int iRead = 0; iRead < abData.length; ) {
      int iThisTime = is.read(abData, iRead, abData.length - iRead);
      if (iThisTime == -1) throw new IOException("Didn't get all data");
      iRead += iThisTime;
    }
  } else {
    ByteArrayOutputStream baos = new ByteArrayOutputStream();
    while (true) {
      int iRead = is.read();
      if (iRead == -1) break;
      baos.write(iRead);
    }
    abData = baos.toByteArray();
  }
  s.close();

  ss.lTime = System.currentTimeMillis() - lBefore;
  if (DEBUG) {
    System.err.print(" [" + abData.length + " bytes]");
    System.err.println(" (" + ss.lTime + " ms)");
  }

  // OK now go through looking for *.gif, jpg, .css, .png
  String sContent = new String(abData);
  Set<String> sDone = new HashSet<String>();
  Matcher m = MEDIALINK.matcher(sContent);
  while (m.find()) {
    String sURL = m.group(1);
    if (sDone.contains(sURL)) continue;
    sDone.add(sURL);

    sURL =
        "http://"
            + HOSTS[iServer]
            + (PORTS[iServer] != 80 ? (":" + PORTS[iServer]) : "")
            + "/om-tn/simple1/"
            + sURL;
    sURL = sURL.replaceAll("simple1/../", "");

    loadMedia(mTokens, new URL(sURL), ss);
  }
}

private void skipTo(String string, InputStream in) throws IOException {
  int state = 0;

  while (true) {
    int b = in.read();
    if (b < 0) throw new EOFException();

    if (b == string.charAt(state)) {
      state++;
      if (state == string.length()) break;
    } else state = 0;
  }
}

public void execute() throws IOException {
  if (out == null) {
    throw new IOException("Not yet initialized");
  } else {
    int amount = in.read(buffer);

    if (amount < 0) {
      out.close();
    } else {
      out.write(buffer, 0, amount);
    }
  }
}

@Override
public int read(char[] charArray) throws IOException {
  lineColPos = 0;
  assert !sniffing;
  assert charArray.length >= 2;
  if (needToNotifyTokenizer) {
    if (driver != null) {
      driver.notifyAboutMetaBoundary();
    }
    needToNotifyTokenizer = false;
  }
  CharBuffer charBuffer = CharBuffer.wrap(charArray);
  charBuffer.limit(charArray.length);
  charBuffer.position(0);
  if (flushing) {
    decoder.flush(charBuffer);
    // return -1 if zero
    int cPos = charBuffer.position();
    return cPos == 0 ? -1 : cPos;
  }
  if (hasPendingReplacementCharacter) {
    charBuffer.put('\uFFFD');
    hasPendingReplacementCharacter = false;
  }
  for (; ; ) {
    if (shouldReadBytes) {
      int oldLimit = byteBuffer.limit();
      int readLen;
      if (charsetBoundaryPassed) {
        readLen = byteArray.length - oldLimit;
      } else {
        readLen = SNIFFING_LIMIT - oldLimit;
      }
      int num = inputStream.read(byteArray, oldLimit, readLen);
      if (num == -1) {
        eofSeen = true;
        inputStream.close();
      } else {
        byteBuffer.position(0);
        byteBuffer.limit(oldLimit + num);
      }
      shouldReadBytes = false;
    }
    boolean finalDecode = false;
    for (; ; ) {
      int oldBytePos = byteBuffer.position();
      CoderResult cr = decoder.decode(byteBuffer, charBuffer, finalDecode);
      bytesRead += byteBuffer.position() - oldBytePos;
      if (cr == CoderResult.OVERFLOW) {
        // Decoder will remember surrogates
        return charBuffer.position();
      } else if (cr == CoderResult.UNDERFLOW) {
        int remaining = byteBuffer.remaining();
        if (!charsetBoundaryPassed) {
          if (bytesRead + remaining >= SNIFFING_LIMIT) {
            needToNotifyTokenizer = true;
            charsetBoundaryPassed = true;
          }
        }

        // XXX what happens if the entire byte buffer consists of
        // a pathologically long malformed sequence?

        // If the buffer was not fully consumed, there may be an
        // incomplete byte sequence that needs to seed the next
        // buffer.
        if (remaining > 0) {
          System.arraycopy(byteArray, byteBuffer.position(), byteArray, 0, remaining);
        }
        byteBuffer.position(0);
        byteBuffer.limit(remaining);
        if (flushing) {
          // The final decode was successful. Not sure if this
          // ever happens.
          // Let's get out in any case.
          int cPos = charBuffer.position();
          return cPos == 0 ? -1 : cPos;
        } else if (eofSeen) {
          // If there's something left, it isn't something that
          // would be
          // consumed in the middle of the stream. Rerun the loop
          // once
          // in the final mode.
          shouldReadBytes = false;
          finalDecode = true;
          flushing = true;
          continue;
        } else {
          // The usual stuff. Want more bytes next time.
          shouldReadBytes = true;
          // return -1 if zero
          int cPos = charBuffer.position();
          return cPos == 0 ? -1 : cPos;
        }
      } else {
        // The result is in error. No need to test.
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < cr.length(); i++) {
          if (i > 0) {
            sb.append(", ");
          }
          sb.append('\u201C');
          sb.append(Integer.toHexString(byteBuffer.get() & 0xFF));
          bytesRead++;
          sb.append('\u201D');
        }
        if (charBuffer.hasRemaining()) {
          charBuffer.put('\uFFFD');
        } else {
          hasPendingReplacementCharacter = true;
        }
        calculateLineAndCol(charBuffer);
        if (cr.isMalformed()) {
          err("Malformed byte sequence: " + sb + ".");
        } else if (cr.isUnmappable()) {
          err("Unmappable byte sequence: " + sb + ".");
        } else {
          throw new RuntimeException(
              "CoderResult was none of overflow, underflow, malformed or unmappable.");
        }
        if (finalDecode) {
          // These were the last bytes of input. Return without
          // relooping.
          // return -1 if zero
          int cPos = charBuffer.position();
          return cPos == 0 ? -1 : cPos;
        }
      }
    }
  }
}

/**
 * Reverts to the version of the file as specified.
 *
 * @param vfs
 * @param uri
 * @param revision
 * @throws NotFoundException
 * @throws ConflictException
 */
public static void revertToVersion(
    final VersionedVFS vfs, final VFSPath uri, final String revision)
    throws NotFoundException, ConflictException {
  final OutputStream out = vfs.getOutputStream(uri);
  final InputStream in = vfs.getInputStream(uri, revision);

  int len;
  final byte[] temp = new byte[65536];

  try {
    len = in.read(temp);

    while (len != -1) {
      out.write(temp, 0, len);
      len = in.read(temp);
    }
  } catch (final IOException e) {
    throw new ConflictException(e);
  }
}

/**
 * Reads from the underlying stream, decodes the data and puts the decoded bytes into the buffer.
 */
private void acquire() throws IOException {
  char[] four = new char[4];
  int i = 0;
  do {
    int b = inputStream.read();
    if (b == -1) {
      if (i != 0) {
        throw new IOException("Bad base64 stream");
      } else {
        buffer = new int[0];
        eof = true;
        return;
      }
    }
    char c = (char) b;
    if (Shared.chars.indexOf(c) != -1 || c == Shared.pad) {
      four[i++] = c;
    } else if (c != '\r' && c != '\n') {
      throw new IOException("Bad base64 stream");
    }
  } while (i < 4);
  boolean padded = false;
  for (i = 0; i < 4; i++) {
    if (four[i] != Shared.pad) {
      if (padded) {
        throw new IOException("Bad base64 stream");
      }
    } else {
      if (!padded) {
        padded = true;
      }
    }
  }
  int l;
  if (four[3] == Shared.pad) {
    if (inputStream.read() != -1) {
      throw new IOException("Bad base64 stream");
    }
    eof = true;
    if (four[2] == Shared.pad) {
      l = 1;
    } else {
      l = 2;
    }
  } else {
    l = 3;
  }
  int aux = 0;
  for (i = 0; i < 4; i++) {
    if (four[i] != Shared.pad) {
      aux = aux | (Shared.chars.indexOf(four[i]) << (6 * (3 - i)));
    }
  }
  buffer = new int[l];
  for (i = 0; i < l; i++) {
    buffer[i] = (aux >>> (8 * (2 - i))) & 0xFF;
  }
}

/**
 * Connects to server and sends the json string 'outputData'
 *
 * @param path The path connecting to
 * @param gsonClientPack The json formated string to be sent to the server
 * @return String The data from the server
 */
public static String connect(String path, String gsonClientPack) {

  System.out.println("Client attempting a connection to : " + ADDRESS + path); // Debugging

  HttpURLConnection connection;
  URL link;
  InputStream input;
  String data = "";
  DataOutputStream output;

  try {
    link = new URL(ADDRESS + path);
    connection = (HttpURLConnection) link.openConnection();
    connection.setAllowUserInteraction(true);
    connection.setRequestMethod("POST");
    connection.setDoOutput(true);
    connection.setDoInput(true);
    connection.setInstanceFollowRedirects(false);

    output = new DataOutputStream(connection.getOutputStream());
    output.writeBytes(gsonClientPack);
    output.flush();
    output.close();

    try { // Try to read the data from server
      input = connection.getInputStream();
      while (input.available() <= 0) ; // no data from server, waiting...
      while (input.available() > 0) {
        int number = input.read();
        data += (char) number;
      }
      input.close();
    } catch (Exception e) {
      e.printStackTrace();
      data = gsonClientPack; // in the case of an exception, return the original pack
    }

    connection.disconnect();
  } catch (Exception e) {
    e.printStackTrace();
  }

  // DEBUGGING //

  System.out.println("Client succesfully connected to : " + ADDRESS + path); // Debugging
  System.out.println("Data To Server: " + gsonClientPack);
  System.out.println("Data From Server: " + data);
  System.out.println("---------------------------------------");

  // END //

  return data;
}

public void copyStreamToFile(InputStream stream, File file) throws FilesystemAccessException {
  String name = file.getFullName();
  long total = 0;
  try {
    byte[] buffer = new byte[TRANSFER_SIZE];
    while (true) {
      int len = stream.read(buffer);
      if (len < 0) break;
      total += len;
      log.debug(
          "read " + len + " bytes from source stream (total: " + total + ") bound for " + name);
      if (len == buffer.length) file.appendContents(buffer);
      else {
        byte[] newBuf = new byte[len];
        System.arraycopy(buffer, 0, newBuf, 0, len);
        file.appendContents(newBuf);
      }
    }
  } catch (IOException exn) {
    throw new FilesystemAccessException("failed to transfer bytes", exn);
  }
}

/**
 * encode the input stream and write the base64 encoded output into the output stream
 *
 * @throws IOException if the input/output streams cannot be read/written
 */
public static void encode(InputStream is, OutputStream os, int breakLen, String breakStr)
    throws IOException {

  // The BUF SIZE must be a multiple of 3 to avoid b64 intermeditate padding
  int BUF_SIZE = 16380;
  byte buf[] = new byte[BUF_SIZE];
  int size = is.read(buf);
  int col = 0;
  while (size > 0) {
    // Encode this chunk
    byte encoded[];
    if (size != BUF_SIZE) {
      byte tmp[] = new byte[size];
      System.arraycopy(buf, 0, tmp, 0, size);
      encoded = encode(tmp);
    } else {
      encoded = encode(buf);
    }
    // Now print it to the OS, breaking if required
    for (int i = 0; i < encoded.length; ++i) {
      os.write((char) encoded[i]);
      col++;
      if (col == breakLen - breakStr.length()) {
        os.write(breakStr.getBytes());
        col = 0;
      }
    }
    size = is.read(buf);
  }
}

