}

private static long readData(InputStream in, OutputStream out, char delimiter)
    throws IOException {
  long bytes = 0;
  while (true) {

    int b = in.read();
    if (b == -1) {
      break;
    }
    bytes += 1;

    byte c = (byte) b;
    if (c == EOL || c == delimiter) {
      break;
    }

    if (c == '\r') {
      in.mark(1);
      byte nextC = (byte) in.read();
      if (nextC != EOL || c == delimiter) {
        in.reset();
      } else {
        bytes += 1;
      }
      break;
    }

    if (out != null) {
      out.write(c);
    }
  }
  return bytes;
}

public byte readByte() throws IOException {
  int result = inputStream.read();
  if (result == -1) {
    throw new IOException("Unexpected end of stream");
  }
  return (byte) result;
}

/**
 * Read the appropriate number of bytes from the stream and return them to the caller.
 *
 * <p>However, for the purposes of the POI project, this risk is deemed negligible. It is,
 * however, so noted.
 *
 * @param stream the InputStream we're reading from
 * @param size the number of bytes to read; in 99.99% of cases, this will be SHORT_SIZE, INT_SIZE,
 *     or LONG_SIZE -- but it doesn't have to be.
 * @return the byte array containing the required number of bytes. The array will contain all
 *     zero's on end of stream
 * @exception IOException will be propagated back to the caller
 * @exception BufferUnderrunException if the stream cannot provide enough bytes
 */
public static byte[] readFromStream(final InputStream stream, final int size)
    throws IOException, BufferUnderrunException {
  byte[] buffer = new byte[size];

  int count = stream.read(buffer);

  if (count == -1) {

    // return a zero-filled buffer
    Arrays.fill(buffer, (byte) 0);
  } else if (count != size) {
    throw new BufferUnderrunException();
  }
  return buffer;
}

private X509Certificate generateCert(InputStream inStream)
    throws IOException, CertificateException {
  if (inStream == null) throw new CertificateException("missing input stream");
  if (!inStream.markSupported()) inStream = new BufferedInputStream(inStream, 8192);
  inStream.mark(20);
  int i = inStream.read();
  if (i == -1) throw new EOFException();
  // If the input is in binary DER format, the first byte MUST be
  // 0x30, which stands for the ASN.1 [UNIVERSAL 16], which is the
  // UNIVERSAL SEQUENCE, with the CONSTRUCTED bit (0x20) set.
  //
  // So if we do not see 0x30 here we will assume it is in Base-64.
  if (i != 0x30) {
    inStream.reset();
    CPStringBuilder line = new CPStringBuilder(80);
    do {
      line.setLength(0);
      do {
        i = inStream.read();
        if (i == -1) throw new EOFException();
        if (i != '\n' && i != '\r') line.append((char) i);
      } while (i != '\n' && i != '\r');
    } while (!line.toString().equals(BEGIN_CERTIFICATE));
    X509Certificate ret =
        new X509Certificate(new BufferedInputStream(new Base64InputStream(inStream), 8192));
    line.setLength(0);
    line.append('-'); // Base64InputStream will eat this.
    do {
      i = inStream.read();
      if (i == -1) throw new EOFException();
      if (i != '\n' && i != '\r') line.append((char) i);
    } while (i != '\n' && i != '\r');
    // XXX ???
    if (!line.toString().equals(END_CERTIFICATE))
      throw new CertificateException("no end-of-certificate marker");
    return ret;
  } else {
    inStream.reset();
    return new X509Certificate(inStream);
  }
}

