public static void eat(InputStream is) throws IOException {
  byte[] abBuffer = new byte[COPYBUFFER_LENGTH];
  while (true) {
    int iRead = is.read(abBuffer);
    if (iRead <= 0) break;
  }
  is.close(); // Was at EOF anyway
