    fail("Test 4: Unexpected IndexOutOfBoundsException.");
  }
  assertEquals("Test 4:", bytesRead, 4);

  // Test 5: Border case.
  try {
    bytesRead = is.read(b, 6, 0);
  } catch (Exception e) {
    fail("Test 5: Unexpected Exception " + e.getMessage());
  }
  assertEquals("Test 5:", bytesRead, 0);
}

private static String readPythonString(InputStream stream) throws IOException {
  int length = (int) readInt(stream);
  byte[] buf = new byte[length];
  stream.read(buf, 0, length);
  String result = new String(buf);
  return result;
}

/** Copy a file from one place to another, providing progress along the way. */
public static void copyFile(File fromFile, File toFile, IProgressMonitor monitor)
    throws IOException {

  // System.out.println("copying " + fromFile.getName());

  byte[] data = new byte[4096];

  InputStream in = new FileInputStream(fromFile);

  toFile.delete();

  OutputStream out = new FileOutputStream(toFile);

  int count = in.read(data);

  while (count != -1) {
    out.write(data, 0, count);

    monitor.worked(count);

    count = in.read(data);
  }

  in.close();
  out.close();

  toFile.setLastModified(fromFile.lastModified());

  monitor.done();
}

protected SourceType createType(String groupName, ZipFile jarFile, ZipEntry elem)
    throws IOException {

  String typeName = elem.getName();
  if (!typeName.endsWith(JS)) {
    return null;
  }
  int end = typeName.lastIndexOf(JS); // remove .js

  typeName = typeName.substring(0, end);
  typeName = typeName.replace("\\", ".");
  typeName = typeName.replace("/", ".");

  InputStream stream = jarFile.getInputStream(elem);
  byte[] bs = new byte[stream.available()];
  stream.read(bs);
  stream.close();

  String source = new String(bs);

  File f = null;
  f = new File(jarFile.getName() + "!" + elem.getName());

  SourceType srcType = new SourceType(groupName, typeName, source, f);

  return srcType;
}

/**
 * tar a resource
 *
 * @param r the resource to tar
 * @param tOut the output stream
 * @param vPath the path name of the file to tar
 * @param tarFileSet the fileset that the file came from, may be null.
 * @throws IOException on error
 * @since Ant 1.7
 */
protected void tarResource(Resource r, TarOutputStream tOut, String vPath, TarFileSet tarFileSet)
    throws IOException {

  if (!r.isExists()) {
    return;
  }

  if (tarFileSet != null) {
    String fullpath = tarFileSet.getFullpath(this.getProject());
    if (fullpath.length() > 0) {
      vPath = fullpath;
    } else {
      // don't add "" to the archive
      if (vPath.length() <= 0) {
        return;
      }

      String prefix = tarFileSet.getPrefix(this.getProject());
      // '/' is appended for compatibility with the zip task.
      if (prefix.length() > 0 && !prefix.endsWith("/")) {
        prefix = prefix + "/";
      }
      vPath = prefix + vPath;
    }

    if (vPath.startsWith("/") && !tarFileSet.getPreserveLeadingSlashes()) {
      int l = vPath.length();
      if (l <= 1) {
        // we would end up adding "" to the archive
        return;
      }
      vPath = vPath.substring(1, l);
    }
  }

  if (r.isDirectory() && !vPath.endsWith("/")) {
    vPath += "/";
  }

  if (vPath.length() >= TarConstants.NAMELEN) {
    if (longFileMode.isOmitMode()) {
      log("Omitting: " + vPath, Project.MSG_INFO);
      return;
    } else if (longFileMode.isWarnMode()) {
      log(
          "Entry: " + vPath + " longer than " + TarConstants.NAMELEN + " characters.",
          Project.MSG_WARN);
      if (!longWarningGiven) {
        log(
            "Resulting tar file can only be processed "
                + "successfully by GNU compatible tar commands",
            Project.MSG_WARN);
        longWarningGiven = true;
      }
    } else if (longFileMode.isFailMode()) {
      throw new BuildException(
          "Entry: " + vPath + " longer than " + TarConstants.NAMELEN + "characters.",
          getLocation());
    }
  }

  TarEntry te = new TarEntry(vPath);
  te.setModTime(r.getLastModified());
  // preserve permissions
  if (r instanceof ArchiveResource) {
    ArchiveResource ar = (ArchiveResource) r;
    te.setMode(ar.getMode());
    if (r instanceof TarResource) {
      TarResource tr = (TarResource) r;
      te.setUserName(tr.getUserName());
      te.setUserId(tr.getUid());
      te.setGroupName(tr.getGroup());
      te.setGroupId(tr.getGid());
    }
  }

  if (!r.isDirectory()) {
    if (r.size() > TarConstants.MAXSIZE) {
      throw new BuildException(
          "Resource: " + r + " larger than " + TarConstants.MAXSIZE + " bytes.");
    }
    te.setSize(r.getSize());
    // override permissions if set explicitly
    if (tarFileSet != null && tarFileSet.hasFileModeBeenSet()) {
      te.setMode(tarFileSet.getMode());
    }
  } else if (tarFileSet != null && tarFileSet.hasDirModeBeenSet()) {
    // override permissions if set explicitly
    te.setMode(tarFileSet.getDirMode(this.getProject()));
  }

  if (tarFileSet != null) {
    // only override permissions if set explicitly
    if (tarFileSet.hasUserNameBeenSet()) {
      te.setUserName(tarFileSet.getUserName());
    }
    if (tarFileSet.hasGroupBeenSet()) {
      te.setGroupName(tarFileSet.getGroup());
    }
    if (tarFileSet.hasUserIdBeenSet()) {
      te.setUserId(tarFileSet.getUid());
    }
    if (tarFileSet.hasGroupIdBeenSet()) {
      te.setGroupId(tarFileSet.getGid());
    }
  }

  InputStream in = null;
  try {
    tOut.putNextEntry(te);

    if (!r.isDirectory()) {
      in = r.getInputStream();

      byte[] buffer = new byte[8 * 1024];
      int count = 0;
      do {
        tOut.write(buffer, 0, count);
        count = in.read(buffer, 0, buffer.length);
      } while (count != -1);
    }

    tOut.closeEntry();
  } finally {
    FileUtils.close(in);
  }
}

private void readLF(InputStream aStream) throws IOException {
  int lf = aStream.read();
  if (lf != LF) {
    throw new IOException("Expected LF char in InputStream");
  }
}

protected void onCreate(Bundle savedInstanceState) {
  // make activity accessible to Unity
  IOuyaActivity.SetActivity(this);

  // make bundle accessible to Unity
  IOuyaActivity.SetSavedInstanceState(savedInstanceState);

  super.onCreate(savedInstanceState);

  // load the raw resource for the application key
  try {
    InputStream inputStream = getResources().openRawResource(R.raw.key);
    byte[] applicationKey = new byte[inputStream.available()];
    inputStream.read(applicationKey);
    inputStream.close();
    IOuyaActivity.SetApplicationKey(applicationKey);
  } catch (IOException e) {
    e.printStackTrace();
  }

  // Create the UnityPlayer
  IOuyaActivity.SetUnityPlayer(new UnityPlayer(this));
  int glesMode = IOuyaActivity.GetUnityPlayer().getSettings().getInt("gles_mode", 1);
  boolean trueColor8888 = false;
  IOuyaActivity.GetUnityPlayer().init(glesMode, trueColor8888);
  setContentView(R.layout.main);

  // Add the Unity view
  FrameLayout layout = (FrameLayout) findViewById(R.id.unityLayout);
  LayoutParams lp = new LayoutParams(LayoutParams.MATCH_PARENT, LayoutParams.MATCH_PARENT);
  layout.addView(IOuyaActivity.GetUnityPlayer().getView(), 0, lp);
  IOuyaActivity.SetLayout(layout);

  // Set the focus
  RelativeLayout mainLayout = (RelativeLayout) findViewById(R.id.mainLayout);
  mainLayout.setFocusableInTouchMode(true);

  Context context = getBaseContext();

  // Init the controller
  OuyaController.init(context);
}

public static String readStringFromStream(InputStream in) throws IOException {

  StringBuilder sb = new StringBuilder(1024);

  for (int i = in.read(); i != -1; i = in.read()) {
    sb.append((char) i);
  }

  in.close();

  return sb.toString();
}

public static byte[] createChecksum(String filename) throws Exception {
  InputStream fis = new FileInputStream(filename);

  byte[] buffer = new byte[1024];
  MessageDigest complete = MessageDigest.getInstance("MD5");
  int numRead;
  do {
    numRead = fis.read(buffer);
    if (numRead > 0) {
      complete.update(buffer, 0, numRead);
    }
  } while (numRead != -1);
  fis.close();
  return complete.digest();
}

private int bsR(final int n) throws IOException {
  int bsLiveShadow = this.bsLive;
  int bsBuffShadow = this.bsBuff;

  if (bsLiveShadow < n) {
    final InputStream inShadow = this.in;
    do {
      int thech = inShadow.read();

      if (thech < 0) {
        throw new IOException("unexpected end of stream");
      }

      bsBuffShadow = (bsBuffShadow << 8) | thech;
      bsLiveShadow += 8;
    } while (bsLiveShadow < n);

    this.bsBuff = bsBuffShadow;
  }

  this.bsLive = bsLiveShadow - n;
  return (bsBuffShadow >> (bsLiveShadow - n)) & ((1 << n) - 1);
}
