public static void main(String[] args) {
  // print options
  System.out.println("Options:");
  System.out.println(
      "\t1 -- Launch #1: The kit robot picking kits from the conveyer, putting them on the stand, and the reverse.");
  System.out.println(
      "\t2 -- Launch #2: The parts robot picking up parts from the nests and putting them in the kit includes vision - use of camera.");
  System.out.println("\t3 -- Launch #3: Parts dumped into bins and fed down the lanes.");
  System.out.print("\n\n");

  Scanner s = new Scanner(System.in);
  int choice = -1;

  // retrieve option from user
  while (true) {
    System.out.print("Choose a number: ");

    String inStr = s.next().toLowerCase().trim();

    try {
      choice = Integer.parseInt(inStr);
      if (choice < 1 || choice > 3) throw new Exception();
      break;
    } catch (Exception e) {
    }
  }

  // instantiate JFrame and put correct demo panel in it
  jf = new JFrame("CSCI 200 -- Team 11");
  JPanel jp = new JPanel();

  switch (choice) {
    case 1:
      jp = new KitRobotDemo();
      break;
    case 2:
      jp = new PartRobotDemo();
      break;
    case 3:
      jp = new LaneDemo();
      break;
    default:
      jp = null;
      break;
  }

  jf.add(jp);
  jf.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
  jf.pack();
  jf.setVisible(true);

  // start timer
  Timer t = new Timer(50, new V0Main());
  t.start();
}

private void createAndShowGUI(final BundleContext context) {
  synchronized (this) {
    if (frame == null) {
      frame = new JFrame("Greeter test");

      frame.setDefaultCloseOperation(JFrame.HIDE_ON_CLOSE);
      frame.setSize(300, 300);

      final JPanel panel = new JPanel();
      panel.setLayout(new BoxLayout(panel, BoxLayout.Y_AXIS));
      final JTextField text = new JTextField("< enter name >");
      text.setAlignmentX(Component.CENTER_ALIGNMENT);
      panel.add(text);
      final JButton button = new JButton("Send greetings!");
      button.setAlignmentX(Component.CENTER_ALIGNMENT);
      panel.add(button);
      final JLabel label = new JLabel("");
      label.setAlignmentX(Component.CENTER_ALIGNMENT);
      panel.add(label);
      frame.add(panel);

      button.addActionListener(
          new ActionListener() {
            @Override
            public void actionPerformed(ActionEvent evt) {
              ServiceReference ref =
                  context.getServiceReference(GreeterInterface.class.getName());
              if (ref != null) {
                GreeterInterface greeter = (GreeterInterface) context.getService(ref);
                label.setText(greeter.greet(text.getText()));
                context.ungetService(ref);
              } else {
                label.setText("No greeter service available");
              }
              frame.repaint();
            }
          });
    }

    frame.pack();
    frame.setVisible(true);
  }
}
