    } else {
      fillAddressFieldCallRequest(
          userLocation, arrivalField, arrivalLocationButton, arrivalLocationProgress);
    }
  }
}

@Override
public void handleMessage(android.os.Message msg) {
  switch (msg.what) {
    case MSG_PROGRESS:
      setProgressBarIndeterminateVisibility(msg.arg1 != 0);
      break;
    case MSG_ATTACHMENT_PROGRESS:
      boolean progress = (msg.arg1 != 0);
      if (progress) {
        mProgressDialog.setMessage(
            getString(R.string.message_view_fetching_attachment_progress, mLoadAttachmentName));
        mProgressDialog.show();
      } else {
        mProgressDialog.dismiss();
      }
      setProgressBarIndeterminateVisibility(progress);
      break;
    case MSG_LOAD_CONTENT_URI:
      String uriString = (String) msg.obj;
      if (mMessageContentView != null) {
        mMessageContentView.loadUrl(uriString);
      }
      break;
    case MSG_SET_ATTACHMENTS_ENABLED:
      for (int i = 0, count = mAttachments.getChildCount(); i < count; i++) {
        AttachmentInfo attachment = (AttachmentInfo) mAttachments.getChildAt(i).getTag();
        attachment.viewButton.setEnabled(msg.arg1 == 1);
        attachment.downloadButton.setEnabled(msg.arg1 == 1);
      }
      break;
    case MSG_LOAD_BODY_ERROR:
      Toast.makeText(MessageView.this, R.string.error_loading_message_body, Toast.LENGTH_LONG)
          .show();
      break;
    case MSG_NETWORK_ERROR:
      Toast.makeText(MessageView.this, R.string.status_network_error, Toast.LENGTH_LONG).show();
      break;
    case MSG_FETCHING_ATTACHMENT:
      Toast.makeText(
              MessageView.this,
              getString(R.string.message_view_fetching_attachment_toast),
              Toast.LENGTH_SHORT)
          .show();
      break;
    case MSG_VIEW_ATTACHMENT_ERROR:
      Toast.makeText(
              MessageView.this,
              getString(R.string.message_view_display_attachment_toast),
              Toast.LENGTH_SHORT)
          .show();
      break;
    case MSG_UPDATE_ATTACHMENT_ICON:
      ((AttachmentInfo) mAttachments.getChildAt(msg.arg1).getTag())
          .iconView.setImageBitmap((Bitmap) msg.obj);
      break;
    case MSG_FINISH_LOAD_ATTACHMENT:
      long attachmentId = (Long) msg.obj;
      doFinishLoadAttachment(attachmentId);
      break;
    default:
      super.handleMessage(msg);
  }
}

private String queryYahooWeather(Context context, String queryString) {
  Log.d("tag", "QueryYahooWeather");
  String qResult = "";
  // queryString = Uri.encode(queryString);

  HttpClient httpClient = new DefaultHttpClient();

  // return Uri.encode(queryString);

  HttpGet httpGet = new HttpGet(queryString);

  try {
    HttpEntity httpEntity = httpClient.execute(httpGet).getEntity();

    if (httpEntity != null) {
      InputStream inputStream = httpEntity.getContent();
      Reader in = new InputStreamReader(inputStream);
      BufferedReader bufferedreader = new BufferedReader(in);
      StringBuilder stringBuilder = new StringBuilder();

      String stringReadLine = null;

      while ((stringReadLine = bufferedreader.readLine()) != null) {
        stringBuilder.append(stringReadLine + "\n");
      }

      qResult = stringBuilder.toString();
    }

  } catch (ClientProtocolException e) {
    e.printStackTrace();
    Toast.makeText(context, e.toString(), Toast.LENGTH_LONG).show();
  } catch (IOException e) {
    e.printStackTrace();
    Toast.makeText(context, e.toString(), Toast.LENGTH_LONG).show();
  }

  return qResult;
}

@Override
public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
  if (!Util.hasSdcard()) {
    CharSequence errorStrings = mContext.getResources().getText(R.string.insert_sd_card);
    Toast.makeText(mContext, errorStrings, Toast.LENGTH_SHORT).show();
    return;
  }
  Map<String, Object> curMap = (Map<String, Object>) gridView.getAdapter().getItem(position);
  final int natureId = (Integer) curMap.get("id");
  DownloadInfo dInfo = DownLoadDBHelper.getInstances().get(natureId);
  if (dInfo != null) {
    boolean isCompleted = DownloadManager.getInstances().isCompleted(natureId);
    if (isCompleted) {
      final DownloadInfo dinfo = DownLoadDBHelper.getInstances().get(natureId);
      String localname = dinfo.getLocalname();
      Util.installAPK(Constants.DOWNLOAD_APK_DIR, localname, true);
      return;
    }
    boolean isDowmloading = DownloadManager.getInstances().getDowmloadingTask(natureId) != null;
    if (isDowmloading) {
      return;
    }
  } else if (dInfo == null) {
    dInfo = new DownloadInfo();
    dInfo.setId(natureId);
    dInfo.setFilename((String) curMap.get("name"));
    dInfo.setLocalname((String) curMap.get("name"));
    dInfo.setUrl((String) curMap.get("url"));
    dInfo.setCompletesize(0);
    dInfo.setFilesize((Integer) curMap.get("size"));
    DownLoadDBHelper.getInstances().insert(dInfo);
  }

  startDownLoadApk((JoyIconView) view, dInfo);
}

/**
 * Starts an e-mail client with all fields filled in. Message is taken from the view, subject is
 * in a string and receiver is decided by a checkbox status.
 *
 * @param view the view to get the message from.
 */
public void sendFaultreportEmail(View view) {
  Intent intent = new Intent(Intent.ACTION_SENDTO);
  intent.setType("text/plain");

  EditText editText =
      (EditText) findViewById(R.id.faultreport_edit_message); // fetch the text from the view
  String message = editText.getText().toString();

  intent.putExtra(Intent.EXTRA_SUBJECT, getString(R.string.faultreport_subject));
  intent.putExtra(Intent.EXTRA_TEXT, message);

  CheckBox checkBox = (CheckBox) findViewById(R.id.checkBox1);
  if (checkBox.isChecked()) {
    intent.setData(
        Uri.parse(
            "mailto:"
                + getString(
                    R.string.faultreport_dataEmail))); // set the address to the data center.
  } else {
    intent.setData(
        Uri.parse(
            "mailto:"
                + getString(
                    R.string
                        .faultreport_buildingEmail))); // set the address to the building center.
  }

  try {
    startActivity(intent);
  } catch (
      android.content.ActivityNotFoundException
          ex) { // if no email clients on the phone, post a toast to let the user know.
    Toast.makeText(FaultreportActivity.this, R.string.noEmailClientFound, Toast.LENGTH_SHORT)
        .show();
  }
}

/** Sends a newly acquired photo to Gallery for cropping */
protected void doCropPhoto(File f) {
  try {

    // Add the image to the media store
    MediaScannerConnection.scanFile(
        this, new String[] {f.getAbsolutePath()}, new String[] {null}, null);

    // Launch gallery to crop the photo
    final Intent intent = getCropImageIntent(Uri.fromFile(f));
    startActivityForResult(intent, PHOTO_PICKED_WITH_DATA);
  } catch (Exception e) {
    Log.e(TAG, "Cannot crop image", e);
    Toast.makeText(this, R.string.photoPickerNotFoundText, Toast.LENGTH_LONG).show();
  }
}

@Override
public boolean onContextItemSelected(MenuItem item) {
  // It is possible that the view has been canceled when we get to
  // this point as back has a higher priority
  if (mCanceled) {
    return true;
  }
  AdapterView.AdapterContextMenuInfo i = (AdapterView.AdapterContextMenuInfo) item.getMenuInfo();
  // If we have no menu info, we can't tell which item was selected.
  if (i == null) {
    return true;
  }

  switch (item.getItemId()) {
    case R.id.new_context_menu_id:
      saveCurrentPage();
      break;
    case R.id.open_context_menu_id:
      loadUrl(i.position);
      break;
    case R.id.edit_context_menu_id:
      editBookmark(i.position);
      break;
    case R.id.shortcut_context_menu_id:
      final Intent send = createShortcutIntent(i.position);
      send.setAction(INSTALL_SHORTCUT);
      sendBroadcast(send);
      break;
    case R.id.delete_context_menu_id:
      if (mMostVisited) {
        Browser.deleteFromHistory(getContentResolver(), getUrl(i.position));
        refreshList();
      } else {
        displayRemoveBookmarkDialog(i.position);
      }
      break;
    case R.id.new_window_context_menu_id:
      openInNewWindow(i.position);
      break;
    case R.id.share_link_context_menu_id:
      BrowserActivity.sharePage(
          BrowserBookmarksPage.this,
          mBookmarksAdapter.getTitle(i.position),
          getUrl(i.position),
          getFavicon(i.position),
          mBookmarksAdapter.getScreenshot(i.position));
      break;
    case R.id.copy_url_context_menu_id:
      copy(getUrl(i.position));
      break;
    case R.id.homepage_context_menu_id:
      BrowserSettings.getInstance().setHomePage(this, getUrl(i.position));
      Toast.makeText(this, R.string.homepage_set, Toast.LENGTH_LONG).show();
      break;
      // Only for the Most visited page
    case R.id.save_to_bookmarks_menu_id:
      boolean isBookmark;
      String name;
      String url;
      if (mViewMode == BookmarkViewMode.GRID) {
        isBookmark = mBookmarksAdapter.getIsBookmark(i.position);
        name = mBookmarksAdapter.getTitle(i.position);
        url = mBookmarksAdapter.getUrl(i.position);
      } else {
        HistoryItem historyItem = ((HistoryItem) i.targetView);
        isBookmark = historyItem.isBookmark();
        name = historyItem.getName();
        url = historyItem.getUrl();
      }
      // If the site is bookmarked, the item becomes remove from
      // bookmarks.
      if (isBookmark) {
        Bookmarks.removeFromBookmarks(this, getContentResolver(), url, name);
      } else {
        Browser.saveBookmark(this, name, url);
      }
      break;
    default:
      return super.onContextItemSelected(item);
  }
  return true;
}

