@Override
public boolean onPreferenceTreeClick(PreferenceScreen preferenceScreen, Preference preference) {
  final String key = preference.getKey();
  if (KEY_CLEAR_SEARCH_HISTORY.equals(key)) {
    SearchRecentSuggestions suggestions =
        new SearchRecentSuggestions(
            getActivity(),
            Utils.getSearchAuthority(getActivity()),
            CalendarRecentSuggestionsProvider.MODE);
    suggestions.clearHistory();
    Toast.makeText(getActivity(), R.string.search_history_cleared, Toast.LENGTH_SHORT).show();
    return true;
  } else {
    return super.onPreferenceTreeClick(preferenceScreen, preference);
  }
}

@Override
protected void onCreate(Bundle savedInstanceState) {
  Log.d(Tag, "Checking version");
  if (android.os.Build.VERSION.SDK_INT <= android.os.Build.VERSION_CODES.DONUT) {
    Log.d(Tag, "Getting context");
    Context context = getApplicationContext();

    CharSequence text = "Too old phone - please upgrade!";
    int duration = 1000;

    Toast toast = Toast.makeText(context, text, duration);

    Log.d(Tag, "Showing toast message");
    toast.show();
  } else {
    Log.d(Tag, "Calling super");
    super.onCreate(savedInstanceState);

    setContentView(R.layout.activity_main);
    Log.d(Tag, "Change onCreate state");
  }
}

private void startPickActivity(Intent intent) {
  try {
    startActivityForResult(intent, REQUEST_CODE_PICK_FILE_OR_DIRECTORY);
  } catch (ActivityNotFoundException e) {
    // Should never happen, but Java requires this catch
    Toast.makeText(this, R.string.saveas_error, Toast.LENGTH_SHORT).show();
  }
}

public void onClick(View v) {
  switch (v.getId()) {
    case R.id.btn_delete:
      MenuHelper.deleteImage(this, mDeletePhotoRunnable, mAllImages.getImageAt(mCurrentPosition));
      break;
    case R.id.btn_play:
      startPlayVideoActivity();
      break;
    case R.id.btn_share:
      {
        IImage image = mAllImages.getImageAt(mCurrentPosition);
        if (!MenuHelper.isWhiteListUri(image.fullSizeImageUri())) {
          return;
        }
        startShareMediaActivity(image);
        break;
      }
    case R.id.btn_set_as:
      {
        IImage image = mAllImages.getImageAt(mCurrentPosition);
        Intent intent = Util.createSetAsIntent(image);
        try {
          startActivity(Intent.createChooser(intent, getText(R.string.setImage)));
        } catch (android.content.ActivityNotFoundException ex) {
          Toast.makeText(this, R.string.no_way_to_share_video, Toast.LENGTH_SHORT).show();
        }
        break;
      }
    case R.id.btn_done:
      finish();
      break;
    case R.id.next_image:
      moveNextOrPrevious(1);
      break;
    case R.id.prev_image:
      moveNextOrPrevious(-1);
      break;
  }
}

/**
 * Perform importing of preferences from the specified JSON file.
 *
 * @param uri JSON file.
 */
private void jsonImport(final Uri uri) {
  final Resources resources = this.getResources();
  final SharedPreferences.Editor editor =
      this.getPreferenceManager().getSharedPreferences().edit();

  try {
    // Load entire file into JSONOBject
    final BufferedReader file =
        new BufferedReader(new InputStreamReader(this.getContentResolver().openInputStream(uri)));
    final StringBuilder json = new StringBuilder();
    String data;
    while ((data = file.readLine()) != null) {
      json.append(data);
    }
    final JSONObject settings = new JSONObject(json.toString());

    // DISPLAY
    final String display_key = resources.getString(R.string.settings_display_key);
    if (settings.has(display_key)) {
      final JSONObject display = settings.getJSONObject(display_key);

      // fps
      final String display_fps = resources.getString(R.string.settings_display_fps_key);
      if (display.has(display_fps)) {
        editor.putInt(display_fps, display.getInt(display_fps));
      }
      // juggerdot blink
      final String display_juggerdotblink =
          resources.getString(R.string.settings_display_juggerdotblink_key);
      if (display.has(display_juggerdotblink)) {
        editor.putInt(display_juggerdotblink, display.getInt(display_juggerdotblink));
      }
      // show hud
      final String display_showhud = resources.getString(R.string.settings_display_showhud_key);
      if (display.has(display_showhud)) {
        editor.putBoolean(display_showhud, display.getBoolean(display_showhud));
      }
      // hud size
      final String display_hudsize = resources.getString(R.string.settings_display_hudsize_key);
      if (display.has(display_hudsize)) {
        editor.putInt(display_hudsize, display.getInt(display_hudsize));
      }
      // hud offset
      final String display_hudoffset =
          resources.getString(R.string.settings_display_hudoffset_key);
      if (display.has(display_hudoffset)) {
        editor.putInt(display_hudoffset, display.getInt(display_hudoffset));
