@Override
protected void onActivityResult(int requestCode, int resultCode, Intent data) {
  switch (requestCode) {
    case REQUEST_CAPTURE:
      {
        if (resultCode == RESULT_OK) {
          requestRecognize(data);
        } else if (resultCode == RESULT_CANCELED) {
          // Maintain the illusion that Capture is the main activity
          finish();
        } else {
          Toast.makeText(this, R.string.capture_failed, 5).show();
          Log.e(TAG, "REQUEST_CAPTURE received unexpected resultCode (" + resultCode + ")");
          requestCapture();
        }
        break;
      }
    case REQUEST_RECOGNIZE:
      {
        if (resultCode == RESULT_OK) {
          handleCompleted(data);
        } else if (resultCode == RESULT_CANCELED) {
          Toast.makeText(this, R.string.recognize_canceled, 3).show();
          Log.i(TAG, "REQUEST_RECOGNIZED received RESULT_CANCELED");
          requestCapture();
        } else {
          Toast.makeText(this, R.string.recognize_failed, 5).show();
          Log.e(TAG, "REQUEST_RECOGNIZE received unexpected resultCode (" + resultCode + ")");
          requestCapture();
        }
        break;
      }
    case REQUEST_REVIEW:
      {
        if (resultCode == RESULT_OK) {
          Toast.makeText(this, R.string.contact_added, 3).show();
        }
        requestCapture();
        break;
      }
    default:
      {
        Log.i(TAG, "Received unknown activity request code (" + requestCode + ")");
        super.onActivityResult(requestCode, resultCode, data);
      }
  }
}

@Override
protected void onPostExecute(Boolean success) {
  indicator.dismiss();

  if (success) {
    if (isMode(MODE_CREATE)) {
      Toast.makeText(CreateActivity.this, R.string.msg_create_success, Toast.LENGTH_SHORT)
          .show();

      Intent intent = new Intent(CreateActivity.this, DetailActivity.class);
      intent.putExtra(DetailActivity.EXTRA_DATA, new_use_case);
      startActivity(intent);
    } else if (isMode(MODE_EDIT)) {
      Toast.makeText(CreateActivity.this, R.string.msg_create_success, Toast.LENGTH_SHORT)
          .show();

      // TODO  UseCase  .
      Intent intent = new Intent(activity, MainActivity.class);
      intent.putExtra(MainActivity.EXTRA_REFRESH_LISTS, true);
      startActivity(intent);

      activity.finish();
      return;
    } else {
      throw new IllegalStateException("Undefined mode detected");
    }

    finish();
  } else {
    Toast.makeText(CreateActivity.this, msg_error, Toast.LENGTH_SHORT).show();
  }
}

@Override
public boolean onOptionsItemSelected(MenuItem item) {
  switch (item.getItemId()) {
    case R.id.reset:
      resetRibbon();
      setupButtons();
      refreshButtons();
      return true;
    case R.id.rearrange:
      if (arrayNum < 10) {
        ArrayList<String> aTargets = new ArrayList<String>();
        for (int i = 0; i < mShortTargets.size(); i++) {
          if (mShortTargets.get(i).equals("**null**")) {
            aTargets.add(NavBarHelpers.getProperSummary(mContext, mLongTargets.get(i)));
          } else {
            aTargets.add(NavBarHelpers.getProperSummary(mContext, mShortTargets.get(i)));
          }
        }
        ArrangeRibbonFragment fragment = new ArrangeRibbonFragment();
        fragment.setResources(
            mContext, mContentRes, aTargets, mShortTargets, mLongTargets, mCustomIcons, arrayNum);
        fragment.show(getFragmentManager(), "rearrange");
      } else {
        showHideAppsDialog();
      }
      return true;
    case R.id.ribbon_toggles:
      if (arrayNum == 2 || arrayNum == 4) {
        allTogglesStrings.clear();
        for (int i = 0; i < allToggles.size(); i++) {
          allTogglesStrings.add(lookupToggle(allToggles.get(i)));
        }
        ArrayList<String> mToggles =
            Settings.System.getArrayList(
                mContentRes, Settings.System.SWIPE_RIBBON_TOGGLES[ribbonNumber]);
        ArrangeRibbonTogglesFragment fragment = new ArrangeRibbonTogglesFragment();
        fragment.setResources(
