public static Toast makeText(Context context, CharSequence s, int duration) {
  if (VERSION.SDK_INT >= VERSION_CODES.ICE_CREAM_SANDWICH) {
    return Toast.makeText(context, s, duration);
  }
  IcsToast toast = new IcsToast(context);
  toast.setDuration(duration);
  TextView view = new TextView(context);
  view.setText(s);
  // Original AOSP using reference on @android:color/bright_foreground_dark
  // bright_foreground_dark - reference on @android:color/background_light
  // background_light - 0xffffffff
  view.setTextColor(0xffffffff);
  view.setGravity(Gravity.CENTER);
  view.setBackgroundResource(R.drawable.abs__toast_frame);
  toast.setView(view);
  return toast;
}

private void stop_services() {
  try {
    if (location_manager != null) {
      location_manager.removeUpdates(location_listener);
      location_manager = null;
    }

    if (wifi_manager != null) {
      if (wifi_manager.isWifiEnabled() != previous_wifi_state)
        wifi_manager.setWifiEnabled(previous_wifi_state);
      unregisterReceiver(wifiEvent);
      wifi_manager = null;
    }

    if (database != null) {
      if (database.isOpen()) database.close();
      database = null;
    }

    started = false;
    Toast.makeText(this, R.string.SERVICE_STOPPED, Toast.LENGTH_SHORT).show();
    NotificationManager nm = (NotificationManager) getSystemService(Context.NOTIFICATION_SERVICE);
    nm.cancel(NOTIFICATION_ELEMENT);
  } catch (Exception e) {
    notify_error(e);
  }
}

private void startViewAction(Intent intent) {
  Boolean slideshow = intent.getBooleanExtra(EXTRA_SLIDESHOW, false);
  if (slideshow) {
    getActionBar().hide();
    DataManager manager = getDataManager();
    Path path = manager.findPathByUri(intent.getData(), intent.getType());
    if (path == null || manager.getMediaObject(path) instanceof MediaItem) {
      path = Path.fromString(manager.getTopSetPath(DataManager.INCLUDE_IMAGE));
    }
    Bundle data = new Bundle();
    data.putString(SlideshowPage.KEY_SET_PATH, path.toString());
    data.putBoolean(SlideshowPage.KEY_RANDOM_ORDER, true);
    data.putBoolean(SlideshowPage.KEY_REPEAT, true);
    if (intent.getBooleanExtra(EXTRA_DREAM, false)) {
      data.putBoolean(SlideshowPage.KEY_DREAM, true);
    }
    getStateManager().startState(SlideshowPage.class, data);
  } else {
    Bundle data = new Bundle();
    DataManager dm = getDataManager();
    Uri uri = intent.getData();
    String contentType = getContentType(intent);
    if (contentType == null) {
      Toast.makeText(this, R.string.no_such_item, Toast.LENGTH_LONG).show();
      finish();
      return;
    }
    if (uri == null) {
      int typeBits = GalleryUtils.determineTypeBits(this, intent);
      data.putInt(KEY_TYPE_BITS, typeBits);
      data.putString(AlbumSetPage.KEY_MEDIA_PATH, getDataManager().getTopSetPath(typeBits));
      getStateManager().startState(AlbumSetPage.class, data);
    } else if (contentType.startsWith(ContentResolver.CURSOR_DIR_BASE_TYPE)) {
      int mediaType = intent.getIntExtra(KEY_MEDIA_TYPES, 0);
      if (mediaType != 0) {
        uri =
            uri.buildUpon()
                .appendQueryParameter(KEY_MEDIA_TYPES, String.valueOf(mediaType))
                .build();
      }
      Path setPath = dm.findPathByUri(uri, null);
      MediaSet mediaSet = null;
      if (setPath != null) {
        mediaSet = (MediaSet) dm.getMediaObject(setPath);
      }
      if (mediaSet != null) {
        if (mediaSet.isLeafAlbum()) {
          data.putString(AlbumPage.KEY_MEDIA_PATH, setPath.toString());
          data.putString(
              AlbumPage.KEY_PARENT_MEDIA_PATH, dm.getTopSetPath(DataManager.INCLUDE_ALL));
          getStateManager().startState(AlbumPage.class, data);
        } else {
          data.putString(AlbumSetPage.KEY_MEDIA_PATH, setPath.toString());
          getStateManager().startState(AlbumSetPage.class, data);
        }
      } else {
        startDefaultPage();
      }
    } else {
      Path itemPath = dm.findPathByUri(uri, intent.getType());
      Path albumPath = dm.getDefaultSetOf(itemPath);
      // TODO: Make this parameter public so other activities can reference it.
      boolean singleItemOnly = intent.getBooleanExtra("SingleItemOnly", false);
      if (!singleItemOnly && (albumPath != null)) {
        data.putString(PhotoPage.KEY_MEDIA_SET_PATH, albumPath.toString());
      }
      data.putString(PhotoPage.KEY_MEDIA_ITEM_PATH, itemPath.toString());
      if (intent.getBooleanExtra(PhotoPage.KEY_TREAT_BACK_AS_UP, false)) {
        data.putBoolean(PhotoPage.KEY_TREAT_BACK_AS_UP, true);
      }

      // Displays the filename as title, reading the filename from the interface:
      // {@link android.provider.OpenableColumns#DISPLAY_NAME}.
      AsyncQueryHandler queryHandler =
          new AsyncQueryHandler(getContentResolver()) {
            @Override
            protected void onQueryComplete(int token, Object cookie, Cursor cursor) {
              try {
                if ((cursor != null) && cursor.moveToFirst()) {
                  String displayName = cursor.getString(0);

                  // Just show empty title if other apps don't set DISPLAY_NAME
                  setTitle((displayName == null) ? "" : displayName);
                }
              } finally {
                Utils.closeSilently(cursor);
              }
            }
          };
      queryHandler.startQuery(
          0, null, uri, new String[] {OpenableColumns.DISPLAY_NAME}, null, null, null);

      getStateManager().startState(PhotoPage.class, data);
    }
  }
}

