private void searchModeLoadFieldLocation(String address, final int fieldId) {
  if (Geocoder.isPresent()) {
    try {
      List<Address> addresses = geocoder.getFromLocationName(address, 1);
      if (addresses != null && !addresses.isEmpty()) {
        Address addr = addresses.get(0);
        double lat = addr.getLatitude();
        double lng = addr.getLongitude();
        if (fieldId == FIELD_DEPARTURE) {
          departureLocation = new LatLng(lat, lng);
        }
        if (fieldId == FIELD_ARRIVAL) {
          arrivalLocation = new LatLng(lat, lng);
        }

        // This function is called for departure and arrival fields
        // Once we have the 2 location, the stations and route loading can continue
        if (departureLocation != null && arrivalLocation != null) {
          // search the contract for the locations
          Contract departureContract =
              Util.getContractForLocation(departureLocation, MainActivity.this);
          Contract arrivalContract =
              Util.getContractForLocation(arrivalLocation, MainActivity.this);

          if (departureContract != null && arrivalContract != null) {
            if (departureContract.getName().equals(arrivalContract.getName())) {
              if (currentContract != null
                  && departureContract.getName().equals(currentContract.getName())) {
                if (searchModeLoadStationsAndRoute()) {
                  searchModeDisplayStationsAndRoute();
                }
              } else {
                currentContract = departureContract;
                waitLoadStationForLauchSearch = true;
                loadStationsForContract(false, currentContract);
              }
            } else {
              searchButton.setEnabled(true);
              setProgressBarIndeterminateVisibility(false);
              Toast.makeText(
                      MainActivity.this, R.string.serach_not_on_same_contract, Toast.LENGTH_LONG)
                  .show();
            }
          } else {
            searchButton.setEnabled(true);
            setProgressBarIndeterminateVisibility(false);
            Toast.makeText(MainActivity.this, R.string.unsupported_contract, Toast.LENGTH_LONG)
                .show();
          }
        }
      } else {
        searchButton.setEnabled(true);
        setProgressBarIndeterminateVisibility(false);
      }
    } catch (IOException e) {
      Log.e("Could not retrieve user address", e);
      searchModeLoadFieldLocationWithGoogleWebservices(address, fieldId);
    }
  } else {
    Log.i("Geocoder not present, falling back to Google webservices");
    searchModeLoadFieldLocationWithGoogleWebservices(address, fieldId);
  }
}

/** Internal helper method to show the cheat sheet toast. */
private static boolean showCheatSheet(View view, CharSequence text) {
  if (TextUtils.isEmpty(text)) {
    return false;
  }

  final int[] screenPos = new int[2]; // origin is device display
  final Rect displayFrame = new Rect(); // includes decorations (e.g. status bar)
  view.getLocationOnScreen(screenPos);
  view.getWindowVisibleDisplayFrame(displayFrame);

  final Context context = view.getContext();
  final int viewWidth = view.getWidth();
  final int viewHeight = view.getHeight();
  final int viewCenterX = screenPos[0] + viewWidth / 2;
  final int screenWidth = context.getResources().getDisplayMetrics().widthPixels;
  final int estimatedToastHeight =
      (int) (ESTIMATED_TOAST_HEIGHT_DIPS * context.getResources().getDisplayMetrics().density);

  Toast cheatSheet = Toast.makeText(context, text, Toast.LENGTH_SHORT);
  boolean showBelow = screenPos[1] < estimatedToastHeight;
  if (showBelow) {
    // Show below
    // Offsets are after decorations (e.g. status bar) are factored in
    cheatSheet.setGravity(
        Gravity.TOP | Gravity.CENTER_HORIZONTAL,
        viewCenterX - screenWidth / 2,
        screenPos[1] - displayFrame.top + viewHeight);
  } else {
    // Show above
    // Offsets are after decorations (e.g. status bar) are factored in
    // NOTE: We can't use Gravity.BOTTOM because when the keyboard is up
    // its height isn't factored in.
    cheatSheet.setGravity(
        Gravity.TOP | Gravity.CENTER_HORIZONTAL,
        viewCenterX - screenWidth / 2,
        screenPos[1] - displayFrame.top - estimatedToastHeight);
  }

  cheatSheet.show();
  return true;
}

