/**
 * Remove a bookmark from the database. If the url is a visited site, it will remain in the
 * database, but only as a history item, and not as a bookmarked site.
 *
 * @param context Context of the calling Activity. This is used to make Toast confirming that the
 *     bookmark has been removed and to lookup the correct content uri. It must not be null.
 * @param cr The ContentResolver being used to remove the bookmark.
 * @param url URL of the website to be removed.
 */
/* package */ static void removeFromBookmarks(
    Context context, ContentResolver cr, String url, String title) {
  Cursor cursor = null;
  try {
    Uri uri = BookmarkUtils.getBookmarksUri(context);
    cursor =
        cr.query(
            uri,
            new String[] {BrowserContract.Bookmarks._ID},
            BrowserContract.Bookmarks.URL
                + " = ? AND "
                + BrowserContract.Bookmarks.TITLE
                + " = ?",
            new String[] {url, title},
            null);

    if (!cursor.moveToFirst()) {
      return;
    }

    // Remove from bookmarks
    WebIconDatabase.getInstance().releaseIconForPageUrl(url);
    uri = ContentUris.withAppendedId(BrowserContract.Bookmarks.CONTENT_URI, cursor.getLong(0));
    cr.delete(uri, null, null);
    if (context != null) {
      Toast.makeText(context, R.string.removed_from_bookmarks, Toast.LENGTH_LONG).show();
    }
  } catch (IllegalStateException e) {
    Log.e(LOGTAG, "removeFromBookmarks", e);
  } finally {
    if (cursor != null) cursor.close();
  }
}

