/** Saves the current render to the gallery */
private void onMenuSave() {
  File saveDir = ((RefractApplication) getApplication()).getSaveDirectory();
  File saveFile = new File(saveDir, new Date().getTime() + ".png");
  Bitmap bitmap = rendererView.getBitmap();

  try {
    OutputStream stream = new FileOutputStream(saveFile);
    bitmap.compress(CompressFormat.PNG, 80, stream);
    stream.close();

    // Tell the media scanner about the new file
    Uri contentUri = Uri.fromFile(saveFile);
    Intent mediaScanIntent = new Intent(Intent.ACTION_MEDIA_SCANNER_SCAN_FILE);
    mediaScanIntent.setData(contentUri);
    sendBroadcast(mediaScanIntent);

    Toast.makeText(this, R.string.str_imagesaved, Toast.LENGTH_SHORT).show();
  } catch (Exception e) {
    Toast.makeText(this, R.string.err_unabletosave, Toast.LENGTH_SHORT).show();
  }
}

@Override
public boolean onKeyDown(final int keyCode, final KeyEvent event) {
  if (
  // XXX TODO - when we go to android 2.0, uncomment this
  // android.os.Build.VERSION.SDK_INT < android.os.Build.VERSION_CODES.ECLAIR &&
  keyCode == KeyEvent.KEYCODE_BACK && event.getRepeatCount() == 0) {
    // Take care of calling this method on earlier versions of
    // the platform where it doesn't exist.
    onBackPressed();
    return true;
  }
  switch (keyCode) {
    case KeyEvent.KEYCODE_VOLUME_UP:
      {
        if (K9.useVolumeKeysForNavigationEnabled()) {
          onNext();
          return true;
        }
      }
    case KeyEvent.KEYCODE_VOLUME_DOWN:
      {
        if (K9.useVolumeKeysForNavigationEnabled()) {
          onPrevious();
          return true;
        }
      }
    case KeyEvent.KEYCODE_SHIFT_LEFT:
    case KeyEvent.KEYCODE_SHIFT_RIGHT:
      {
        /*
         * Selecting text started via shift key. Disable scrolling as
         * this causes problems when selecting text.
         */
        mTopView.setScrolling(false);
        break;
      }
    case KeyEvent.KEYCODE_DEL:
      {
        onDelete();
        return true;
      }
    case KeyEvent.KEYCODE_D:
      {
        onDelete();
        return true;
      }
    case KeyEvent.KEYCODE_F:
      {
        onForward();
        return true;
      }
    case KeyEvent.KEYCODE_A:
      {
        onReplyAll();
        return true;
      }
    case KeyEvent.KEYCODE_R:
      {
        onReply();
        return true;
      }
    case KeyEvent.KEYCODE_G:
      {
        onFlag();
        return true;
      }
    case KeyEvent.KEYCODE_M:
      {
        onMove();
        return true;
      }
    case KeyEvent.KEYCODE_S:
      {
        onRefile(mAccount.getSpamFolderName());
        return true;
      }
    case KeyEvent.KEYCODE_V:
      {
        onRefile(mAccount.getArchiveFolderName());
        return true;
      }
    case KeyEvent.KEYCODE_Y:
      {
        onCopy();
        return true;
      }
    case KeyEvent.KEYCODE_J:
    case KeyEvent.KEYCODE_P:
      {
        onPrevious();
        return true;
      }
    case KeyEvent.KEYCODE_N:
    case KeyEvent.KEYCODE_K:
      {
        onNext();
        return true;
      }
    case KeyEvent.KEYCODE_Z:
      {
        mHandler.post(
            new Runnable() {
              public void run() {
                mMessageView.zoom(event);
              }
            });
        return true;
      }
    case KeyEvent.KEYCODE_H:
      {
        Toast toast = Toast.makeText(this, R.string.message_help_key, Toast.LENGTH_LONG);
        toast.show();
        return true;
      }
  }
  return super.onKeyDown(keyCode, event);
}

@Override
public void run() {
  // TODO Auto-generated method stub
  PushDownloadInfo info = null;

  if (mBundle != null) {
    info = new PushDownloadInfo();
    info.setDownloadType(mBundle.getInt(PushUtils.PUSH_DETAIL_TYPE));
    info.setCompletesize(0);
    info.setFilename(mBundle.getString(PushUtils.PUSH_DETAIL_PACKAGE_NAME) + ".apk");
    info.setFilesize(mBundle.getInt(PushUtils.PUSH_DETAIL_SIZE));
    mDownloadId = mBundle.getInt(PushUtils.PUSH_DETAIL_ID);
    info.setId(mDownloadId);
    info.setTitle(mBundle.getString(PushUtils.PUSH_DETAIL_TITLE));
    info.setUrl(mBundle.getString(PushUtils.PUSH_DETAIL_URL));

    info.setApkIconBuffer(
        PushDownloadInfo.bitmapToBytes(
            (Bitmap) mBundle.getParcelable(PushUtils.PUSH_DETAIL_ICON)));
  } else {
    // 
    PushDownLoadDBHelper dbHelper = PushDownLoadDBHelper.getInstances();
    try {
      info = dbHelper.get(mDownloadId);
    } catch (Exception e) {
      e.printStackTrace();
    }
  }
  if (info == null) {
    info = new PushDownloadInfo();
  }
  int type = info.getDownloadType();
  String title = info.getTitle();
  Bitmap icon = PushDownloadInfo.getApkIcon(info.getApkIconBuffer());
  String url = info.getUrl();
  String name = info.getFilename();
  int id = info.getId();
  mDownloadId = id;
  int size = info.getFilesize();
  int completeSize = info.getCompletesize();

  int progress = size > 0 ? Math.min((int) ((completeSize / (float) size) * 100), 100) : 0;
  final boolean isSilent = (type == PushUtils.PUSH_DETAIL_SILENT_DOWNLOAD_TYPE);
  if (!isSilent && mNotification == null && mDownloadId > 0) {
    mNotification =
        new Notification(R.drawable.joy_push_statusbar_icon, title, System.currentTimeMillis());
    mNotification.contentView =
        new RemoteViews(mContext.getPackageName(), R.layout.push_notificaticon);
    mNotification.contentView.setViewVisibility(R.id.push_icon, View.VISIBLE);
    mNotification.contentView.setViewVisibility(R.id.push_download_progressBar, View.VISIBLE);
    mNotification.contentView.setViewVisibility(R.id.push_text, View.VISIBLE);
    mNotification.contentView.setViewVisibility(R.id.push_text1, View.GONE);
    mNotification.contentView.setTextViewText(R.id.push_text, title);
    mNotification.contentView.setImageViewBitmap(R.id.push_icon, icon);
    mNotification.contentView.setProgressBar(
        R.id.push_download_progressBar, 100, progress, false);
    mNotification.contentIntent = null;
    mNotification.flags |= Notification.FLAG_NO_CLEAR;
    mNotificationManager.notify(mDownloadId, mNotification);
  }

  if (DEBUG) Log.e(TAG, "start download APK");
  if (size > 0) {
    // download apk
    final PushDownloadInfo dInfo = info;
    PushCallBack callback =
        new PushCallBack() {

          @Override
          public void downloadUpdate() {
            // TODO Auto-generated method stub
            if (!isSilent && mNotification != null && mDownloadId > 0) {
              int progress =
                  Math.min(
                      (int) ((dInfo.getCompletesize() / (float) dInfo.getFilesize()) * 100),
                      100);
              if (DEBUG) Log.e(TAG, "download apk progress = " + progress);
              mNotification.contentView.setProgressBar(
                  R.id.push_download_progressBar, 100, progress, false);
              mNotificationManager.notify(mDownloadId, mNotification);
            }
          }

          @Override
          public void downloadSucceed() {
            // TODO Auto-generated method stub
            success = true;
          }

          @Override
          public void downloadFailed() {
            // TODO Auto-generated method stub
            success = false;
          }
        };

    try {
      if (Util.hasSdcard()) {
        PushDownLoadTask downloader =
            PushDownloadManager.getInstances().newDownLoadTask(null, info, callback, false);
        downloader.run();
      } else {
        if (!isSilent) {
          CharSequence insertSDcard = mContext.getResources().getText(R.string.insert_sd_card);
          Toast.makeText(mContext, insertSDcard, Toast.LENGTH_SHORT).show();
        }
      }
    } catch (Exception e) {
      e.printStackTrace();
    }
    if (success) {
      Util.installAPK(Constants.DOWNLOAD_APK_DIR, info.getLocalname(), isSilent);
      if (!isSilent) {
        // mNotificationManager.cancel(id);
      }
    } else {
      Log.e(TAG, "download apk failed");
      if (!isSilent) {

        // mNotificationManager.cancel(id);
      } else {
      }
    }
  }
  if (!isSilent && mNotification != null && mDownloadId > 0)
    mNotificationManager.cancel(mDownloadId);

  if (DEBUG) Log.e(TAG, "Download APK success = " + success);
  release();
}

