  CharacterActivity activity = (CharacterActivity) this.getActivity();

  String name =
      ((EditText) this.getView().findViewById(R.id.create_item_name)).getText().toString();
  String description =
      ((EditText) this.getView().findViewById(R.id.create_item_description)).getText().toString();

  if (name.trim().length() == 0) {
    isError = true;
  }

  float weight = 0.0f;
  int amount = 1;
  int value = 0;
  try {
    input =
        ((EditText) this.getView().findViewById(R.id.create_item_weight)).getText().toString();
    weight = Float.parseFloat(input);
  } catch (NumberFormatException e) {
    if (input.length() > 0) {
      Toast.makeText(this.getActivity(), R.string.item_create_error_weight, Toast.LENGTH_SHORT)
          .show();
      isError = true;
    }
  }
  try {
    input =
        ((EditText) this.getView().findViewById(R.id.create_item_amount)).getText().toString();
    amount = Integer.parseInt(input);
  } catch (NumberFormatException e) {
    if (input.length() > 0) {
      Toast.makeText(this.getActivity(), R.string.item_create_error_amount, Toast.LENGTH_SHORT)
          .show();
      isError = true;
    }
  }
  try {
    input = ((EditText) this.getView().findViewById(R.id.create_item_value)).getText().toString();
    value = Integer.parseInt(input);
  } catch (NumberFormatException e) {
    if (input.length() > 0) {
      Toast.makeText(this.getActivity(), R.string.item_create_error_value, Toast.LENGTH_SHORT)
          .show();
      isError = true;
    }
  }
  ItemEffects effect =
      ItemEffects.getEffect(
          ((Spinner) this.getView().findViewById(R.id.create_item_effecttype))
              .getSelectedItemPosition());
  String effectValueString =
      ((EditText) this.getView().findViewById(R.id.create_item_effectvalue)).getText().toString();

  int effectValue = 0;
  if (effectValueString.length() > 0) effectValue = Integer.parseInt(effectValueString);

  ItemSlots slot =
      ItemSlots.getItemSlot(
          ((Spinner) this.getView().findViewById(R.id.create_item_slot))
              .getSelectedItemPosition());

  Item i = null;

  switch (slot) {
    case WEAPON:
      try {
        Dice damage =
            new Dice(
                ((EditText) this.getView().findViewById(R.id.create_weapon_damage))
                    .getText()
                    .toString());
        int multi =
            Integer.parseInt(
                ((EditText) this.getView().findViewById(R.id.create_weapon_critmulti))
                    .getText()
                    .toString());
        int rangeindex =
            ((Spinner) this.getView().findViewById(R.id.create_weapon_critrange))
                    .getSelectedItemPosition()
                + 1;
        PfHandedness hand =
            PfHandedness.getHandedness(
                ((Spinner) this.getView().findViewById(R.id.create_weapon_handedness))
                    .getSelectedItemPosition());
        i = new Weapon(name, damage, multi, rangeindex, hand);
        ((Weapon) i)
            .setDamageType(
                ((EditText) this.getView().findViewById(R.id.create_weapon_damagetype))
                    .getText()
                    .toString());
      } catch (RuntimeException e) {
        if (e instanceof NumberFormatException) {
          Toast.makeText(
                  this.getActivity(), R.string.item_create_error_multiplier, Toast.LENGTH_SHORT)
              .show();
        } else {
          Toast.makeText(
                  this.getActivity(), R.string.item_create_error_damage, Toast.LENGTH_SHORT)
              .show();
        }
        isError = true;
      }
      break;
    case NOT_EQUIPABLE:
      i = new Item(name);
      break;
    default:
      int ac = 0, maxdex = 99, penalty = 0;

      try {
        input =
            ((EditText) this.getView().findViewById(R.id.create_armor_ac)).getText().toString();
        ac = Integer.parseInt(input);
      } catch (NumberFormatException e) {
        if (input.length() > 0) {
          Toast.makeText(this.getActivity(), R.string.item_create_error_ac, Toast.LENGTH_SHORT)
              .show();
          isError = true;
        }
      }
      try {
        input =
            ((EditText) this.getView().findViewById(R.id.create_armor_maxdex))
                .getText()
                .toString();
        maxdex = Integer.parseInt(input);
      } catch (NumberFormatException e) {
        if (input.length() > 0) {
          Toast.makeText(
                  this.getActivity(), R.string.item_create_error_maxdex, Toast.LENGTH_SHORT)
              .show();
          isError = true;
        }
      }
      try {
        input =
            ((EditText) this.getView().findViewById(R.id.create_armor_penalty))
                .getText()
                .toString();
        penalty = Integer.parseInt(input);
      } catch (NumberFormatException e) {
        if (input.length() > 0) {
          Toast.makeText(
                  this.getActivity(), R.string.item_create_error_penalty, Toast.LENGTH_SHORT)
              .show();
          isError = true;
        }
      }

      i = new Wearable(name, ac, maxdex, penalty);
      break;
  }

  if (i != null) {
    i.setDescription(description);
    i.setStackSize(amount);
    i.setValue(value);
    i.setWeight(weight);

    if (effect == ItemEffects.AB_AND_DAMAGE) {
      i.addEffect(ItemEffects.ATTACK_BONUS, effectValue);
      i.addEffect(ItemEffects.DAMAGE_BONUS, effectValue);
    } else {
      i.addEffect(effect, effectValue);
    }
    i.setSlot(slot);
  } else {
    isError = true;
  }

  if (!isError) {
    activity.addItem(i);
    this.dismiss();
  } else {
    Toast.makeText(this.getActivity(), R.string.item_create_error, Toast.LENGTH_SHORT).show();
  }
}

@Override
public boolean onPreferenceTreeClick(PreferenceScreen preferenceScreen, Preference preference) {
  if (preference.getKey().equals(KEY_FIRMWARE_VERSION)) {
    System.arraycopy(mHits, 1, mHits, 0, mHits.length - 1);
    mHits[mHits.length - 1] = SystemClock.uptimeMillis();
    if (mHits[0] >= (SystemClock.uptimeMillis() - 500)) {
      Intent intent = new Intent(Intent.ACTION_MAIN);
      intent.setClassName("android", com.android.internal.app.PlatLogoActivity.class.getName());
      try {
        startActivity(intent);
      } catch (Exception e) {
        Log.e(LOG_TAG, "Unable to start activity " + intent.toString());
      }
    }
  } else if (preference.getKey().equals(KEY_BUILD_NUMBER)) {
    if (mDevHitCountdown > 0) {
      mDevHitCountdown--;
      if (mDevHitCountdown == 0) {
        getActivity()
            .getSharedPreferences(DevelopmentSettings.PREF_FILE, Context.MODE_PRIVATE)
            .edit()
            .putBoolean(DevelopmentSettings.PREF_SHOW, true)
            .apply();
        if (mDevHitToast != null) {
          mDevHitToast.cancel();
        }
        mDevHitToast = Toast.makeText(getActivity(), R.string.show_dev_on, Toast.LENGTH_LONG);
        mDevHitToast.show();
      } else if (mDevHitCountdown > 0 && mDevHitCountdown < (TAPS_TO_BE_A_DEVELOPER - 2)) {
        if (mDevHitToast != null) {
          mDevHitToast.cancel();
        }
        mDevHitToast =
            Toast.makeText(
                getActivity(),
                getResources()
                    .getQuantityString(
                        R.plurals.show_dev_countdown, mDevHitCountdown, mDevHitCountdown),
                Toast.LENGTH_SHORT);
        mDevHitToast.show();
      }
    } else if (mDevHitCountdown < 0) {
      if (mDevHitToast != null) {
        mDevHitToast.cancel();
      }
      mDevHitToast = Toast.makeText(getActivity(), R.string.show_dev_already, Toast.LENGTH_LONG);
      mDevHitToast.show();
    }
  }
  return super.onPreferenceTreeClick(preferenceScreen, preference);
}

