@Override
public boolean onOptionsItemSelected(MenuItem item) {
  // Handle item selection
  switch (item.getItemId()) {
    case R.id.do_sync:
      if (!ServerCredentials.check(getApplicationContext())) {
        Toast.makeText(
                getApplicationContext(),
                getResources().getString(R.string.sync_log_in_first),
                Toast.LENGTH_SHORT)
            .show();
        return true;
      }
      Log.d(TAG, "Preparing sync requests");
      new ZoteroAPITask(getBaseContext()).execute(APIRequest.update(Item.load(itemKey, db)));
      Toast.makeText(
              getApplicationContext(),
              getResources().getString(R.string.sync_started),
              Toast.LENGTH_SHORT)
          .show();
      return true;
    case R.id.do_new:
      Bundle b = new Bundle();
      b.putString("itemKey", itemKey);
      removeDialog(DIALOG_COLLECTION_LIST);
      this.b = b;
      showDialog(DIALOG_COLLECTION_LIST);
      return true;
    case R.id.do_prefs:
      startActivity(new Intent(this, SettingsActivity.class));
      return true;
    default:
      return super.onOptionsItemSelected(item);
  }
}

public void onCreate(Bundle savedInstanceState) {
  super.onCreate(savedInstanceState);

  SharedPreferences prefs3 =
      PreferenceManager.getDefaultSharedPreferences(getApplicationContext());
  SharedPreferences.Editor editor = prefs3.edit();
  editor.putInt("itemPosition", 4);
  editor.commit();

  getApplicationContext();
  SharedPreferences prefs =
      getApplicationContext().getSharedPreferences(Api.PREFS_NAME, Context.MODE_PRIVATE);
  getApplicationContext();
  final SharedPreferences prefs2 =
      getApplicationContext().getSharedPreferences(Api.PREF_PROFILE4, Context.MODE_PRIVATE);
  boolean toastenabled =
      getApplicationContext()
          .getSharedPreferences(Api.PREFS_NAME, 0)
          .getBoolean(Api.PREF_TASKERNOTIFY, false);
  final Editor editRules = prefs.edit();
  editRules.clear();
  for (Entry<String, ?> entry : prefs2.getAll().entrySet()) {
    Object rule = entry.getValue();
    String keys = entry.getKey();
    if (rule instanceof Boolean) editRules.putBoolean(keys, ((Boolean) rule).booleanValue());
    else if (rule instanceof Float) editRules.putFloat(keys, ((Float) rule).floatValue());
    else if (rule instanceof String) editRules.putString(keys, ((String) rule));
    else if (rule instanceof Long) editRules.putLong(keys, ((Long) rule).longValue());
    else if (rule instanceof Integer) editRules.putInt(keys, ((Integer) rule).intValue());
  }
  editRules.commit();
  Api.applications = null;
  toggleUserSettings(getApplicationContext());
  if (Api.isEnabled(getApplicationContext())) {
    Api.applyIptablesRules(getApplicationContext(), true);
    if (toastenabled) {
      Toast.makeText(getApplicationContext(), R.string.tasker_profile, Toast.LENGTH_LONG).show();
    }
  } else {
    Api.saveRules(getApplicationContext());
    Api.purgeIptables(getApplicationContext(), true);
    if (toastenabled) {
      Toast.makeText(getApplicationContext(), R.string.tasker_profile_disabled, Toast.LENGTH_LONG)
          .show();
    }
  }
  finish();
}

@Override
public void onDismiss(DialogInterface dialog) {
  if (mUnlockConfirmed) {
    mUnlockConfirmed = false;
    mError.setVisibility(View.VISIBLE);
    mKeyStore.unlock(mOldPassword.getText().toString());
    int error = mKeyStore.getLastError();
    if (error == KeyStore.NO_ERROR) {
      mRetriesRemaining = -1;
      Toast.makeText(CredentialStorage.this, R.string.credentials_enabled, Toast.LENGTH_SHORT)
          .show();
      // aha, now we are unlocked, switch to key guard.
      // we'll end up back in onResume to install
      ensureKeyGuard();
    } else if (error == KeyStore.UNINITIALIZED) {
      mRetriesRemaining = -1;
      Toast.makeText(CredentialStorage.this, R.string.credentials_erased, Toast.LENGTH_SHORT)
          .show();
      // we are reset, we can now set new password with key guard
      handleUnlockOrInstall();
    } else if (error >= KeyStore.WRONG_PASSWORD) {
      // we need to try again
      mRetriesRemaining = error - KeyStore.WRONG_PASSWORD + 1;
      handleUnlockOrInstall();
    }
    return;
  }
  finish();
}

