public static String[] checkValidNumber(
    Context context, TrustedContact newCont, String text, boolean checkText, boolean showError) {
  if (newCont != null && !newCont.getNumber().isEmpty()) {
    final String number = newCont.getNumber(0);

    if (number.length() > 0 && (!checkText || text.length() > 0)) {
      return new String[] {number, text};

    } else if (showError) {
      final AlertDialog.Builder builder = new AlertDialog.Builder(context);
      builder
          .setMessage(R.string.insufficent_information_provided)
          .setCancelable(true)
          .setPositiveButton(
              android.R.string.ok,
              new DialogInterface.OnClickListener() {
                public void onClick(final DialogInterface dialog, final int id) {}
              });
      final AlertDialog alert = builder.create();
      alert.show();
    }

  } else if (showError) {
    Toast.makeText(context, R.string.invalid_number_message, Toast.LENGTH_SHORT).show();
  }
  return null;
}

