public String manipulate(String uri) {
  String outUri = "";
  outUri = uri;
  // we check if the path is a regular expression
  if (regexp != null && replacement != null) {
    Pattern p = Pattern.compile(regexp, Pattern.CASE_INSENSITIVE);
    Matcher m = p.matcher(outUri);
    outUri = m.replaceAll(replacement);
  }
  if (log.isDebugEnabled())
    log.debug("RegUriManipulateService:: input uri :" + uri + " -- output uri : " + outUri);

  return outUri;
}

private String quoteIfWhitespace(String value) {
  if (value == null) return null;
  Matcher m = WHITESPACE.matcher(value);
  if (m.find()) {
    return "\"" + value + "\"";
  }
  return value;
}

@Override
public LogMessage parse(CharSequence text) {
  // TODO(gak): do this stuff in terms of CharSequence instead of String
  String string = text.toString();
  if (string.startsWith(CONTROL_PREFIX)) {
    ImmutableList<String> parts =
        ImmutableList.copyOf(
            CONTROL_TYPE_SPLITTER.split(string.substring(CONTROL_PREFIX.length())));
    Class<? extends CaliperControlLogMessage> messageType = typeMap.inverse().get(parts.get(0));
    return gson.fromJson(parts.get(1), messageType);
  } else {
    Matcher gcMatcher = GC_PATTERN.matcher(string);
    if (gcMatcher.matches()) {
      return new GcLogMessage(
          "Full".equals(gcMatcher.group(1))
              ? GcLogMessage.Type.FULL
              : GcLogMessage.Type.INCREMENTAL,
          ShortDuration.of(BigDecimal.valueOf(Double.parseDouble(gcMatcher.group(2))), SECONDS));
    }
    Matcher jitMatcher = JIT_PATTERN.matcher(string);
    if (jitMatcher.matches()) {
      return new HotspotLogMessage();
    }
    Matcher vmOptionMatcher = VM_OPTION_PATTERN.matcher(string);
    if (vmOptionMatcher.matches()) {
      return new VmOptionLogMessage(vmOptionMatcher.group(2), vmOptionMatcher.group(3));
    }
    return new GenericLogMessage();
  }
}

private static List<String> getBuildConfigurations(String solutionContent) {
  // A pattern to extract the build configurations from a visual studio solution
  String confExtractExp =
      "(\tGlobalSection\\(SolutionConfigurationPlatforms\\).*?^\tEndGlobalSection$)";
  Pattern confExtractPattern =
      Pattern.compile(confExtractExp, Pattern.MULTILINE + Pattern.DOTALL);
  List<String> buildConfigurations = new ArrayList<String>();
  // Extracts all the projects from the solution
  Matcher blockMatcher = confExtractPattern.matcher(solutionContent);
  if (blockMatcher.find()) {
    String buildConfigurationBlock = blockMatcher.group(1);
    String buildConfExtractExp = " = (.*)\\|";
    Pattern buildConfExtractPattern = Pattern.compile(buildConfExtractExp);
    Matcher buildConfMatcher = buildConfExtractPattern.matcher(buildConfigurationBlock);
    while (buildConfMatcher.find()) {
      String buildConfiguration = buildConfMatcher.group(1);
      buildConfigurations.add(buildConfiguration);
    }
  }
  return buildConfigurations;
}

protected static String increment(int count, String template) {
  Pattern p = Pattern.compile("\\$(\\d+)");
  Matcher m = p.matcher(template);
  while (m.find()) {
    int num = Integer.parseInt(m.group(1));
    int newNum = num + count;
    template = template.replaceAll("\\$" + num, "\\$" + newNum);
    // System.out.println(template);
  }
  return template;
}

