}

private String processHtml(final String source, StringBuilder errorMessages) {
  if (M_evilTags == null) init();

  // normalize all variants of the "<br>" HTML tag to be "<br />\n"
  // TODO call a method to do this in each process routine
  String Html = M_patternTagBr.matcher(source).replaceAll("<br />");

  // process text and tags
  StringBuilder buf = new StringBuilder();
  if (Html != null) {
    try {
      int start = 0;
      Matcher m = M_patternTag.matcher(Html);

      // if there are no tags, return as is
      if (!m.find()) return Html;
      m.reset(Html);

      // if there are tags, make sure they are safe
      while (m.find()) {
        // append text that isn't part of a tag
        if (m.start() > start) buf.append(Html.substring(start, m.start()));
        start = m.end();

        buf.append(checkTag(m.group(), errorMessages));
      }

      // tail
      if (Html.length() > start) buf.append(Html.substring((start)));
    } catch (Exception e) {
      M_log.warn("FormattedText.processEscapedHtml M_patternTag.matcher(Html):", e);
    }
  }
  return String.valueOf(buf.toString());
}

public static final List<String> getSplit(Pattern pattern, String line) {
  final Matcher m = pattern.matcher(line);
  if (m.find() == false) {
    return null;
  }
  final List<String> result = new ArrayList<String>();
  for (int i = 1; i <= m.groupCount(); i++) {
    result.add(m.group(i));
  }
  return result;
}

/**
 * Is used to search for building letters. If a building letter is found, it will be stored in the
 * result array and then errased from the INPUT string. <br>
 * After checking for the building letter the INPUT string is spiltted and each part is placed in
 * the corresponding array index.
 */
private void buildingLetter() {
  sub[0] = sub[0].replaceAll(SAL, "");
  sub[0] = sub[0].replaceAll(ETAGE, "");
  p1 = Pattern.compile("[a-lA-L]{1}");
  m1 = p1.matcher(sub[0]);
  if (m1.find()) { // CHECK 22
    result[2] = sub[0].substring(m1.start(), m1.end());
    sub[0] = sub[0].replaceFirst(result[2], "").trim();
  }
  sub = sub[0].trim().split(" ");
  if (sub.length == 1) { // CHECK 23
    result[1] = sub[0];
  } else if (sub.length == 2) { // CHECK 24
    result[1] = sub[0];
    result[3] = sub[1];
  }
}

private String formatNationalNumber(
    String number, String regionCode, PhoneNumberFormat numberFormat, String carrierCode) {
  PhoneMetadata metadata = getMetadataForRegion(regionCode);
  List<NumberFormat> intlNumberFormats = metadata.intlNumberFormats();
  // When the intlNumberFormats exists, we use that to format national number for the
  // INTERNATIONAL format instead of using the numberDesc.numberFormats.
  List<NumberFormat> availableFormats =
      (intlNumberFormats.size() == 0 || numberFormat == PhoneNumberFormat.NATIONAL)
          ? metadata.numberFormats()
          : metadata.intlNumberFormats();
  String formattedNationalNumber =
      formatAccordingToFormats(number, availableFormats, numberFormat, carrierCode);
  if (numberFormat == PhoneNumberFormat.RFC3966) {
    formattedNationalNumber = SEPARATOR_PATTERN.matcher(formattedNationalNumber).replaceAll("-");
  }
  return formattedNationalNumber;
}

