public void shouldReplaceLink() throws Exception {
  String tweet = "Panic Status Board: http://bit.ly/aTs0uZ";
  Pattern p =
      Pattern.compile(
          "\\b(https?|ftp|file)://[-a-zA-Z0-9+&@#/%?=~_|!:,.;]*[-a-zA-Z0-9+&@#/%=~_|]");
  Matcher m = p.matcher(tweet);
