/**
 * Given a String, returns a String identical to the provided String except that all regex special
 * characters will be escaped for use in regular expressions.
 *
 * <p>For example, this method will return "\(hi\)" on input "(hi)"
 *
 * @param escapeMe The string to escape
 * @return The escaped string
 */
public String escapeRegexSpecialChars(String escapeMe) {
  if (log.isDebugEnabled())
    log.debug("Entering escapeRegexSpecialChars for the String: " + escapeMe);

  // Returns a pattern which matches all special characters
  Pattern escaper = Pattern.compile("([\\(\\)\\.\\$\\^\\{\\}\\[\\]\\|\\*\\+\\?\\\\])");

  // Escape the special characters and return
  String result = escaper.matcher(escapeMe).replaceAll("\\\\$1");

  if (log.isDebugEnabled()) log.debug("The escaped String is: " + result);

  return result;
}

/**
 * Processes and validates HTML formatted text received from the web browser (from the WYSIWYG
 * editor). Validates that the user input follows the Sakai formatted text specification; can
 * disallow dangerous stuff such as &lt;SCRIPT&gt; JavaScript tags. Encodes the text according to
 * the formatted text specification, for the rest of the system to use.
 *
 * @param strFromBrowser The formatted text as sent from the web browser (from the WYSIWYG editor)
 * @param errorMessages User-readable error messages will be returned here.
 * @param checkForEvilTags If true, check for tags and attributes that shouldn't be in formatted
 *     text
 * @param replaceWhitespaceTags If true, clean up line breaks to be like "&lt;br /&gt;".
 * @return The validated processed HTML formatted text, ready for use by the system.
 */
public static String processFormattedText(
    final String strFromBrowser,
    StringBuilder errorMessages,
    boolean checkForEvilTags,
    boolean replaceWhitespaceTags) {
  String val = strFromBrowser;
  if (val == null || val.length() == 0) return val;

  if (replaceWhitespaceTags) {
    // normalize all variants of the "<br>" HTML tag to be "<br />\n"
    val = M_patternTagBr.matcher(val).replaceAll("<br />");

    // replace "<p>" with nothing. Replace "</p>" and "<p />" HTML tags with "<br />"
    // val = val.replaceAll("<p>", "");
    // val = val.replaceAll("</p>", "<br />\n");
    // val = val.replaceAll("<p />", "<br />\n");
  }

  if (checkForEvilTags) {
    val = processHtml(strFromBrowser, errorMessages);
  }

  // deal with hardcoded empty space character from Firefox 1.5
  if (val.equals("&nbsp;")) {
    val = "";
  }

  // close any open HTML tags (that the user may have accidentally left open)
  StringBuilder buf = new StringBuilder();
  trimFormattedText(val, Integer.MAX_VALUE, buf);
  val = buf.toString();

  // TODO: Fully parse and validate the formatted text against
  // the formatted text specification. Perhaps this could be
  // done by treating the text as an XML document and validating
  // the XML document against a Document-Type-Definition (DTD) for
  // formatted text. This would allow for validating the
  // attributes of allowed tags, for example.

  return val;
}

/**
 * Helper method to parse out the required fields in the query. Expects the query to look like:
 * obj.attr = value
 */
private void Parse() {
  Pattern p = Pattern.compile("((\\w*)\\.)?(\\w*)\\s(.*)\\s(.*)");
  Matcher m = p.matcher(query);
  OperatorFactory c = new OperatorFactory();
  boolean matchFound = m.find();
  if (matchFound) {
    objectName = m.group(2);
    objectAttr = m.group(3);
    operator = m.group(4);
    value = m.group(5);
  } else {
    // If match isn't found then the query is in the wrong form.
    assert (matchFound == true);
  }
}

private boolean createFormattingTemplate(NumberFormat format) {
  String numberPattern = format.getPattern();

  // The formatter doesn't format numbers when numberPattern contains "|", e.g.
  // (20|3)\d{4}. In those cases we quickly return.
  if (numberPattern.indexOf('|') != -1) {
    return false;
  }

  // Replace anything in the form of [..] with \d
  numberPattern = CHARACTER_CLASS_PATTERN.matcher(numberPattern).replaceAll("\\\\d");

  // Replace any standalone digit (not the one in d{}) with \d
  numberPattern = STANDALONE_DIGIT_PATTERN.matcher(numberPattern).replaceAll("\\\\d");
  formattingTemplate.setLength(0);
  String tempTemplate = getFormattingTemplate(numberPattern, format.getFormat());
  if (tempTemplate.length() > 0) {
    formattingTemplate.append(tempTemplate);
    return true;
  }
  return false;
}

private static String replaceAllLiteral(Pattern pattern, String value, String replace) {
  replace = replace.replaceAll("([$\\\\])", "\\\\$0");
  return pattern.matcher(value).replaceAll(replace);
}

@Override
public void pubsubEvent(IIdentity pubsubService, String node, String itemId, Object payload) {

  if (LOG.isDebugEnabled())
    LOG.debug(
        "pubsubEvent:pubsubService="
            + pubsubService
            + ",node="
            + node
            + ",itemId="
            + itemId
            + ",payload="
            + payload);

  try {
    if (itemId == null) {
      LOG.error("Remote context change event itemId can't be null");
      return;
    }

    // Check if itemId, i.e. ctxId, matches against filter
    final Matcher filterMatcher = this.patternFilter.matcher(itemId);
    if (!filterMatcher.matches()) {
      if (LOG.isDebugEnabled())
        LOG.debug("Ignoring remote context change event for ctxId " + itemId);
      return;
    }

    final CtxIdentifier ctxId = CtxIdentifierFactory.getInstance().fromString(itemId);
    final CtxChangeEvent ctxChangeEvent = new CtxChangeEvent(ctxId);
    final String topic = node;
    if (CtxChangeEventTopic.CREATED.equals(topic)) this.listener.onCreation(ctxChangeEvent);
    else if (CtxChangeEventTopic.UPDATED.equals(topic)) this.listener.onUpdate(ctxChangeEvent);
    else if (CtxChangeEventTopic.MODIFIED.equals(topic))
      this.listener.onModification(ctxChangeEvent);
    else if (CtxChangeEventTopic.REMOVED.equals(topic)) this.listener.onRemoval(ctxChangeEvent);
    else LOG.error("Unexpected remote context change event topic: '" + topic + "'");
  } catch (MalformedCtxIdentifierException mcie) {

    LOG.error(
        "Malformed context identifier in remote context change event: "
            + mcie.getLocalizedMessage(),
        mcie);
  }
}

