public CharSequence filter(CharSequence source) {
  StringBuilder output = new StringBuilder(source);
  final int sourceLength = source.length();

  for (int i = 0; i < sourceLength; i++) {
    char c = source.charAt(i);

    // Character requires Unicode, try to replace it
    if (!mStripNonDecodableOnly || !gsm.canEncode(c)) {
      String s = String.valueOf(c);

      // Try normalizing the character into Unicode NFKD form and
      // stripping out diacritic mark characters.
      s = Normalizer.normalize(s, Normalizer.Form.NFKD);
      s = diacritics.matcher(s).replaceAll("");

      // Special case characters that don't get stripped by the
      // above technique.
      s = s.replace("", "OE");
      s = s.replace("", "oe");
      s = s.replace("", "L");
      s = s.replace("", "l");
      s = s.replace("", "DJ");
      s = s.replace("", "dj");
      s = s.replace("", "A");
      s = s.replace("", "B");
      s = s.replace("", "E");
      s = s.replace("", "Z");
      s = s.replace("", "H");
      s = s.replace("", "I");
      s = s.replace("", "K");
      s = s.replace("", "M");
      s = s.replace("", "N");
      s = s.replace("", "O");
      s = s.replace("", "P");
      s = s.replace("", "T");
      s = s.replace("", "Y");
      s = s.replace("", "X");
      s = s.replace("", "A");
      s = s.replace("", "B");
      s = s.replace("", "");
      s = s.replace("", "");
      s = s.replace("", "E");
      s = s.replace("", "Z");
      s = s.replace("", "H");
      s = s.replace("", "");
      s = s.replace("", "I");
      s = s.replace("", "K");
      s = s.replace("", "");
      s = s.replace("", "M");
      s = s.replace("", "N");
      s = s.replace("", "");
      s = s.replace("", "O");
      s = s.replace("", "");
      s = s.replace("", "P");
      s = s.replace("", "");
      s = s.replace("", "T");
      s = s.replace("", "Y");
      s = s.replace("", "");
      s = s.replace("", "X");
      s = s.replace("", "");
      s = s.replace("", "");
      s = s.replace("", "");

      output.replace(i, i + 1, s);
    }
  }

  // Source is a spanned string, so copy the spans from it
  if (source instanceof Spanned) {
    SpannableString spannedoutput = new SpannableString(output);
    TextUtils.copySpansFrom((Spanned) source, 0, sourceLength, null, spannedoutput, 0);

    return spannedoutput;
  }

  // Source is a vanilla charsequence, so return output as-is
  return output.toString();
}

static Parameter toParameter(String param) throws IllegalArgumentException {
  Matcher m = PARAM.matcher(param);
  if (!m.matches()) {
    throw new IllegalArgumentException("Illegal parameter syntax: " + param);
  }

  String name = m.group(1);
  Class type = null;
  int min = 1;
  int max = 1;

  String grp = m.group(2);
  if ("".equals(grp)) {
    grp = null;
  }
  if (grp != null) {
    try {
      type = Class.forName(grp);
    } catch (ClassNotFoundException e) {
      // try prefixing with java.lang
      try {
        type = Class.forName("java.lang." + grp);
      } catch (ClassNotFoundException e1) {
        // try prefixing with jts.geom
        try {
          type = Class.forName("com.vividsolutions.jts.geom." + grp);
        } catch (ClassNotFoundException e2) {
          // throw back the original
          throw (IllegalArgumentException)
              new IllegalArgumentException("Unknown type: " + grp).initCause(e);
        }
      }
    }
  }

  // recognize some well known names
  if (type == null) {
    if ("geom".equals(name)) {
      type = Geometry.class;
    }
  }

  if (type == null) {
    type = Object.class;
  }

  grp = m.group(3);
  if (grp != null) {
    min = !"".equals(grp) ? Integer.parseInt(grp) : -1;
  }

  grp = m.group(4);
  if (grp != null) {
    max = !"".equals(grp) ? Integer.parseInt(grp) : -1;
  }

  return new org.geotools.data.Parameter(name, type, min, max);
}

