public void test_toMatchResult() {
  String testPattern = "(((abb)a)(bb))";
  String testString = "babbabbcccabbabbabbabbabb";
  Pattern pat = Pattern.compile(testPattern);
  Matcher mat = pat.matcher(testString);

  mat.region(1, 7);
  assertTrue("matcher should find pattern in given region", mat.matches());
  assertEquals("matched section should start from 1 position", 1, mat.toMatchResult().start());
  assertEquals(
      "matched section for 2 group should start from 1 position",
      1,
      mat.toMatchResult().start(2));
  assertEquals(
      "matched section for whole pattern should end on 7 position", 7, mat.toMatchResult().end());
  assertEquals(
      "matched section for 3 group should end at 4 position", 4, mat.toMatchResult().end(3));
  assertEquals("group not matched", "abbabb", mat.toMatchResult().group());
  assertEquals("3 group not matched", "abb", mat.toMatchResult().group(3));
  assertEquals(
      "Total number of groups does not matched with given pattern",
      4,
      mat.toMatchResult().groupCount());
}

private static Collection<String> getResourcesLinesFromDirectory(File directory, Pattern pattern)
    throws IOException {
  ArrayList<String> lines = Lists.newArrayList();
  File[] list = directory.listFiles();
  if (list != null) {
    for (final File file : list) {
      if (file.isDirectory()) {
        lines.addAll(getResourcesLinesFromDirectory(file, pattern));
      } else {
        final String fileName = file.getCanonicalPath();
        final boolean accept = pattern.matcher(fileName).matches();
        if (accept) {
          lines.addAll(Resources.readLines(file.toURI().toURL(), Charsets.UTF_8));
        }
      }
    }
  }
  return lines;
}

