protected boolean isExistsBodyTextInScript(String scriptText) {
  String pattern =
      ".*("
          + "\\$\\{"
          + BODY_VARIABLE_NAME
          + "\\}|"
          + "\\$\\{"
          + BODY_VARIABLE_NAME
          + "[^a-zA-Z_$].*\\}|"
          + "\\$\\{.*[^a-zA-Z_$]"
          + BODY_VARIABLE_NAME
          + "\\}|"
          + "\\$\\{.*[^a-zA-Z_$]"
