static InetAddress parse(BufferedReader bufferedreader) throws IOException, UnknownHostException {
  String line;
  while ((line = bufferedreader.readLine()) != null) {
    boolean gatewayLine = false;
    if (line.indexOf("default") == 0 || line.indexOf("0.0.0.0") >= 0) {
      // MacOSX
      if (line.indexOf("default") == 0) {
        gatewayLine = true;
      }
      // this is the line with the gateway IP, search for the first
      // good entry.
      Matcher m = IP_PATTERN.matcher(line);
      int start = 0;
      while (m.find(start)) {
        String tmp = m.group();
        // first entry
        if (start == 0 && tmp.equals("0.0.0.0")) {
          gatewayLine = true;
        } else if (!tmp.equals("0.0.0.0") && gatewayLine) {
          return InetAddress.getByName(tmp);
        }
        start = m.end() + 1;
      }
    }
  }
  return null;
}

public void testEmbeddedFlags() {
  String baseString = "(?i)((?s)a)";
  String testString = "A";
  Pattern pat = Pattern.compile(baseString);
  Matcher mat = pat.matcher(testString);
  assertTrue(mat.matches());

  baseString = "(?x)(?i)(?s)(?d)a";
  testString = "A";
  pat = Pattern.compile(baseString);
  mat = pat.matcher(testString);
  assertTrue(mat.matches());

  baseString = "(?x)(?i)(?s)(?d)a.";
  testString = "a\n";
  pat = Pattern.compile(baseString);
  mat = pat.matcher(testString);
  assertTrue(mat.matches());

  baseString = "abc(?x:(?i)(?s)(?d)a.)";
  testString = "abcA\n";
  pat = Pattern.compile(baseString);
  mat = pat.matcher(testString);
  assertTrue(mat.matches());

  baseString = "abc((?x)d)(?i)(?s)a";
  testString = "abcdA";
  pat = Pattern.compile(baseString);
  mat = pat.matcher(testString);
  assertTrue(mat.matches());
}

