@Override
public Object call(Properties bindings, Object[] args) {
  if (args.length == 3) {
    Object o1 = args[0];
    Object o2 = args[1];
    Object o3 = args[2];
    if (o1 != null && o2 != null && o3 != null && o3 instanceof String) {
      String str = (o1 instanceof String) ? (String) o1 : o1.toString();

      if (o2 instanceof String) {
        return str.replace((String) o2, (String) o3);
      } else if (o2 instanceof Pattern) {
        Pattern pattern = (Pattern) o2;
        return pattern.matcher(str).replaceAll((String) o3);
      }
    }
  }
  return new EvalError(
      ControlFunctionRegistry.getFunctionName(this)
          + " expects 3 strings, or 1 string, 1 regex, and 1 string");
}

/**
 * Clean up version parameters. Other builders use more fuzzy definitions of the version syntax.
 * This method cleans up such a version to match an OSGi version.
 */
static String cleanupVersion(String version) {
  StringBuffer result = new StringBuffer();
  Matcher m = FUZZY_VERSION.matcher(version);
  if (m.matches()) {
    String major = m.group(1);
    String minor = m.group(3);
    String micro = m.group(5);
    String qualifier = m.group(7);

    if (major != null) {
      result.append(major);
      if (minor != null) {
        result.append(".");
        result.append(minor);
        if (micro != null) {
          result.append(".");
          result.append(micro);
          if (qualifier != null) {
            result.append(".");
            cleanupModifier(result, qualifier);
          }
        } else if (qualifier != null) {
          result.append(".0.");
          cleanupModifier(result, qualifier);
        } else {
          result.append(".0");
        }
      } else if (qualifier != null) {
        result.append(".0.0.");
        cleanupModifier(result, qualifier);
      } else {
        result.append(".0.0");
      }
    }
  } else {
    result.append("0.0.0.");
    cleanupModifier(result, version);
  }
  return result.toString();
}

public static String encode(String s) {
  if (tst.matcher(s).find()) {
    s = amp.matcher(s).replaceAll("&amp;");
    s = lt.matcher(s).replaceAll("&lt;");
    s = gt.matcher(s).replaceAll("&gt;");
    s = squ.matcher(s).replaceAll("&apos;");
    s = dqu.matcher(s).replaceAll("&quot;");
  }
  return s;
}

/**
 * Copies in to out line by line, escaping each line for html characters and highlighting error
 * lines. Closes <code>in</code> when done.
 */
private static void sendLogAsHtml(BufferedReader in, HtmlWriter out) throws IOException {
  try {
    out.startTag("pre").nl();
    String line = in.readLine();
    while (line != null) {
      Matcher m = ERROR_PATTERN.matcher(line);
      boolean error = m.find();
      if (error) {
        out.startTag("span", "class=", "error");
      }
      out.text(line);
      if (error) {
        out.endTag("span");
      }
      out.nl(); // the readLine doesn't include the newline.
      line = in.readLine();
    }
    out.endTag("pre").nl();
  } finally {
    in.close();
  }
}

@Override
protected ColumnData createColumnData(ResultSet columnSet) throws SQLException {
  ColumnData columnData = super.createColumnData(columnSet);
  String type = columnData.type.toLowerCase();

  if ("number".equals(type)) {
    if (columnData.size == 22 && columnData.decimalDigits == 0) {
      columnData.size = 0;
    }

  } else if ("float".equals(type)) {
    if (columnData.size == 126 && columnData.decimalDigits == 0) {
      columnData.size = 0;
    }

  } else if ("urowid".equals(type)) {
    if (columnData.size == 4000) {
      columnData.size = 0;
    }

  } else if ("anydata".equals(type)) {
    columnData.size = 0;

  } else {
    Matcher yearToMonthMatcber = INTERVAL_YEAR_TO_MONTH_PATTERN.matcher(columnData.type);
    Matcher dayToSecondMatcber = INTERVAL_DAY_TO_SECCOND_PATTERN.matcher(columnData.type);
    Matcher timestampMatcber = TIMESTAMP_PATTERN.matcher(columnData.type);

    if (yearToMonthMatcber.matches()) {
      columnData.type = "interval year to month";

      if (columnData.size == 2) {
        columnData.size = 0;
      }

    } else if (dayToSecondMatcber.matches()) {
      columnData.type = "interval day to second";

      if (columnData.size == 2 && columnData.decimalDigits == 6) {
        columnData.size = 0;
        columnData.decimalDigits = 0;
      }

    } else if (timestampMatcber.matches()) {
      columnData.type = columnData.type.replaceAll("\\(.\\)", "");
      columnData.size = 0;

      if (columnData.decimalDigits == 6) {
        columnData.size = 0;

      } else {
        columnData.size = columnData.decimalDigits;
      }

      columnData.decimalDigits = 0;
    }
  }

  return columnData;
}

/**
 * 
 *
 * @param f 
 * @return 
 */
public static Class<?>[] getGenericTypes(Field f) {
  String gts = f.toGenericString();
  Matcher m = PTN.matcher(gts);
  if (m.find()) {
    String s = m.group(2);
    String[] ss = Strings.splitIgnoreBlank(s);
    if (ss.length > 0) {
      Class<?>[] re = new Class<?>[ss.length];
      try {
        for (int i = 0; i < ss.length; i++) {
          String className = ss[i];
          if (className.length() > 0 && className.charAt(0) == '?') re[i] = Object.class;
          else {
            int pos = className.indexOf('<');
            if (pos < 0) re[i] = Lang.loadClass(className);
            else re[i] = Lang.loadClass(className.substring(0, pos));
          }
        }
        return re;
      } catch (ClassNotFoundException e) {
        throw Lang.wrapThrow(e);
      }
    }
  }
  return new Class<?>[0];
}

