/**
 * Identifies lambdaConvention elements. The elementsValue is expected to be a comma seperated
 * lambda values and 0 or more locants. Where a lambda value has the following form: optional
 * locant, the word lambda and then a number which is the valency specified (with possibly some
 * attempt to indicate this number is superscripted) If the element is followed by heteroatoms
 * (possibly multiplied) they are multiplied and the locant/lambda assigned to them Otherwise a
 * new lambdaConvention element is created with the valency specified by the lambda convention
 * taking the attribute "lambda" In the case where heteroatoms belong to a fused ring system a new
 * lambdaConvention element is also created. The original locants are retained in the benzo
 * specific fused ring nomenclature: 2H-5lambda^5-phosphinino[3,2-b]pyran --> 2H 5lambda^5
 * phosphinino[3,2-b]pyran BUT 1lambda^4,5-Benzodithiepin --> 1lambda^4 1,5-Benzodithiepin
 *
 * @param subOrRoot
 * @throws ComponentGenerationException
 */
private void processLambdaConvention(Element subOrRoot) throws ComponentGenerationException {
  List<Element> lambdaConventionEls = subOrRoot.getChildElements(LAMBDACONVENTION_EL);
  boolean fusedRingPresent = false;
  if (lambdaConventionEls.size() > 0) {
    if (subOrRoot.getChildElements(GROUP_EL).size() > 1) {
      fusedRingPresent = true;
    }
  }
  for (Element lambdaConventionEl : lambdaConventionEls) {
    boolean frontLocantsExpected =
        false; // Is the lambdaConvention el followed by benz/benzo of a fused ring system (these
               // have front locants which correspond to the final fused rings numbering) or by a
               // polycylicspiro system
    String[] lambdaValues =
        MATCH_COMMA.split(StringTools.removeDashIfPresent(lambdaConventionEl.getValue()));
    Element possibleHeteroatomOrMultiplier = OpsinTools.getNextSibling(lambdaConventionEl);
    int heteroCount = 0;
    int multiplierValue = 1;
    while (possibleHeteroatomOrMultiplier != null) {
      if (possibleHeteroatomOrMultiplier.getName().equals(HETEROATOM_EL)) {
        heteroCount += multiplierValue;
        multiplierValue = 1;
      } else if (possibleHeteroatomOrMultiplier.getName().equals(MULTIPLIER_EL)) {
        multiplierValue =
            Integer.parseInt(possibleHeteroatomOrMultiplier.getAttributeValue(VALUE_ATR));
      } else {
        break;
      }
      possibleHeteroatomOrMultiplier = OpsinTools.getNextSibling(possibleHeteroatomOrMultiplier);
    }
    boolean assignLambdasToHeteroAtoms = false;
    if (lambdaValues.length
        == heteroCount) { // heteroatom and number of locants +lambdas must match
      if (fusedRingPresent
          && possibleHeteroatomOrMultiplier != null
          && possibleHeteroatomOrMultiplier.getName().equals(GROUP_EL)
          && possibleHeteroatomOrMultiplier
              .getAttributeValue(SUBTYPE_ATR)
              .equals(HANTZSCHWIDMAN_SUBTYPE_VAL)) {
        // You must not set the locants of a HW system which forms a component of a fused ring
        // system. The locant specified corresponds to the complete fused ring system.
      } else {
        assignLambdasToHeteroAtoms = true;
      }
    } else if (possibleHeteroatomOrMultiplier != null
        && ((heteroCount == 0
                && OpsinTools.getNextSibling(lambdaConventionEl)
                    .equals(possibleHeteroatomOrMultiplier)
                && fusedRingPresent
                && possibleHeteroatomOrMultiplier.getName().equals(GROUP_EL)
                && (possibleHeteroatomOrMultiplier.getValue().equals("benzo")
                    || possibleHeteroatomOrMultiplier.getValue().equals("benz"))
                && !OpsinTools.getNextSibling(possibleHeteroatomOrMultiplier)
                    .getName()
                    .equals(FUSION_EL)
                && !OpsinTools.getNextSibling(possibleHeteroatomOrMultiplier)
                    .getName()
                    .equals(LOCANT_EL))
            || (possibleHeteroatomOrMultiplier.getName().equals(POLYCYCLICSPIRO_EL)
                && (possibleHeteroatomOrMultiplier.getAttributeValue(VALUE_ATR).equals("spirobi")
                    || possibleHeteroatomOrMultiplier
                        .getAttributeValue(VALUE_ATR)
                        .equals("spiroter"))))) {
      frontLocantsExpected =
          true; // a benzo fused ring e.g. 1lambda4,3-benzothiazole or a symmetrical poly cyclic
                // spiro system
    }
    List<Element> heteroAtoms =
        new ArrayList<
            Element>(); // contains the heteroatoms to apply the lambda values too. Can be empty
                        // if the values are applied to a group directly rather than to a
                        // heteroatom
    if (assignLambdasToHeteroAtoms) { // populate heteroAtoms, multiplied heteroatoms are
                                      // multiplied out
      Element multiplier = null;
      Element heteroatomOrMultiplier = OpsinTools.getNextSibling(lambdaConventionEl);
      while (heteroatomOrMultiplier != null) {
        if (heteroatomOrMultiplier.getName().equals(HETEROATOM_EL)) {
          heteroAtoms.add(heteroatomOrMultiplier);
          if (multiplier != null) {
            for (int i = 1; i < Integer.parseInt(multiplier.getAttributeValue(VALUE_ATR)); i++) {
              Element newHeteroAtom = heteroatomOrMultiplier.copy();
              OpsinTools.insertBefore(heteroatomOrMultiplier, newHeteroAtom);
              heteroAtoms.add(newHeteroAtom);
            }
            multiplier.detach();
            multiplier = null;
          }
        } else if (heteroatomOrMultiplier.getName().equals(MULTIPLIER_EL)) {
          if (multiplier != null) {
            break;
          } else {
            multiplier = heteroatomOrMultiplier;
          }
        } else {
          break;
        }
        heteroatomOrMultiplier = OpsinTools.getNextSibling(heteroatomOrMultiplier);
      }
    }

    for (int i = 0;
        i < lambdaValues.length;
        i++) { // assign all the lambdas to heteroatoms or to newly created lambdaConvention
               // elements
      String lambdaValue = lambdaValues[i];
      Matcher m = matchLambdaConvention.matcher(lambdaValue);
      if (m.matches()) { // a lambda
        Attribute valencyChange = new Attribute(LAMBDA_ATR, m.group(2));
        Attribute locantAtr = null;
        if (m.group(1) != null) {
          locantAtr = new Attribute(LOCANT_ATR, m.group(1));
        }
        if (frontLocantsExpected) {
          if (m.group(1) == null) {
            throw new ComponentGenerationException(
                "Locant not found for lambda convention before a benzo fused ring system");
          }
          lambdaValues[i] = m.group(1);
        }
        if (assignLambdasToHeteroAtoms) {
          Element heteroAtom = heteroAtoms.get(i);
          heteroAtom.addAttribute(valencyChange);
          if (locantAtr != null) {
            heteroAtom.addAttribute(locantAtr);
          }
        } else {
          Element newLambda = new TokenEl(LAMBDACONVENTION_EL);
          newLambda.addAttribute(valencyChange);
          if (locantAtr != null) {
            newLambda.addAttribute(locantAtr);
          }
          OpsinTools.insertBefore(lambdaConventionEl, newLambda);
        }
      } else { // just a locant e.g 1,3lambda5
        if (!assignLambdasToHeteroAtoms) {
          if (!frontLocantsExpected) {
            throw new ComponentGenerationException(
                "Lambda convention not specified for locant: " + lambdaValue);
          }
        } else {
          Element heteroAtom = heteroAtoms.get(i);
          heteroAtom.addAttribute(new Attribute(LOCANT_ATR, lambdaValue));
        }
      }
    }
    if (!frontLocantsExpected) {
      lambdaConventionEl.detach();
    } else {
      lambdaConventionEl.setName(LOCANT_EL);
      lambdaConventionEl.setValue(StringTools.arrayToString(lambdaValues, ","));
    }
  }
}

/**
 * Builds a list of {@link ReplaceEdit} for a refactoring wizard.
 *
 * @param text the original text
 * @param regex the regular expression to replace
 * @param newValue the new text
 * @param leftRegex a regular expression that defines a left delimiter (can be null for none)
 * @param rightRegex a regular expression that defines a right delimiter (can be null for none)
 * @return a non-null list of Replace edits
 */
public static List<ReplaceEdit> buildReplaceEdits(
    String text, String regex, String leftRegex, String rightRegex, String newValue) {

  StringBuilder completeRegex = new StringBuilder();
  if (leftRegex != null) completeRegex.append(leftRegex);
  completeRegex.append(regex);
  if (rightRegex != null) completeRegex.append(rightRegex);

  List<ReplaceEdit> edits = new ArrayList<ReplaceEdit>();
  Pattern completePattern =
      Pattern.compile(completeRegex.toString(), Pattern.MULTILINE | Pattern.DOTALL);
  Pattern pattern = Pattern.compile(regex, Pattern.MULTILINE | Pattern.DOTALL);
  Matcher m = completePattern.matcher(text);
  while (m.find()) {

    String sequence = m.group();
    Matcher subMatcher = pattern.matcher(sequence);
    if (subMatcher.find()) {
      int start = m.start() + subMatcher.start();
      int length = subMatcher.group().length();

      ReplaceEdit replaceEdit = new ReplaceEdit(start, length, newValue);
      edits.add(replaceEdit);
    }
  }

  return edits;
}

/**
 * Get the script stack of this exception as an array of {@link ScriptStackElement}s. If
 * optimization is enabled, this includes java stack elements whose source and method names
 * suggest they have been generated by the Rhino script compiler.
 *
 * @return the script stack for this exception
 * @since 1.7R3
 */
public ScriptStackElement[] getScriptStack() {
  List<ScriptStackElement> list = new ArrayList<ScriptStackElement>();
  ScriptStackElement[][] interpreterStack = null;
  if (interpreterStackInfo != null) {
    Evaluator interpreter = Context.createInterpreter();
    if (interpreter instanceof Interpreter)
      interpreterStack = ((Interpreter) interpreter).getScriptStackElements(this);
  }
  int interpreterStackIndex = 0;
  StackTraceElement[] stack = getStackTrace();
  // Pattern to recover function name from java method name -
  // see Codegen.getBodyMethodName()
  // kudos to Marc Guillemot for coming up with this
  Pattern pattern = Pattern.compile("_c_(.*)_\\d+");
  for (StackTraceElement e : stack) {
    String fileName = e.getFileName();
    if (e.getMethodName().startsWith("_c_")
        && e.getLineNumber() > -1
        && fileName != null
        && !fileName.endsWith(".java")) {
      String methodName = e.getMethodName();
      Matcher match = pattern.matcher(methodName);
      // the method representing the main script is always "_c_script_0" -
      // at least we hope so
      methodName = !"_c_script_0".equals(methodName) && match.find() ? match.group(1) : null;
      list.add(new ScriptStackElement(fileName, methodName, e.getLineNumber()));
    } else if ("org.mozilla.javascript.Interpreter".equals(e.getClassName())
        && "interpretLoop".equals(e.getMethodName())
        && interpreterStack != null
        && interpreterStack.length > interpreterStackIndex) {
      for (ScriptStackElement elem : interpreterStack[interpreterStackIndex++]) {
        list.add(elem);
      }
    }
  }
  return list.toArray(new ScriptStackElement[list.size()]);
}

static HtmlCommand getInstance(String html) {
  final Matcher m = srcPattern.matcher(html);
  if (m.find() == false) {
    return new Text("(SYNTAX ERROR)");
  }
  final String src = m.group(1);
  try {
    final File f = FileSystem.getInstance().getFile(src);
    if (f.exists() == false) {
      return new Text("(File not found: " + f + ")");
    }
    final int vspace = getVspace(html);
    final ImgValign valign = getValign(html);
    return new Img(new TileImage(ImageIO.read(f), valign, vspace), src);
  } catch (IOException e) {
    return new Text("ERROR " + e.toString());
  }
}

/**
 * Prepares the given HTML formatted text for output as part of an HTML document. Makes sure that
 * links open up in a new window by setting 'target="_blank"' on anchor tags.
 *
 * @param value The formatted text to escape
 * @param supressNewlines If true, remove newlines ("&lt;br /&gt;") when escaping.
 * @return The string to include in an HTML document.
 */
private static String escapeHtmlFormattedText(String value, boolean supressNewlines) {
  if (value == null) return "";
  if (value.length() == 0) return "";

  if (supressNewlines) {
    // zap HTML line breaks ("<br />") into plain-old whitespace
    value = M_patternTagBr.matcher(value).replaceAll(" ");
  }

  // make sure that links open up in a new window. This
  // makes sure every anchor tag has a blank target.
  // for example:
  // <a href="http://www.microsoft.com">Microsoft</a>
  // becomes:
  // <a href="http://www.microsoft.com" target="_blank">Microsoft</a>
  // removed for KNL-526
  // value = M_patternAnchorTagWithTarget.matcher(value).replaceAll("$1$2>"); // strips out
  // targets
  // value = M_patternAnchorTag.matcher(value).replaceAll("$1$2$3 target=\"_blank\">"); // adds in
  // blank targets
  // added for KNL-526
  String testvalue0 = M_patternAnchorTag.matcher(value).replaceAll("$0");
  String testvalue1 = M_patternAnchorTag.matcher(value).replaceAll("$1");
  String testvalue2 = M_patternAnchorTag.matcher(value).replaceAll("$2");
  String testvalue3 = M_patternAnchorTag.matcher(value).replaceAll("$3");

  String testvalueWO0 = M_patternAnchorTagWithOutTarget.matcher(value).replaceAll("$0");
  String testvalueWO1 = M_patternAnchorTagWithOutTarget.matcher(value).replaceAll("$1");
  String testvalueWO2 = M_patternAnchorTagWithOutTarget.matcher(value).replaceAll("$2");
  // String testvalueWO3 = M_patternAnchorTagWithOutTarget.matcher(value).replaceAll("$3");
  // String testvalueWO4 = M_patternAnchorTagWithOutTarget.matcher(value).replaceAll("$4");

  Matcher m = M_patternAnchorTagWithOutTarget.matcher(value);
  if (m.find()) {
    value = m.replaceAll("$1$2 target=\"_blank\">"); // adds a target to A tags without one
  }

  return value;
}

