private static Collection<String> getResourcesLinesFromJarFile(File file, Pattern pattern)
    throws IOException {
  final ArrayList<String> lines = Lists.newArrayList();
  ZipFile zf = new ZipFile(file);
  final Enumeration e = zf.entries();
  while (e.hasMoreElements()) {
    final ZipEntry ze = (ZipEntry) e.nextElement();
    final String fileName = ze.getName();
    final boolean accept = pattern.matcher(fileName).matches();
    if (accept) {
      lines.addAll(
          CharStreams.readLines(new InputStreamReader(zf.getInputStream(ze), Charsets.UTF_8)));
    }
  }
  zf.close();
  return lines;
}

public static MultiTypeType getArrayType(String type, IType currentNamespace, int offset) {
  int beginIndex = type.indexOf("[") + 1; // $NON-NLS-1$
  int endIndex = type.lastIndexOf("]"); // $NON-NLS-1$
  if (endIndex != -1) {
    type = type.substring(beginIndex, endIndex);
  }
  MultiTypeType arrayType = new MultiTypeType();
  Matcher m = ARRAY_TYPE_PATTERN.matcher(type);
  if (m.find()) {
    arrayType.addType(getArrayType(m.group(), currentNamespace, offset));
    type = m.replaceAll(""); // $NON-NLS-1$
  }
  String[] typeNames = type.split(","); // $NON-NLS-1$
  for (String name : typeNames) {
    if (!"".equals(name)) { // $NON-NLS-1$

      if (name.indexOf(NamespaceReference.NAMESPACE_SEPARATOR) > 0 && currentNamespace != null) {
        // check if the first part is an
        // alias,then get the full name
        ModuleDeclaration moduleDeclaration =
            SourceParserUtil.getModuleDeclaration(currentNamespace.getSourceModule());
        String prefix = name.substring(0, name.indexOf(NamespaceReference.NAMESPACE_SEPARATOR));
        final Map<String, UsePart> result =
            PHPModelUtils.getAliasToNSMap(
                prefix, moduleDeclaration, offset, currentNamespace, true);
        if (result.containsKey(prefix)) {
          String fullName = result.get(prefix).getNamespace().getFullyQualifiedName();
          name = name.replace(prefix, fullName);
          if (name.charAt(0) != NamespaceReference.NAMESPACE_SEPARATOR) {
            name = NamespaceReference.NAMESPACE_SEPARATOR + name;
          }
        }
      }
      arrayType.addType(getEvaluatedType(name, currentNamespace));
    }
  }
  return arrayType;
}

