int read_ValLabels(String valLabelsCommand) {

  int readStatus = 0;

  Map<String, Map<String, String>> valueLabelTable =
      new LinkedHashMap<String, Map<String, String>>();
  Map<String, String> valueVariableMappingTable = new LinkedHashMap<String, String>();

  // Value Labels are referenced by the (declared) variable names,
  // followed by a number of value-"label" pairs; for every variable
  // the entry is terminated with a "/".

  dbgLog.fine("parsing " + valLabelsCommand + " for value labels.");

  if (valLabelsCommand == null || valLabelsCommand.equals("")) {
    return readStatus;
  }

  String varName = null;
  String valLabelDeclaration = null;

  String varValue = null;
  String valueLabel = null;

  int labelCounter = 0;

  String valLabelRegex = "\\s*(\\S+)\\s+([^/]+)/";
  Pattern valLabelPattern = Pattern.compile(valLabelRegex);
  Matcher valLabelMatcher = valLabelPattern.matcher(valLabelsCommand);

  String labelDeclarationRegex = "\\s*(\\S+)\\s+(\"[^\"]*\")";
  Pattern labelDeclarationPattern = Pattern.compile(labelDeclarationRegex);

  while (valLabelMatcher.find()) {
    varName = valLabelMatcher.group(1);
    valLabelDeclaration = valLabelMatcher.group(2);

    dbgLog.fine("found value label declaration for " + varName + ": " + valLabelDeclaration);

    Map<String, String> valueLabelPairs = new LinkedHashMap<String, String>();

    Matcher labelDeclarationMatcher = labelDeclarationPattern.matcher(valLabelDeclaration);

    int localLabelCounter = 0;

    while (labelDeclarationMatcher.find()) {
      varValue = labelDeclarationMatcher.group(1);
      valueLabel = labelDeclarationMatcher.group(2);

      dbgLog.fine("found label " + valueLabel + " for value " + varValue);

      Boolean isNumeric = false; // TODO: !
      if (isNumeric) {
        // Numeric variable:
        dbgLog.fine("processing numeric value label");
        valueLabelPairs.put(doubleNumberFormatter.format(new Double(varValue)), valueLabel);
      } else {
        // String variable
        dbgLog.fine("processing string value label");
        varValue = varValue.replaceFirst("^[\"']", "");
        varValue = varValue.replaceFirst("[\"']$", "");

        valueLabelPairs.put(varValue, valueLabel);
      }

      localLabelCounter++;
    }

    if (localLabelCounter > 0) {
      valueLabelTable.put(varName, valueLabelPairs);
      valueVariableMappingTable.put(varName, varName);
      labelCounter += localLabelCounter;
    }

    // TODO:
    // Do SPSS cards support shared value label sets -- ?

  }

  if (labelCounter > 0) {
    smd.setValueLabelTable(valueLabelTable);
    smd.setValueLabelMappingTable(valueVariableMappingTable);
    readStatus = labelCounter;
  }

  // TODO:
  // Better validation, error reporting.

  return readStatus;
}

/**
 * @param request the request to analyse the user-agent header of.
 * @return A string identifying the browser that made the request, of the form gecko-1-8 or
 *     ie-5[space]ie-5-5, or empty string if unknown
 */
public static String getBrowserString(HttpServletRequest request) {
  String sAgent = request.getHeader("user-agent");
  // Filter troublemakers
  if (sAgent.indexOf("KHTML") != -1) return "khtml";
  if (sAgent.indexOf("Opera") != -1) return "opera";

  // Check version of our two supported browsers
  Matcher mGecko = GECKOVERSION.matcher(sAgent);
  if (mGecko.matches()) {
    return "gecko-" + mGecko.group(1) + "-" + mGecko.group(2);
  }
  Matcher mIE = IEVERSION.matcher(sAgent);
  if (mIE.matches()) {
    return "winie-" + mIE.group(1); // Major verison only
  }

  return "";
}

/**
 * split the given line into it's relevant segments if applicable (i.e. the line matches the
 * required regular expression.
 *
 * @param line the line to be segmented
 * @return a Log Line object for the given line
 */
public static LogLine getLogLine(String line) {
  // FIXME: consider moving this code into the LogLine class. To do this
  // we need to much more carefully define the structure and behaviour
  // of the LogLine class
  Matcher match;

  if (line.indexOf(":ip_addr") > 0) {
    match = valid14.matcher(line);
  } else {
    match = valid13.matcher(line);
  }

  if (match.matches()) {
    // set up a new log line object
    LogLine logLine =
        new LogLine(
            parseDate(match.group(1).trim()),
            LogManager.unescapeLogField(match.group(2)).trim(),
            LogManager.unescapeLogField(match.group(3)).trim(),
            LogManager.unescapeLogField(match.group(4)).trim(),
            LogManager.unescapeLogField(match.group(5)).trim());

    return logLine;
  } else {
    match = validBase.matcher(line);
    if (match.matches()) {
      LogLine logLine =
          new LogLine(
              parseDate(match.group(1).trim()),
              LogManager.unescapeLogField(match.group(2)).trim(),
              null,
              null,
              null);
      return logLine;
    }
    return null;
  }
}

private void visitScriptConfig(ScriptConfig config) {
  GroovyScript script = config.getValue();
  if (script != null && script.getSource() != null) {
    Matcher datasourceNameFinder = DATASOURCE_NAME_FINDER_PATTERN.matcher(script.getSource());
    while (datasourceNameFinder.find()) {
      this.addDataSourceName(datasourceNameFinder.group(1));
    }
  }
}

public String readLine() throws IOException {
  String s = source.readLine();
  if (s == null) {
    return null;
  }

  Matcher m = definePattern.matcher(s);
  if (m.find()) {
    return manageDefine(m);
  }

  m = undefPattern.matcher(s);
  if (m.find()) {
    return manageUndef(m);
  }

  s = defines.applyDefines(s);
  return s;
}

/** Parse a unified diff and return true on success or false and set the error message. */
public boolean parseDiff() {
  // Don't re-parse the parsed diff
  if (parsed) return true;

  Pattern chunkStart = Pattern.compile(diffChunkStart);
  Pattern propChunk = Pattern.compile(propChunkStart);
  Pattern fileTarget = Pattern.compile(chunkFileTarget);
  Pattern fileSource = Pattern.compile(chunkFileSource);
  Pattern idx = Pattern.compile(index);
  Pattern equals = Pattern.compile(eqs);

  Matcher m = null;

  BufferedReader r = new BufferedReader(new StringReader(theDiff));
  String line;

  boolean diffStart = false, chnkStart = false;
  StringBuffer curChunkText = null;
  String curPath = null;
  DiffChunkImpl curChunk = null;
  List<DiffChunk> curChunkList = null;
  try {
    while ((line = r.readLine()) != null) {
      /*
       * Skip SVN introduced lines like
       * Index: specs/src/eu/sqooss/impl/service/dsl/SpRevision.java
       * ===========================================================
       */
      if (idx.matcher(line).matches() || equals.matcher(line).matches()) {
        continue;
      }

      /* Match lines like
       * --- specs/src/eu/sqooss/impl/service/dsl/SpRevision.java
       *
       *
       * and record file name. Match either of those 2 lines
       * signifies start of parsing chunks
       */
      if (fileSource.matcher(line).matches()) {
        // New group found
        if (diffStart == true && curChunkList.size() != 0) {
          diffChunks.put(curPath, curChunkList);
        }

        m = fileSource.matcher(line);
        m.matches();
        curPath = FileUtils.appendPath(basePath, m.group(1));
        changedPaths.add(curPath);
        curChunkList = new ArrayList<DiffChunk>();
        diffStart = true;
        continue;
      }
      /* those lines are not really interesting
       * +++ specs/src/eu/sqooss/impl/service/dsl/SpRevision.java
       */
      if (fileTarget.matcher(line).matches()) {
        continue;
      }

      /*
       * Match a chunk describing a property change
       * (this is SVN specific code)
       * Property changes on: ProjectFile.java
       */
      if (propChunk.matcher(line).matches()) {
        /* Match */
        if (chnkStart == true) {
          curChunk.setChunk(curChunkText.toString());
          curChunkList.add(curChunk);
        }

        chnkStart = true;
        curChunk = new DiffChunkImpl();
        curChunk.setDiffOp(DiffOp.UNDEF);
        curChunkText = new StringBuffer();

        /* If the property change is the only thing
         * appearing in the diff, we need to start
         * parsing and init some variables first.
         */
        if (!diffStart) {
          curChunkList = new ArrayList<DiffChunk>();
          diffStart = true;
          m = propChunk.matcher(line);
          m.matches();
          curPath = FileUtils.appendPath(basePath, m.group(1));
          changedPaths.add(curPath);
        }
      }

      if (!diffStart) {
        error = "Not a chunk header: " + line;
        return false;
      }

      /* Match chunk start lines like
       * @@ -111,10 +111,10 @@ or
       * @@ -1 +1 @@
       */
      if (chunkStart.matcher(line).matches()) {

        if (chnkStart == true) {
          curChunk.setChunk(curChunkText.toString());
          curChunkList.add(curChunk);
        }

        chnkStart = true;
        curChunk = new DiffChunkImpl();
        m = chunkStart.matcher(line);
        // We already know that it matches, just trigger the group extraction
        m.matches();

        // Unified diffs do not support this
        curChunk.setDiffOp(DiffOp.UNDEF);
        curChunk.setPath(curPath);
        curChunk.setSourceStartLine(Integer.parseInt(m.group(1)));
        if (m.group(2) != null) {
          curChunk.setSourceLenght(Integer.parseInt(m.group(2).substring(1)));
        } else {
          curChunk.setSourceLenght(0);
        }
        curChunk.setTargetStartLine(Integer.parseInt(m.group(3)));
        if (m.group(4) != null) {
          curChunk.setTargetLength(Integer.parseInt(m.group(4).substring(1)));
        } else {
          curChunk.setTargetLength(0);
        }
        curChunkText = new StringBuffer();
        continue;
      }

      curChunkText.append(line).append("\n");
    }
  } catch (IOException e) {
    error = "Error reading diff file";
    return false;
  }
  // Clean up
  if (curChunk != null) {
    curChunk.setChunk(curChunkText.toString());
    curChunkList.add(curChunk);
    diffChunks.put(curPath, curChunkList);
  } else {
    // This means that the actual diff was empty
    List<DiffChunk> l = Collections.emptyList();
    diffChunks.put(curPath, l);
  }

  // Don't hold up space now that the diff is parsed
  theDiff = null;
  parsed = true;
  return true;
}

protected static void removePontuation(Subfield sf, String regExp) {
  String value = sf.getValue();
  Pattern p = Pattern.compile(regExp);
  Matcher m = p.matcher(value);
  String newValue = m.replaceAll("");
  sf.setValue(newValue);
}

/**
 * Expand the string template and replaces the selection with the expansion of the template. The
 * template String may contain any of the following special tags.
 * <li>{@code #{selection}} replaced with the selection, if any. If there is no selection, then
 *     the {@code #{selection}} tag will be removed.
 * <li>{@code #{p:any text}} will be replaced by {@code any text} and then set selection to {@code
 *     any text}
 *
 *     <p>This method properly handles indentation as follows: The indentation of the whole block
 *     will match the indentation of the caret line, or the line with the beginning of the
 *     selection, if the selection is in whole line, i.e.e one or more lines of selected text.
 *     {@see selectLines()}
 *
 * @param target JEditorCOmponent to be affected
 * @param templateLines template split as a String array of lines.
 * @see insertLinesTemplate
 */
public static void insertLinesTemplate(JTextComponent target, String[] templateLines) {
  // get some stuff we'll need:
  String thisIndent = getIndent(getLineAt(target, target.getSelectionStart()));
  String[] selLines = getSelectedLines(target);
  int selStart = -1, selEnd = -1;
  StringBuffer sb = new StringBuffer();
  for (String tLine : templateLines) {
    int selNdx = tLine.indexOf("#{selection}");
    if (selNdx >= 0) {
      // for each of the selected lines:
      for (String selLine : selLines) {
        sb.append(tLine.subSequence(0, selNdx));
        sb.append(selLine);
        sb.append('\n');
      }
    } else {
      sb.append(thisIndent);
      // now check for any ptags
      Matcher pm = PTAGS_PATTERN.matcher(tLine);
      int lineStart = sb.length();
      while (pm.find()) {
        selStart = pm.start() + lineStart;
        pm.appendReplacement(sb, pm.group(1));
        selEnd = sb.length();
      }
      pm.appendTail(sb);
      sb.append('\n');
    }
  }
  int ofst = target.getSelectionStart();
  target.replaceSelection(sb.toString());
  if (selStart >= 0) {
    // target.setCaretPosition(selStart);
    target.select(ofst + selStart, ofst + selEnd);
  }
}

public static Header readHeader(DataInputStream in) throws IOException {
  int valid = 0;
  String programType = null;
  float gamma = 1.0f;
  float exposure = 1.0f;
  int width = 0;
  int height = 0;

  String buf = in.readLine();
  if (buf == null) {
    throw new IOException("Unexpected EOF reading magic token");
  }
  if (buf.charAt(0) == '#' && buf.charAt(1) == '?') {
    valid |= VALID_PROGRAMTYPE;
    programType = buf.substring(2);
    buf = in.readLine();
    if (buf == null) {
      throw new IOException("Unexpected EOF reading line after magic token");
    }
  }

  boolean foundFormat = false;
  boolean done = false;
  while (!done) {
    if (buf.equals("FORMAT=32-bit_rle_rgbe")) {
      foundFormat = true;
    } else if (buf.startsWith(gammaString)) {
      valid |= VALID_GAMMA;
      gamma = Float.parseFloat(buf.substring(gammaString.length()));
    } else if (buf.startsWith(exposureString)) {
      valid |= VALID_EXPOSURE;
      exposure = Float.parseFloat(buf.substring(exposureString.length()));
    } else {
      Matcher m = widthHeightPattern.matcher(buf);
      if (m.matches()) {
        width = Integer.parseInt(m.group(2));
        height = Integer.parseInt(m.group(1));
        done = true;
      }
    }

    if (!done) {
      buf = in.readLine();
      if (buf == null) {
        throw new IOException("Unexpected EOF reading header");
      }
    }
  }

  if (!foundFormat) {
    throw new IOException("No FORMAT specifier found");
  }

  return new Header(valid, programType, gamma, exposure, width, height);
}

private int extractNumber(String line) {
  String numbers = new String();

  Pattern p = Pattern.compile("\\d+");
  Matcher m = p.matcher(line);
  while (m.find()) {
    numbers = numbers + m.group();
  }

  return Integer.parseInt(numbers);
}

public boolean process(CellWrapper cell, FPParser parser) throws FPParseException {
  String value = cell.getStringValue();
  Matcher mat = pat.matcher(value);
  if (!mat.find()) {
    return false;
  }
  String condition = mat.group(1);
  RowWrapper row = cell.getRow();
  AbstractBlock block = new WhileBlock(row, condition);
  parser.addBlockElement(block);
  return true;
}

/** @test java.util.regex.Matcher#end(int) test end(int) method. */
@TestTargetNew(
  level = TestLevel.PARTIAL_COMPLETE,
  notes = "Verifies the basic functionality of end(int group) method.",
  method = "end",
  args = {int.class}
)
public void test_endI() {
  String testPattern = "(((abb)a)(bb))";
  String testString = "cccabbabbabbabbabb";
  Pattern pat = Pattern.compile(testPattern);
  Matcher mat = pat.matcher(testString);
  int start = 3;
  int end = 6;
  int i, j;

  for (j = 0; j < 3; j++) {
    while (mat.find(start + j - 2)) {
      for (i = 0; i < 4; i++) {
        assertEquals(
            "End is wrong for group " + i + " :" + mat.group(i),
            start + mat.group(i).length(),
            mat.end(i));
      }
      assertEquals(
          "End is wrong for group " + i + " :" + mat.group(i),
          start + 4 + mat.group(i).length(),
          mat.end(i));

      start = end;
      end += 3;
    }
  }
}

public CommandExecutionResult execute(List<String> lines) {
  final Context context = new Context();
  final Matcher mStart = getStartingPattern().matcher(lines.get(0).trim());
  if (mStart.find() == false) {
    throw new IllegalStateException();
  }
  if (mStart.group(1) != null) {
    context.push(mStart.group(1));
  }

  lines = new ArrayList<String>(lines.subList(1, lines.size() - 1));
  StringUtils.trim(lines, true);

  for (String s : lines) {
    assert s.length() > 0;
    if (s.equals("}")) {
      context.pop();
      continue;
    }
    final Matcher m = p1.matcher(s);
    if (m.find() == false) {
      throw new IllegalStateException();
    }
    if (m.group(2) != null) {
      context.push(m.group(1));
    } else if (m.group(3) != null) {
      final String key = context.getFullParam() + m.group(1);
      getSystem().setParam(key, m.group(3));
    } else {
      throw new IllegalStateException();
    }
  }

  return CommandExecutionResult.ok();
}

/**
 * Retrieves the NYSIIS code for a given String object.
 *
 * @param str String to encode using the NYSIIS algorithm
 * @return A NYSIIS code for the String supplied
 */
public String nysiis(String str) {
  if (str == null) {
    return null;
  }

  // Use the same clean rules as Soundex
  str = SoundexUtils.clean(str);

  if (str.length() == 0) {
    return str;
  }

  // Translate first characters of name:
  // MAC -> MCC, KN -> NN, K -> C, PH | PF -> FF, SCH -> SSS
  str = PAT_MAC.matcher(str).replaceFirst("MCC");
  str = PAT_KN.matcher(str).replaceFirst("NN");
  str = PAT_K.matcher(str).replaceFirst("C");
  str = PAT_PH_PF.matcher(str).replaceFirst("FF");
  str = PAT_SCH.matcher(str).replaceFirst("SSS");

  // Translate last characters of name:
  // EE -> Y, IE -> Y, DT | RT | RD | NT | ND -> D
  str = PAT_EE_IE.matcher(str).replaceFirst("Y");
  str = PAT_DT_ETC.matcher(str).replaceFirst("D");

  // First character of key = first character of name.
  final StringBuilder key = new StringBuilder(str.length());
  key.append(str.charAt(0));

  // Transcode remaining characters, incrementing by one character each time
  final char[] chars = str.toCharArray();
  final int len = chars.length;

  for (int i = 1; i < len; i++) {
    final char next = i < len - 1 ? chars[i + 1] : SPACE;
    final char aNext = i < len - 2 ? chars[i + 2] : SPACE;
    final char[] transcoded = transcodeRemaining(chars[i - 1], chars[i], next, aNext);
    System.arraycopy(transcoded, 0, chars, i, transcoded.length);

    // only append the current char to the key if it is different from the last one
    if (chars[i] != chars[i - 1]) {
      key.append(chars[i]);
    }
  }

  if (key.length() > 1) {
    char lastChar = key.charAt(key.length() - 1);

    // If last character is S, remove it.
    if (lastChar == 'S') {
      key.deleteCharAt(key.length() - 1);
      lastChar = key.charAt(key.length() - 1);
    }

    if (key.length() > 2) {
      final char last2Char = key.charAt(key.length() - 2);
      // If last characters are AY, replace with Y.
      if (last2Char == 'A' && lastChar == 'Y') {
        key.deleteCharAt(key.length() - 2);
      }
    }

    // If last character is A, remove it.
    if (lastChar == 'A') {
      key.deleteCharAt(key.length() - 1);
    }
  }

  final String string = key.toString();
  return this.isStrict() ? string.substring(0, Math.min(TRUE_LENGTH, string.length())) : string;
}
