/**
 * Convenience method to follow http to https redirects. Will follow a total of 5 redirects, then
 * fail out due to foolishness on the url's part.
 *
 * @param c the {@link URLConnection} to open
 * @return an {@link InputStream} that is not necessarily at the same url, possibly at a 403
 *     redirect.
 * @throws IOException
 * @see {@link #getURLStream(URL)}
 */
protected static InputStream openConnectionCheckRedirects(URLConnection c) throws IOException {
  boolean redir;
  int redirects = 0;
  InputStream in = null;
  do {
    if (c instanceof HttpURLConnection) {
      ((HttpURLConnection) c).setInstanceFollowRedirects(false);
    }
    // We want to open the input stream before getting headers
    // because getHeaderField() et al swallow IOExceptions.
    in = c.getInputStream();
    redir = false;
    if (c instanceof HttpURLConnection) {
      HttpURLConnection http = (HttpURLConnection) c;
      int stat = http.getResponseCode();
      if (stat == 300
          || stat == 301
          || stat == 302
          || stat == 303
          || stat == 305
          || stat == 307) {
        URL base = http.getURL();
        String loc = http.getHeaderField("Location");
        URL target = null;
        if (loc != null) {
          target = new URL(base, loc);
        }
        http.disconnect();
        // Redirection should be allowed only for HTTP and HTTPS
        // and should be limited to 5 redirections at most.
        if (target == null
            || !(target.getProtocol().equals("http") || target.getProtocol().equals("https"))
            || redirects >= 5) {
          throw new SecurityException("illegal URL redirect");
        }
        redir = true;
        c = target.openConnection();
        redirects++;
      }
    }
  } while (redir);
  return in;
}

private CyCustomGraphics translateURL(String inputValue) {
  try {
    final URL url = new URL(inputValue);
    String mimeType = mimeTypes.get(inputValue);
    if (mimeType == null) {
      URLConnection conn = url.openConnection();
      if (conn == null) return null;
      mimeType = conn.getContentType();
      mimeTypes.put(inputValue, mimeType);
    }
    for (CyCustomGraphicsFactory factory : cgMgr.getAllCustomGraphicsFactories()) {
      if (factory.supportsMime(mimeType)) {
        CyCustomGraphics cg = factory.getInstance(url);
        if (cg != null) return cg;
      }
    }

  } catch (MalformedURLException e) {
  } catch (IOException e) {
  }
  return null;
}

