/**
 * Uses an instance of this class to read an XML document from a given URL and extract the
 * TableMeta objects from it.
 *
 * @param url containing a TableSet document or similar
 */
public static TableMeta[] readTableSet(URL url) throws IOException, SAXException {
  SAXParserFactory spfact = SAXParserFactory.newInstance();
  SAXParser parser;
  try {
    spfact.setNamespaceAware(false);
    spfact.setValidating(false);
    parser = spfact.newSAXParser();
  } catch (ParserConfigurationException e) {
    throw (IOException) new IOException("SAX trouble").initCause(e);
  } catch (SAXException e) {
    throw (IOException) new IOException("SAX trouble").initCause(e);
  }
  TableSetSaxHandler tsHandler = new TableSetSaxHandler();
  URLConnection conn = url.openConnection();
  if (conn instanceof HttpURLConnection) {
    HttpURLConnection hconn = (HttpURLConnection) conn;
    int code = hconn.getResponseCode();
    if (code != HttpURLConnection.HTTP_OK) {
      throw new IOException(
          "Table resource access failure (" + code + " " + hconn.getResponseMessage() + ")");
    }
  }
  InputStream in = new BufferedInputStream(conn.getInputStream());
  try {
    parser.parse(in, tsHandler);
    return tsHandler.getTables();
  } finally {
    in.close();
  }
}

/**
 * Opens and returns a connection to the given URL.
 *
 * <p>The default implementation uses the given {@linkplain #setProxy(java.net.Proxy) proxy} - if
 * any - to open a connection.
 *
 * @param url the URL to open a connection to
 * @param proxy the proxy to use, may be {@code null}
 * @return the opened connection
 * @throws IOException in case of I/O errors
 */
protected HttpURLConnection openConnection(URL url, Proxy proxy) throws IOException {
  URLConnection urlConnection =
      (proxy != null ? url.openConnection(proxy) : url.openConnection());
  Assert.isInstanceOf(HttpURLConnection.class, urlConnection);
  return (HttpURLConnection) urlConnection;
}

protected String post(String uri, Arg[] args) throws IOException {
  String data = arglist2string(args);
  Log.d(LOG_TAG, "post " + uri + ", data : " + data);
  URL url = new URL(uri);
  HttpURLConnection conn = (HttpURLConnection) url.openConnection();
  conn.setDoOutput(true);
  conn.setRequestMethod("POST");
  conn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
  conn.setRequestProperty("Content-Length", "" + Integer.toString(data.getBytes().length));
  conn.setRequestProperty("Cookie", getCookiesHeader());
  conn.setUseCaches(false);
  conn.setDoInput(true);
  conn.setDoOutput(true);

  DataOutputStream wr = new DataOutputStream(conn.getOutputStream());
  wr.writeBytes(data);
  wr.flush();

  StringBuilder builder = new StringBuilder();
  InputStreamReader is;
  if (conn.getResponseCode() == 200) {
    is = new InputStreamReader(conn.getInputStream(), "UTF-8");
  } else {
    is = new InputStreamReader(conn.getErrorStream(), "UTF-8");
  }
  BufferedReader in = new BufferedReader(is);
  String inputLine;
  while ((inputLine = in.readLine()) != null) {
    builder.append(inputLine);
  }
  in.close();
  updateCookies(conn.getHeaderFields().get("Set-Cookie"));

  return builder.toString();
}

/**
 * Tries to get last modification timestamp of the resource by obtaining a <tt>URLConnection</tt>
 * to the file in the filesystem or JAR.
 *
 * @param resourcePath The relative (to the servlet) resource path.
 * @return Either the last modified filestamp or if an error occurs, the JVM system startup
 *     timestamp.
 */
protected Long getLastModifiedTimestamp(String resourcePath) {
  try {
    // Try to load it from filesystem or JAR through URLConnection
    URL resourceURL = Resources.getResource(resourcePath, getServletContext());
    if (resourceURL == null) {
      // Fall back to startup time of the JVM
      return ManagementFactory.getRuntimeMXBean().getStartTime();
    }
    URLConnection resourceConn = resourceURL.openConnection();
    return resourceConn.getLastModified();
  } catch (Exception ex) {
    // Fall back to startup time of the JVM
    return ManagementFactory.getRuntimeMXBean().getStartTime();
  }
}

