public boolean isTokenValid(String token) throws AuthenticationException {
  try {
    URL u = new URL(getAuthenticationURL() + "/isTokenValid");
    HttpURLConnection uc = (HttpURLConnection) u.openConnection();
    uc.setRequestMethod("POST");
    uc.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");

    uc.setDoOutput(true);
    uc.setDoInput(true);

    PrintWriter pr = new PrintWriter(new OutputStreamWriter(uc.getOutputStream()));
    pr.append("tokenid=" + URLEncoder.encode(token, "UTF-8"));
    pr.close();

    logger.fine(
        "IsTokenValid response: " + uc.getResponseCode() + ": " + uc.getResponseMessage());

    BufferedReader br = new BufferedReader(new InputStreamReader(uc.getInputStream()));
    String line;
    while ((line = br.readLine()) != null) {
      logger.finest("IsTokenValid response line: " + line);

      if (line.startsWith("boolean=")) {
        return Boolean.parseBoolean(line.substring("boolean=".length()));
      }
    }
  } catch (IOException ioe) {
    throw new AuthenticationException(ioe);
  }

  throw new AuthenticationException("No validation in response");
}

/**
 * Query web service if given product by instance_urn is registered. Returns true only when we
 * have 'REGISTERED' response from server. Otherwise return false.
 */
public static boolean isRegistered2(String host, String uuid) {
  try {
    URL url = new URL(host + "/ProductRegistrationService/status/" + uuid);
    LOG.log(Level.FINE, "Query URL: " + url);
    // HttpURLConnection con = (HttpURLConnection) (url.openConnection());
    HttpsURLConnection con = (HttpsURLConnection) (url.openConnection());

    con.setRequestMethod("GET");
    con.setDoOutput(true);
    con.setDoInput(true);
    con.setAllowUserInteraction(false);
    con.setUseCaches(false);

    con.connect();
    int responseCode = con.getResponseCode();

    LOG.log(Level.FINE, "Response code = " + responseCode);
    if (responseCode == 200) {
      BufferedReader reader = new BufferedReader(new InputStreamReader(con.getInputStream()));
      StringBuffer sb = new StringBuffer();

      while (true) {
        String line = reader.readLine();
        if (line == null) {
          break;
        }
        sb.append(line);
      }

      reader.close();
      String response = sb.toString();

      LOG.log(Level.FINE, "Response: " + response);

      // the response should be equal to 'REGISTERED' or 'NOT REGISTERED'
      if (response.equals("REGISTERED")) {
        return true;
      } else {
        return false;
      }
    } else if (responseCode == 404) {
      // response code of 404 is not found, which means not registered
      return false;
    } else {
      // unknown response code
      return false;
    }
  } catch (Exception ex) {
    LOG.log(Level.FINE, "Error: " + ex.getMessage(), ex);
  }
  return false;
}

/**
 * Given a ticket an a list of citations, retrieve BibtexEntries from JStor
 *
 * @param ticket A valid ticket as returned by openTicket()
 * @param citations A list of citations as returned by getCitations()
 * @return A collection of BibtexEntries parsed from the bibtex returned by JStor.
 * @throws IOException Most probably related to a problem connecting to JStor.
 */
protected Collection<BibtexEntry> getBibtexEntries(String ticket, String citations)
    throws IOException {
  try {
    URL url = new URL(URL_BIBTEX);
    URLConnection conn = url.openConnection();
    conn.setRequestProperty("Cookie", ticket + "; " + citations);
    conn.connect();

    BibtexParser parser =
        new BibtexParser(new BufferedReader(new InputStreamReader(conn.getInputStream())));
    return parser.parse().getDatabase().getEntries();
  } catch (MalformedURLException e) {
    // Propagate...
    throw new RuntimeException(e);
  }
}

