private String doBrowserRequest(String urlString, String body) throws IOException {
  int responsecode = 200;
  URL result = new URL(urlString);
  HttpURLConnection conn = (HttpURLConnection) result.openConnection();

  conn.setConnectTimeout(timeoutInMillis);
  conn.setReadTimeout(timeoutInMillis);
  conn.setRequestProperty("Content-Type", "application/xml");
  // Send POST output.
  if (body != null) {
    conn.setDoOutput(true);
    OutputStreamWriter wr = new OutputStreamWriter(conn.getOutputStream());
    wr.write(body);
    wr.flush();
    wr.close();
  }
  // conn.setInstanceFollowRedirects(false);
  // responsecode = conn.getResponseCode();
  if (responsecode == 301) {
    String pathToServlet = conn.getRequestProperty("Location");
    throw new RuntimeException("Bug! 301 redirect??? " + pathToServlet);
  } else if (responsecode != 200) {
    throw new RuntimeException(conn.getResponseMessage());
  } else {
    InputStream is = conn.getInputStream();
    return stringContentsOfInputStream(is);
  }
}

public ResourceBundle newBundle(
    String baseName, Locale locale, String format, ClassLoader loader, boolean reload)
    throws IllegalAccessException, InstantiationException, IOException {
  // The below code is copied from default Control#newBundle() implementation.
  // Only the PropertyResourceBundle line is changed to read the file as UTF-8.
  String bundleName = toBundleName(baseName, locale);
  String resourceName = toResourceName(bundleName, BUNDLE_EXTENSION);
  ResourceBundle bundle = null;
  InputStream stream = null;
  if (reload) {
    URL url = loader.getResource(resourceName);
    if (url != null) {
      URLConnection connection = url.openConnection();
      if (connection != null) {
        connection.setUseCaches(false);
        stream = connection.getInputStream();
      }
    }
  } else {
    stream = loader.getResourceAsStream(resourceName);
  }
  if (stream != null) {
    try {
      bundle = new PropertyResourceBundle(new InputStreamReader(stream, "UTF-8"));
    } finally {
      stream.close();
    }
  }
  return bundle;
}

public static String validate(final String urlLink) {
  int responseCode = -1;
  HttpURLConnection conn = null;
  try {
    URL url = null;
    if (!urlLink.startsWith("http")) {
      url = new URL("http://" + urlLink);
    } else {
      url = new URL(urlLink);
    }
    conn = (HttpURLConnection) url.openConnection();

    // Set up a request.
    conn.setConnectTimeout(2000); // 10 sec
    conn.setReadTimeout(5000); // 10 sec
    conn.setInstanceFollowRedirects(true);
    conn.setRequestProperty("User-agent", "crawler");

    // Send the request.
    conn.connect();

    // Get the response.
    responseCode = conn.getResponseCode();
  } catch (final Exception e) {
    if (conn != null) conn.disconnect();
    return rb.getString("exception.invalidURL");
  }
  if (responseCode == 200) {
    String textUrl = conn != null ? conn.getURL().toExternalForm() : "";
    conn.disconnect();
    return textUrl;
  } else {
    conn.disconnect();
    return rb.getString("exception.invalidURL");
  }
}

