/**
 * Perform the trackback autodiscovery process
 *
 * @param blog Blog information
 * @param blogEntry Blog entry
 */
private void trackbackAutodiscovery(Blog blog, BlogEntry blogEntry) {
  try {
    // Build the URL parameters for the trackback ping URL
    StringBuffer trackbackPingURLParameters = new StringBuffer();
    trackbackPingURLParameters
        .append("&")
        .append(TrackbackPlugin.TRACKBACK_URL_PARAM)
        .append("=")
        .append(URLEncoder.encode(blogEntry.getLink(), UTF8));
    trackbackPingURLParameters
        .append("&")
        .append(TrackbackPlugin.TRACKBACK_TITLE_PARAM)
        .append("=")
        .append(URLEncoder.encode(blogEntry.getTitle(), UTF8));
    trackbackPingURLParameters
        .append("&")
        .append(TrackbackPlugin.TRACKBACK_BLOG_NAME_PARAM)
        .append("=")
        .append(URLEncoder.encode(blog.getBlogName(), UTF8));

    String excerpt = blogEntry.getDescription().replaceAll("<.*?>", "");
    if (excerpt.length() > 255) {
      excerpt = excerpt.substring(0, 251);
      excerpt += "...";
    }
    trackbackPingURLParameters
        .append("&")
        .append(TrackbackPlugin.TRACKBACK_EXCERPT_PARAM)
        .append("=")
        .append(URLEncoder.encode(excerpt, UTF8));

    // Extract all the HREF links from the blog description
    Matcher hrefMatcher = HREF_PATTERN.matcher(blogEntry.getDescription());
    while (hrefMatcher.find()) {

      // If we have a group count of 2, the inner group will be the http:// reference
      // Read the entire contents of the URL into a buffer
      if (hrefMatcher.groupCount() == 2) {
        String hyperlink = hrefMatcher.group(1);
        _logger.debug("Found hyperlink: " + hyperlink);
        BufferedReader br;
        URL hyperlinkURL = new URL(hyperlink);
        br = new BufferedReader(new InputStreamReader(hyperlinkURL.openStream()));
        String html;
        StringBuffer contents = new StringBuffer();
        while ((html = br.readLine()) != null) {
          contents.append(html).append("\n");
        }

        // Look for the Auto Trackback RDF in the HTML
        Matcher rdfOuterMatcher = RDF_OUTER_PATTERN.matcher(contents.toString());
        while (rdfOuterMatcher.find()) {
          _logger.debug("Found outer RDF text in hyperlink");
          for (int i = 0; i < rdfOuterMatcher.groupCount(); i++) {
            String outerRdfText = rdfOuterMatcher.group(i);

            // Look for the inner RDF description
            Matcher rdfInnerMatcher = RDF_INNER_PATTERN.matcher(outerRdfText);
            while (rdfInnerMatcher.find()) {
              _logger.debug("Found inner RDF text in hyperlink");
              for (int j = 0; j < rdfInnerMatcher.groupCount(); j++) {
                String innerRdfText = rdfInnerMatcher.group(j);

                // Look for a dc:identifier attribute which matches the current hyperlink
                Matcher dcIdentifierMatcher = DC_IDENTIFIER_PATTERN.matcher(innerRdfText);
                if (dcIdentifierMatcher.find()) {
                  String dcIdentifier = dcIdentifierMatcher.group(1);

                  // If we find a match, send a trackback ping to the
                  if (dcIdentifier.equals(hyperlink)) {
                    _logger.debug("Matched dc:identifier to hyperlink");
                    Matcher trackbackPingMatcher = TRACKBACK_PING_PATTERN.matcher(innerRdfText);
                    if (trackbackPingMatcher.find()) {
                      StringBuffer trackbackPingURL =
                          new StringBuffer(trackbackPingMatcher.group(1));
                      if (trackbackPingURL.indexOf("?") == -1) {
                        trackbackPingURL.append("?");
                      }
                      trackbackPingURL.append(trackbackPingURLParameters);

                      _logger.debug(
                          "Automatically sending trackback ping to URL: "
                              + trackbackPingURL.toString());
                      URL trackbackUrl = new URL(trackbackPingURL.toString());

                      // Open a connection to the trackback URL and read its input
                      HttpURLConnection trackbackUrlConnection =
                          (HttpURLConnection) trackbackUrl.openConnection();
                      trackbackUrlConnection.setRequestMethod("POST");
                      trackbackUrlConnection.setRequestProperty(
                          "Content-Type", "application/x-www-form-urlencoded");
                      trackbackUrlConnection.setDoOutput(true);
                      trackbackUrlConnection.connect();
                      BufferedReader trackbackStatus =
                          new BufferedReader(
                              new InputStreamReader(trackbackUrlConnection.getInputStream()));
                      String line;
                      StringBuffer status = new StringBuffer();
                      while ((line = trackbackStatus.readLine()) != null) {
                        status.append(line).append("\n");
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  } catch (IOException e) {
    _logger.error(e);
  }
}

protected Session createSession() {
  // default the protocol to be SRW for the case when no explain record
  // can be found
  String protocol = "SRW";
  HttpURLConnection connection = null;

  try {
    logger.fine("Locating Explain record to detect SRW / SRU protocol support");
    // Performing a straight httpGet request on the URL will return a
    // zeerex explain record that can be interogated to determine the
    // support type at the following XPath:
    // /explain/serverInfo@protocol.
    // <br>
    // This attribute can have three values SRW , SRU , SRW/U
    // If no XML is returned from the HttpGet then SRW support is
    // assumed
    URL sessionURL = new URL(this.getHost());
    connection = (HttpURLConnection) sessionURL.openConnection();
    connection.setRequestMethod("GET");
    // send the GET request
    connection.connect();
    // Read the input stream to get the returned XML
    BufferedReader in = new BufferedReader(new InputStreamReader(connection.getInputStream()));
    StringBuffer xml = new StringBuffer();
    String inputLine;
    // loop round reading each line until non are left
    while ((inputLine = in.readLine()) != null) {
      // append the line to the xml buffer
      xml.append(inputLine);
      // add space instead of newline so not to corrupt XML
      xml.append(" ");
    }

    logger.fine("Parsing Explain record to detect SRW / SRU protocol support");
    // parse the returned XML into a document
    Node node = (Node) DOMFactory.parse(xml.toString()).getDocumentElement();
    // extract the protocol from the XML
    protocol = Config.getValue(Config.selectSingleNode(node, PROTOCOL_XPATH));
    logger.fine("Found Protocol: " + protocol);

  } catch (MalformedURLException exc) {
    // The host URL may be invalid so will assume SRW for now as the
    // error handling later on will trap this properly
    logger.warning(
        "MalformedURLException trying to send GET request assuming SRW: " + exc.getMessage());
  } catch (IOException exc) {
    // The host URL may be invalid so will assume SRW for now as the
    // error handling later on will trap this properly
    logger.warning("IOException trying to send GET request assuming SRW: " + exc.getMessage());
  } catch (JaferException exc) {
    // The returned response may not be XML or can not be parsed so will
    // assume SRW
    logger.warning("JaferException parsing the returned XML assuming SRW: " + exc.getMessage());
  } finally {
    if (connection != null) {
      // ensure we close the connection
      connection.disconnect();
    }
  }
  // If the host only supports SRU then we must use the SRUBinding
  // otherwise SRWBinding takes priority for SRW and SRW/U values
  if (protocol != null && protocol.equalsIgnoreCase("SRU")) {
    logger.fine("Creating SRU Binding");
    return new SRWSession(new SRUBinding(this.getHost()));
  } else {
    logger.fine("Creating SRW Binding");
    return new SRWSession(new SRWBinding(this.getHost()));
  }
}

/** Check one link, given its DocumentBase and the tag */
public String checkLink(URL linkURL) {

  try {
    // Open it; if the open fails we'll likely throw an exception
    URLConnection luf = linkURL.openConnection();
    final String protocol = linkURL.getProtocol();
    if (protocol.equals("http") || protocol.equals("https")) {
      HttpURLConnection huf = (HttpURLConnection) luf;
      final int responseCode = huf.getResponseCode();
      return (responseCode == -1)
          ? "Server error: bad HTTP response " + responseCode
          : responseCode + " " + huf.getResponseMessage();
    } else if (linkURL.getProtocol().equals("ftp")) {
      return "(skipping FTP link)";
    } else if (linkURL.getProtocol().equals("file")) {
      InputStream is = luf.getInputStream();
      is.close();
      // If that didn't throw an exception, the file is probably OK
      return "(File)";
    } else return "(non-HTTP)";
  } catch (IOException e) {
    return "DEAD " + e.toString();
  }
}

