/** @return the request with the conditional get headers. */
private RecordedRequest assertConditionallyCached(MockResponse response) throws Exception {
  // scenario 1: condition succeeds
  server.enqueue(response.setBody("A").setStatus("HTTP/1.1 200 A-OK"));
  server.enqueue(new MockResponse().setResponseCode(HttpURLConnection.HTTP_NOT_MODIFIED));

  // scenario 2: condition fails
  server.enqueue(response.setBody("B").setStatus("HTTP/1.1 200 B-OK"));
  server.enqueue(new MockResponse().setStatus("HTTP/1.1 200 C-OK").setBody("C"));

  server.play();

  URL valid = server.getUrl("/valid");
  HttpURLConnection connection1 = (HttpURLConnection) valid.openConnection();
  assertEquals("A", readAscii(connection1));
  assertEquals(HttpURLConnection.HTTP_OK, connection1.getResponseCode());
  assertEquals("A-OK", connection1.getResponseMessage());
  HttpURLConnection connection2 = (HttpURLConnection) valid.openConnection();
  assertEquals("A", readAscii(connection2));
  assertEquals(HttpURLConnection.HTTP_OK, connection2.getResponseCode());
  assertEquals("A-OK", connection2.getResponseMessage());

  URL invalid = server.getUrl("/invalid");
  HttpURLConnection connection3 = (HttpURLConnection) invalid.openConnection();
  assertEquals("B", readAscii(connection3));
  assertEquals(HttpURLConnection.HTTP_OK, connection3.getResponseCode());
  assertEquals("B-OK", connection3.getResponseMessage());
  HttpURLConnection connection4 = (HttpURLConnection) invalid.openConnection();
  assertEquals("C", readAscii(connection4));
  assertEquals(HttpURLConnection.HTTP_OK, connection4.getResponseCode());
  assertEquals("C-OK", connection4.getResponseMessage());

  server.takeRequest(); // regular get
  return server.takeRequest(); // conditional get
}

public static final HTTPStream createHTTPStream(
    String address,
    boolean isPost,
    byte[] postData,
    String headers,
    int timeOutMs,
    java.lang.StringBuffer responseHeaders) {
  try {
    HttpURLConnection connection = (HttpURLConnection) (new URL(address).openConnection());

    if (connection != null) {
      try {
        if (isPost) {
          connection.setConnectTimeout(timeOutMs);
          connection.setDoOutput(true);
          connection.setChunkedStreamingMode(0);

          OutputStream out = connection.getOutputStream();
          out.write(postData);
          out.flush();
        }

        return new HTTPStream(connection);
      } catch (Throwable e) {
        connection.disconnect();
      }
    }
  } catch (Throwable e) {
  }

  return null;
}

/**
 * Connect to an HTTP URL and return the response as a string.
 *
 * <p>Note that the HTTP method override is used on non-GET requests. (i.e. requests are made as
 * "POST" with method specified in the body).
 *
 * @param url - the resource to open: must be a welformed URL
 * @param method - the HTTP method to use ("GET", "POST", etc.)
 * @param params - the query parameter for the URL (e.g. access_token=foo)
 * @return the URL contents as a String
 * @throws MalformedURLException - if the URL format is invalid
 * @throws IOException - if a network problem occurs
 */
public static String openUrl(String url, String method, Bundle params)
    throws MalformedURLException, IOException {
  // random string as boundary for multi-part http post
  String strBoundary = "3i2ndDfv2rTHiSisAbouNdArYfORhtTPEefj3q2f";
  String endLine = "\r\n";

  OutputStream os;

  if (method.equals("GET")) {
    url = url + "?" + encodeUrl(params);
  }
  Util.logd("Facebook-Util", method + " URL: " + url);
  HttpURLConnection conn = (HttpURLConnection) new URL(url).openConnection();
  conn.setRequestProperty(
      "User-Agent", System.getProperties().getProperty("http.agent") + " FacebookAndroidSDK");
  if (!method.equals("GET")) {
    Bundle dataparams = new Bundle();
    for (String key : params.keySet()) {
      if (params.getByteArray(key) != null) {
        dataparams.putByteArray(key, params.getByteArray(key));
      }
    }

    // use method override
    if (!params.containsKey("method")) {
      params.putString("method", method);
    }

    if (params.containsKey("access_token")) {
      String decoded_token = URLDecoder.decode(params.getString("access_token"));
      params.putString("access_token", decoded_token);
    }

    conn.setRequestMethod("POST");
    conn.setRequestProperty("Content-Type", "multipart/form-data;boundary=" + strBoundary);
    conn.setDoOutput(true);
    conn.setDoInput(true);
    conn.setRequestProperty("Connection", "Keep-Alive");
    conn.connect();
    os = new BufferedOutputStream(conn.getOutputStream());

    os.write(("--" + strBoundary + endLine).getBytes());
    os.write((encodePostBody(params, strBoundary)).getBytes());
    os.write((endLine + "--" + strBoundary + endLine).getBytes());

    if (!dataparams.isEmpty()) {

      for (String key : dataparams.keySet()) {
        os.write(
            ("Content-Disposition: form-data; filename=\"" + key + "\"" + endLine).getBytes());
        os.write(("Content-Type: content/unknown" + endLine + endLine).getBytes());
        os.write(dataparams.getByteArray(key));
        os.write((endLine + "--" + strBoundary + endLine).getBytes());
      }
    }
    os.flush();
  }

  String response = "";
  try {
    response = read(conn.getInputStream());
  } catch (FileNotFoundException e) {
    // Error Stream contains JSON that we can parse to a FB error
    response = read(conn.getErrorStream());
  }
  return response;
}

/**
 * Connects to server and sends the json string 'outputData'
 *
 * @param path The path connecting to
 * @param gsonClientPack The json formated string to be sent to the server
 * @return String The data from the server
 */
public static String connect(String path, String gsonClientPack) {

  System.out.println("Client attempting a connection to : " + ADDRESS + path); // Debugging

  HttpURLConnection connection;
  URL link;
  InputStream input;
  String data = "";
  DataOutputStream output;

  try {
    link = new URL(ADDRESS + path);
    connection = (HttpURLConnection) link.openConnection();
    connection.setAllowUserInteraction(true);
    connection.setRequestMethod("POST");
    connection.setDoOutput(true);
    connection.setDoInput(true);
    connection.setInstanceFollowRedirects(false);

    output = new DataOutputStream(connection.getOutputStream());
    output.writeBytes(gsonClientPack);
    output.flush();
    output.close();

    try { // Try to read the data from server
      input = connection.getInputStream();
      while (input.available() <= 0) ; // no data from server, waiting...
      while (input.available() > 0) {
        int number = input.read();
        data += (char) number;
      }
      input.close();
    } catch (Exception e) {
      e.printStackTrace();
      data = gsonClientPack; // in the case of an exception, return the original pack
    }

    connection.disconnect();
  } catch (Exception e) {
    e.printStackTrace();
  }

  // DEBUGGING //

  System.out.println("Client succesfully connected to : " + ADDRESS + path); // Debugging
  System.out.println("Data To Server: " + gsonClientPack);
  System.out.println("Data From Server: " + data);
  System.out.println("---------------------------------------");

  // END //

  return data;
}

/**
 * Issue a POST request to the server.
 *
 * @param endpoint POST address.
 * @param params request parameters.
 * @throws IOException propagated from POST.
 */
private static void post(String endpoint, Map<String, String> params) throws IOException {
  URL url;
  try {
    url = new URL(endpoint);
  } catch (MalformedURLException e) {
    throw new IllegalArgumentException("invalid url: " + endpoint);
  }
  StringBuilder bodyBuilder = new StringBuilder();
  Iterator<Entry<String, String>> iterator = params.entrySet().iterator();
  // constructs the POST body using the parameters
  while (iterator.hasNext()) {
    Entry<String, String> param = iterator.next();
    bodyBuilder.append(param.getKey()).append('=').append(param.getValue());
    if (iterator.hasNext()) {
      bodyBuilder.append('&');
    }
  }
  String body = bodyBuilder.toString();
  Log.v(TAG, "Posting '" + body + "' to " + url);
  byte[] bytes = body.getBytes();
  HttpURLConnection conn = null;
  try {
    conn = (HttpURLConnection) url.openConnection();
    conn.setDoOutput(true);
    conn.setUseCaches(false);
    conn.setFixedLengthStreamingMode(bytes.length);
    conn.setRequestMethod("POST");
    conn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded;charset=UTF-8");
    // post the request
    OutputStream out = conn.getOutputStream();
    out.write(bytes);
    out.close();
    // handle the response
    int status = conn.getResponseCode();
    if (status != 200) {
      throw new IOException("Post failed with error code " + status);
    }
  } finally {
    if (conn != null) {
      conn.disconnect();
    }
  }
}

/**
 * Retrieve a jar file from the web.
 *
 * @param requiredDeps Dependencies to install
 */
private void downloadDependency(Dependency dependency) {
  InputStream in = null;
  OutputStream out = null;
  try {
    checkDestinationFolder(dependency);

    System.out.println("\nDownloading file " + dependency.getJarName() + "...");
    URL url = new URL(dependency.getResourceURL());
    URLConnection urlc = url.openConnection();

    in = urlc.getInputStream();
    out = new FileOutputStream(new File(dependency.getDestFolder(), dependency.getJarName()));
    NumberFormat percentFormat = NumberFormat.getPercentInstance();
    int blockSize = 4 * 1024;
    byte[] buf = new byte[blockSize]; // 4K buffer
    int bytesRead;
    int progressBytes = 0;
    int updateProgressBar = 0;
    while ((bytesRead = in.read(buf)) != -1) {
      out.write(buf, 0, bytesRead);
      progressBytes += bytesRead;
      updateProgressBar++;
      if (updateProgressBar > 10) {
        double percent = (progressBytes / dependency.getSize());
        System.out.print("\r" + percentFormat.format(percent));
        updateProgressBar = 0;
      }
    }
    out.flush();
    out.close();
    in.close();
    System.out.println("\r100% ");
  } catch (Exception e) {
    throw new RuntimeException("Error downloading file " + dependency.getJarName(), e);
  } finally {
    if (in != null) {
      try {
        in.close();
      } catch (IOException ioe) {
      }
    }
    if (out != null) {
      try {
        out.close();
      } catch (IOException ioe) {
      }
    }
  }
}

