/** Generic method that posts a plugin to the metrics website */
private void postPlugin(boolean isPing) throws IOException {
  // Construct the post data
  final StringBuilder data = new StringBuilder();
  data.append(encode("guid"))
      .append('=')
      .append(encode(BungeeCord.getInstance().config.getUuid()));
  encodeDataPair(data, "version", ProxyServer.getInstance().getVersion());
  encodeDataPair(data, "server", "0");
  encodeDataPair(data, "players", Integer.toString(ProxyServer.getInstance().getOnlineCount()));
  encodeDataPair(data, "revision", String.valueOf(REVISION));

  // If we're pinging, append it
  if (isPing) {
    encodeDataPair(data, "ping", "true");
  }

  // Create the url
  URL url = new URL(BASE_URL + String.format(REPORT_URL, encode("BungeeCord")));

  // Connect to the website
  URLConnection connection;

  connection = url.openConnection();

  connection.setDoOutput(true);
  final BufferedReader reader;
  final String response;
  try (OutputStreamWriter writer = new OutputStreamWriter(connection.getOutputStream())) {
    writer.write(data.toString());
    writer.flush();
    reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));
    response = reader.readLine();
  }
  reader.close();

  if (response == null || response.startsWith("ERR")) {
    throw new IOException(response); // Throw the exception
  }
}

protected long proxyPlainTextRequest(
    URL url, String pathInContext, String pathParams, HttpRequest request, HttpResponse response)
    throws IOException {
  log.fine("PROXY URL=" + url);

  URLConnection connection = url.openConnection();
  if (System.getProperty("http.proxyHost") != null
      && System.getProperty("https.proxyHost") == null
      && "https".equals(url.getProtocol())) {
    /* Proxy HTTPS connections even if https.proxyHost isn't set because that's
    what Selenium used to do. */
    String proxyHost = System.getProperty("http.proxyHost");
    int proxyPort = Integer.getInteger("http.proxyPort");
    InetSocketAddress proxyAddress = new InetSocketAddress(proxyHost, proxyPort);
    connection = url.openConnection(new Proxy(Proxy.Type.HTTP, proxyAddress));
  }

  connection.setAllowUserInteraction(false);

  if (proxyInjectionMode) {
    adjustRequestForProxyInjection(request, connection);
  }

  // Set method
  HttpURLConnection http = null;
  if (connection instanceof HttpURLConnection) {
    http = (HttpURLConnection) connection;
    http.setRequestMethod(request.getMethod());
    http.setInstanceFollowRedirects(false);
    if (trustAllSSLCertificates && connection instanceof HttpsURLConnection) {
      TrustEverythingSSLTrustManager.trustAllSSLCertificates((HttpsURLConnection) connection);
    }
  }

  // check connection header
  String connectionHdr = request.getField(HttpFields.__Connection);
  if (connectionHdr != null
      && (connectionHdr.equalsIgnoreCase(HttpFields.__KeepAlive)
          || connectionHdr.equalsIgnoreCase(HttpFields.__Close))) connectionHdr = null;

  // copy headers
  boolean xForwardedFor = false;
  boolean isGet = "GET".equals(request.getMethod());
  boolean hasContent = false;
  Enumeration enm = request.getFieldNames();
  while (enm.hasMoreElements()) {
    // TODO could be better than this!
    String hdr = (String) enm.nextElement();

    if (_DontProxyHeaders.containsKey(hdr) || !_chained && _ProxyAuthHeaders.containsKey(hdr))
      continue;
    if (connectionHdr != null && connectionHdr.indexOf(hdr) >= 0) continue;

    if (!isGet && HttpFields.__ContentType.equals(hdr)) hasContent = true;

    Enumeration vals = request.getFieldValues(hdr);
    while (vals.hasMoreElements()) {
      String val = (String) vals.nextElement();
      if (val != null) {
        // don't proxy Referer headers if the referer is Selenium!
        if ("Referer".equals(hdr) && (-1 != val.indexOf("/selenium-server/"))) {
          continue;
        }
        if (!isGet && HttpFields.__ContentLength.equals(hdr) && Integer.parseInt(val) > 0) {
          hasContent = true;
        }

        connection.addRequestProperty(hdr, val);
        xForwardedFor |= HttpFields.__XForwardedFor.equalsIgnoreCase(hdr);
      }
    }
  }

  // Proxy headers
  if (!_anonymous) connection.setRequestProperty("Via", "1.1 (jetty)");
  if (!xForwardedFor)
    connection.addRequestProperty(HttpFields.__XForwardedFor, request.getRemoteAddr());

  // a little bit of cache control
  String cache_control = request.getField(HttpFields.__CacheControl);
  if (cache_control != null
      && (cache_control.indexOf("no-cache") >= 0 || cache_control.indexOf("no-store") >= 0))
    connection.setUseCaches(false);

  // customize Connection
  customizeConnection(pathInContext, pathParams, request, connection);

  try {
    connection.setDoInput(true);

    // do input thang!
    InputStream in = request.getInputStream();
    if (hasContent) {
      connection.setDoOutput(true);
      IO.copy(in, connection.getOutputStream());
    }

    // Connect
    connection.connect();
  } catch (Exception e) {
    // TODO(simon): Whhhaaaat?
    // LogSupport.ignore(log, e);
  }

  InputStream proxy_in = null;

  // handler status codes etc.
  int code = -1;
  if (http != null) {
    proxy_in = http.getErrorStream();

    try {
      code = http.getResponseCode();
    } catch (SSLHandshakeException e) {
      throw new RuntimeException(
          "Couldn't establish SSL handshake. Try using trustAllSSLCertificates.\n"
              + e.getLocalizedMessage(),
          e);
    }
    response.setStatus(code);
    response.setReason(http.getResponseMessage());

    String contentType = http.getContentType();
    log.fine("Content-Type is: " + contentType);
  }

  if (proxy_in == null) {
    try {
      proxy_in = connection.getInputStream();
    } catch (Exception e) {
      // TODO(simon): Whhaaattt?
      // LogSupport.ignore(log, e);
      proxy_in = http.getErrorStream();
    }
  }

  // clear response defaults.
  response.removeField(HttpFields.__Date);
  response.removeField(HttpFields.__Server);

  // set response headers
  int h = 0;
  String hdr = connection.getHeaderFieldKey(h);
  String val = connection.getHeaderField(h);
  while (hdr != null || val != null) {
    if (hdr != null
        && val != null
        && !_DontProxyHeaders.containsKey(hdr)
        && (_chained || !_ProxyAuthHeaders.containsKey(hdr))) response.addField(hdr, val);
    h++;
    hdr = connection.getHeaderFieldKey(h);
    val = connection.getHeaderField(h);
  }
  if (!_anonymous) response.setField("Via", "1.1 (jetty)");

  response.removeField(HttpFields.__ETag); // possible cksum? Stop caching...
  response.removeField(HttpFields.__LastModified); // Stop caching...

  // Handled
  long bytesCopied = -1;
  request.setHandled(true);
  if (proxy_in != null) {
    bytesCopied = ModifiedIO.copy(proxy_in, response.getOutputStream());
  }

  return bytesCopied;
}

