/** Generic method that posts a plugin to the metrics website */
private void postPlugin(final boolean isPing) throws IOException {
  // Server software specific section
  PluginDescriptor description = plugin.getDescriptor();
  String pluginName = description.getName();
  boolean onlineMode =
      Configuration.getServerConfig().isOnlineMode(); // TRUE if online mode is enabled
  String pluginVersion = description.getVersion();
  String serverVersion =
      String.format(
          "%s %s (MC: %s)",
          Canary.getSpecificationTitle(),
          Canary.getSpecificationVersion(),
          Canary.getServer().getServerVersion());
  int playersOnline = Canary.getServer().getNumPlayersOnline();

  // END server software specific section -- all code below does not use any code outside of this
  // class / Java

  // Construct the post data
  StringBuilder json = new StringBuilder(1024);
  json.append('{');

  // The plugin's description file containg all of the plugin data such as name, version, author,
  // etc
  appendJSONPair(json, "guid", guid);
  appendJSONPair(json, "plugin_version", pluginVersion);
  appendJSONPair(json, "server_version", serverVersion);
  appendJSONPair(json, "players_online", Integer.toString(playersOnline));

  // New data as of R6
  String osname = System.getProperty("os.name");
  String osarch = System.getProperty("os.arch");
  String osversion = System.getProperty("os.version");
  String java_version = System.getProperty("java.version");
  int coreCount = Runtime.getRuntime().availableProcessors();

  // normalize os arch .. amd64 -> x86_64
  if (osarch.equals("amd64")) {
    osarch = "x86_64";
  }

  appendJSONPair(json, "osname", osname);
  appendJSONPair(json, "osarch", osarch);
  appendJSONPair(json, "osversion", osversion);
  appendJSONPair(json, "cores", Integer.toString(coreCount));
  appendJSONPair(json, "auth_mode", onlineMode ? "1" : "0");
  appendJSONPair(json, "java_version", java_version);

  // If we're pinging, append it
  if (isPing) {
    appendJSONPair(json, "ping", "1");
  }

  if (graphs.size() > 0) {
    synchronized (graphs) {
      json.append(',');
      json.append('"');
      json.append("graphs");
      json.append('"');
      json.append(':');
      json.append('{');

      boolean firstGraph = true;

      final Iterator<Graph> iter = graphs.iterator();

      while (iter.hasNext()) {
        Graph graph = iter.next();

        StringBuilder graphJson = new StringBuilder();
        graphJson.append('{');

        for (Plotter plotter : graph.getPlotters()) {
          appendJSONPair(
              graphJson, plotter.getColumnName(), Integer.toString(plotter.getValue()));
        }

        graphJson.append('}');

        if (!firstGraph) {
          json.append(',');
        }

        json.append(escapeJSON(graph.getName()));
        json.append(':');
        json.append(graphJson);

        firstGraph = false;
      }

      json.append('}');
    }
  }

  // close json
  json.append('}');

  // Create the url
  URL url = new URL(BASE_URL + String.format(REPORT_URL, urlEncode(pluginName)));

  // Connect to the website
  URLConnection connection;

  // Mineshafter creates a socks proxy, so we can safely bypass it
  // It does not reroute POST requests so we need to go around it
  if (isMineshafterPresent()) {
    connection = url.openConnection(Proxy.NO_PROXY);
  } else {
    connection = url.openConnection();
  }

  byte[] uncompressed = json.toString().getBytes();
  byte[] compressed = gzip(json.toString());

  // Headers
  connection.addRequestProperty("User-Agent", "MCStats/" + REVISION);
  connection.addRequestProperty("Content-Type", "application/json");
  connection.addRequestProperty("Content-Encoding", "gzip");
  connection.addRequestProperty("Content-Length", Integer.toString(compressed.length));
  connection.addRequestProperty("Accept", "application/json");
  connection.addRequestProperty("Connection", "close");

  connection.setDoOutput(true);

  if (debug) {
    System.out.println(
        "[Metrics] Prepared request for "
            + pluginName
            + " uncompressed="
            + uncompressed.length
            + " compressed="
            + compressed.length);
  }

  // Write the data
  OutputStream os = connection.getOutputStream();
  os.write(compressed);
  os.flush();

  // Now read the response
  final BufferedReader reader =
      new BufferedReader(new InputStreamReader(connection.getInputStream()));
  String response = reader.readLine();

  // close resources
  os.close();
  reader.close();

  if (response == null || response.startsWith("ERR") || response.startsWith("7")) {
    if (response == null) {
      response = "null";
    } else if (response.startsWith("7")) {
      response = response.substring(response.startsWith("7,") ? 2 : 1);
    }

    throw new IOException(response);
  } else {
    // Is this the first update this hour?
    if (response.equals("1") || response.contains("This is your first update this hour")) {
      synchronized (graphs) {
        final Iterator<Graph> iter = graphs.iterator();

        while (iter.hasNext()) {
          final Graph graph = iter.next();

          for (Plotter plotter : graph.getPlotters()) {
            plotter.reset();
          }
        }
      }
    }
  }
}

protected X509CRL loadCRL(URL url) throws IOException, CRLException, URISyntaxException {
  String protocol = url.getProtocol();
  boolean local = false;
  if (protocol.equalsIgnoreCase("file")) local = true;
  X509CRL ret;
  try {
    URLConnection conn = url.openConnection();
    if (!local) {
      conn.setConnectTimeout(params.getRemoteConnectionTimeout());
      conn.setReadTimeout(params.getRemoteConnectionTimeout());
    }
    InputStream is = new BufferedInputStream(conn.getInputStream());
    ret = loadCrlWrapper(is);
  } catch (IOException e) {
    if (!local && params.getDiskCachePath() != null) {
      File input = utils.getCacheFile(url);
      if (input.exists()) {
        InputStream is = new BufferedInputStream(new FileInputStream(input));
        ret = loadCrlWrapper(is);
        notifyObservers(
            url.toExternalForm(),
            Severity.WARNING,
            new IOException(
                "Warning: CRL was not loaded from its URL, "
                    + "but its previously cached copy was loaded from disk file "
                    + input.getPath(),
                e));
        return ret;
      } else throw e;
    }
    throw e;
  }

  if (!local) utils.saveCacheFile(ret.getEncoded(), url);

  return ret;
}

private String sendRequestInternal(String url, String body, boolean is_post) throws IOException {
  HttpURLConnection connection = null;
  try {
    connection = (HttpURLConnection) new URL(url).openConnection();
    connection.setConnectTimeout(30000);
    connection.setReadTimeout(30000);
    connection.setUseCaches(false);
    connection.setDoOutput(is_post);
    connection.setDoInput(true);
    connection.setRequestMethod(is_post ? "POST" : "GET");
    if (enable_compression) connection.setRequestProperty("Accept-Encoding", "gzip");
    if (is_post) connection.getOutputStream().write(body.getBytes("UTF-8"));
    int code = connection.getResponseCode();
    Log.i(TAG, "code=" + code);
    // It may happen due to keep-alive problem
    // http://stackoverflow.com/questions/1440957/httpurlconnection-getresponsecode-returns-1-on-second-invocation
    if (code == -1) throw new WrongResponseCodeException("Network error");
    //      200
    // on error can also read error stream from connection.
    InputStream is = new BufferedInputStream(connection.getInputStream(), 8192);
    String enc = connection.getHeaderField("Content-Encoding");
    if (enc != null && enc.equalsIgnoreCase("gzip")) is = new GZIPInputStream(is);
    String response = Utils.convertStreamToString(is);
    return response;
  } finally {
    if (connection != null) connection.disconnect();
  }
}

